tests/calculator.spec.js.content:
const { test, expect } = require('@playwright/test');

test.describe('Mini Calculator Tests', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('http://localhost:3000');
    await page.waitForTimeout(3000);
  });

  test('IGF-1 LR3 calculator calculation', async ({ page }) => {
    // Navigate to peptides section
    await page.evaluate(() => window.scrollTo(0, 2000));
    await page.waitForTimeout(1000);

    // Search for IGF-1 LR3
    const searchInput = page.locator('input[placeholder*="Search peptides"]');
    await searchInput.fill('IGF-1 LR3');
    await page.waitForTimeout(1000);

    // Click on IGF-1 LR3 card
    await page.locator('text=IGF-1 LR3').first().click();
    await page.waitForTimeout(2000);

    // Fill calculator inputs
    await page.locator('input[placeholder="1"]').fill('1');
    await page.locator('input[placeholder="2"]').fill('2');
    await page.locator('input[placeholder="40"]').fill('40');

    // Click calculate
    await page.locator('button:has-text("Calculate")').click();
    await page.waitForTimeout(2000);

    // Verify results
    await expect(page.locator('text=0.50 mg/ml')).toBeVisible();
    await expect(page.locator('text=0.080 ml')).toBeVisible();
    await expect(page.locator('text=500')).toBeVisible();
    await expect(page.locator('text=25')).toBeVisible();
  });

  test('Semaglutide calculator calculation', async ({ page }) => {
    // Navigate to peptides section
    await page.evaluate(() => window.scrollTo(0, 2000));
    await page.waitForTimeout(1000);

    // Search for Semaglutide
    const searchInput = page.locator('input[placeholder*="Search peptides"]');
    await searchInput.fill('Semaglutide');
    await page.waitForTimeout(1000);

    // Click on Semaglutide card
    await page.locator('text=Semaglutide').first().click();
    await page.waitForTimeout(2000);

    // Fill calculator inputs
    await page.locator('input[placeholder="5"]').fill('5');
    await page.locator('input[placeholder="2"]').fill('2');
    await page.locator('input[placeholder="0.25"]').fill('0.25');

    // Click calculate
    await page.locator('button:has-text("Calculate")').click();
    await page.waitForTimeout(2000);

    // Verify results appear
    await expect(page.locator('text=Results:')).toBeVisible();
    await expect(page.locator('text=2.50 mg/ml')).toBeVisible();
  });

  test('Tirzepatide calculator calculation', async ({ page }) => {
    // Navigate to peptides section
    await page.evaluate(() => window.scrollTo(0, 2000));
    await page.waitForTimeout(1000);

    // Search for Tirzepatide
    const searchInput = page.locator('input[placeholder*="Search peptides"]');
    await searchInput.fill('Tirzepatide');
    await page.waitForTimeout(1000);

    // Click on Tirzepatide card
    await page.locator('text=Tirzepatide').first().click();
    await page.waitForTimeout(2000);

    // Fill calculator inputs
    await page.locator('input[placeholder="10"]').fill('10');
    await page.locator('input[placeholder="2"]').fill('2');
    await page.locator('input[placeholder="2.5"]').fill('2.5');

    // Click calculate
    await page.locator('button:has-text("Calculate")').click();
    await page.waitForTimeout(2000);

    // Verify results appear
    await expect(page.locator('text=Results:')).toBeVisible();
    await expect(page.locator('text=5.00 mg/ml')).toBeVisible();
  });

  test('Ipamorelin calculator calculation', async ({ page }) => {
    // Navigate to peptides section
    await page.evaluate(() => window.scrollTo(0, 2000));
    await page.waitForTimeout(1000);

    // Search for Ipamorelin
    const searchInput = page.locator('input[placeholder*="Search peptides"]');
    await searchInput.fill('Ipamorelin');
    await page.waitForTimeout(1000);

    // Click on Ipamorelin card
    await page.locator('text=Ipamorelin').first().click();
    await page.waitForTimeout(2000);

    // Fill calculator inputs
    await page.locator('input[placeholder="5"]').fill('5');
    await page.locator('input[placeholder="2"]').fill('2');
    await page.locator('input[placeholder="200"]').fill('200');

    // Click calculate
    await page.locator('button:has-text("Calculate")').click();
    await page.waitForTimeout(2000);

    // Verify results appear
    await expect(page.locator('text=Results:')).toBeVisible();
    await expect(page.locator('text=2.50 mg/ml')).toBeVisible();
  });

  test('Calculator input persistence', async ({ page }) => {
    // Navigate to peptides section and open IGF-1 LR3
    await page.evaluate(() => window.scrollTo(0, 2000));
    await page.waitForTimeout(1000);

    const searchInput = page.locator('input[placeholder*="Search peptides"]');
    await searchInput.fill('IGF-1 LR3');
    await page.waitForTimeout(1000);

    await page.locator('text=IGF-1 LR3').first().click();
    await page.waitForTimeout(2000);

    // Fill inputs
    await page.locator('input[placeholder="1"]').fill('2');
    await page.locator('input[placeholder="2"]').fill('3');
    await page.locator('input[placeholder="40"]').fill('50');

    // Close modal
    await page.locator('button[aria-label="Close"]').click();
    await page.waitForTimeout(1000);

    // Reopen modal
    await page.locator('text=IGF-1 LR3').first().click();
    await page.waitForTimeout(2000);

    // Verify inputs are persisted
    await expect(page.locator('input[placeholder="1"]')).toHaveValue('2');
    await expect(page.locator('input[placeholder="2"]')).toHaveValue('3');
    await expect(page.locator('input[placeholder="40"]')).toHaveValue('50');
  });

  test('Calculator error handling', async ({ page }) => {
    // Navigate to peptides section
    await page.evaluate(() => window.scrollTo(0, 2000));
    await page.waitForTimeout(1000);

    const searchInput = page.locator('input[placeholder*="Search peptides"]');
    await searchInput.fill('IGF-1 LR3');
    await page.waitForTimeout(1000);

    await page.locator('text=IGF-1 LR3').first().click();
    await page.waitForTimeout(2000);

    // Try to calculate without inputs
    await page.locator('button:has-text("Calculate")').click();
    await page.waitForTimeout(1000);

    // Verify error message appears
    await expect(page.locator('text=Please enter a valid')).toBeVisible();
  });
});

tests/phase2-acceptance.spec.js.content:
const { test, expect } = require('@playwright/test');

test.describe('Phase 2 Acceptance Tests', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('http://localhost:3000');
    await page.waitForTimeout(3000);
  });

  test('Complete peptide search and filter workflow', async ({ page }) => {
    // Navigate to peptides section
    await page.evaluate(() => document.querySelector('#peptides').scrollIntoView());
    await page.waitForTimeout(2000);

    // Test search functionality
    const searchInput = page.locator('input[placeholder*="Search peptides"]');
    await searchInput.fill('Semaglutide');
    await page.waitForTimeout(1000);

    // Verify search results
    await expect(page.locator('text=Showing 1 of')).toBeVisible();
    await expect(page.locator('text=Semaglutide')).toBeVisible();

    // Test FDA filter
    await page.selectOption('select[aria-label="FDA Status"]', 'approved');
    await page.waitForTimeout(1000);

    // Clear search and test category filter
    await searchInput.clear();
    await page.selectOption('select[aria-label="Category"]', 'weightManagement');
    await page.waitForTimeout(1000);

    // Clear all filters
    await page.click('button:has-text("Clear")');
    await page.waitForTimeout(1000);
  });

  test('PDP with calculator, COA, and social sharing', async ({ page }) => {
    // Navigate to peptides and open IGF-1 LR3
    await page.evaluate(() => document.querySelector('#peptides').scrollIntoView());
    await page.waitForTimeout(2000);

    const searchInput = page.locator('input[placeholder*="Search peptides"]');
    await searchInput.fill('IGF-1 LR3');
    await page.waitForTimeout(1000);

    await page.click('text=IGF-1 LR3');
    await page.waitForTimeout(2000);

    // Verify PDP opened with tabs
    await expect(page.locator('text=Overview')).toBeVisible();
    await expect(page.locator('text=Calculator')).toBeVisible();
    await expect(page.locator('text=COA')).toBeVisible();
    await expect(page.locator('text=Share')).toBeVisible();

    // Test calculator tab
    await page.click('button:has-text("Calculator")');
    await page.waitForTimeout(1000);

    // Fill calculator inputs
    await page.fill('input[placeholder="1"]', '1');
    await page.fill('input[placeholder="2"]', '2');
    await page.fill('input[placeholder="40"]', '40');
    await page.click('button:has-text("Calculate")');
    await page.waitForTimeout(2000);

    // Verify results
    await expect(page.locator('text=Results:')).toBeVisible();
    await expect(page.locator('text=0.50 mg/ml')).toBeVisible();

    // Test COA tab
    await page.click('button:has-text("COA")');
    await page.waitForTimeout(1000);
    await expect(page.locator('text=Certificates of Analysis')).toBeVisible();

    // Test Share tab
    await page.click('button:has-text("Share")');
    await page.waitForTimeout(1000);
    await expect(page.locator('text=Messages')).toBeVisible();
    await expect(page.locator('text=Mail')).toBeVisible();
    await expect(page.locator('text=Facebook')).toBeVisible();
  });

  test('Compare functionality capped at 4 items', async ({ page }) => {
    await page.evaluate(() => document.querySelector('#peptides').scrollIntoView());
    await page.waitForTimeout(2000);

    // Add first peptide to compare
    const peptideCards = page.locator('[data-testid="peptide-card"]').or(page.locator('text=Semaglutide')).first();
    await peptideCards.hover();
    await page.click('[aria-label*="Add to comparison"]').first();
    await page.waitForTimeout(500);

    // Add second peptide
    await page.click('[aria-label*="Add to comparison"]').nth(1);
    await page.waitForTimeout(500);

    // Add third peptide
    await page.click('[aria-label*="Add to comparison"]').nth(2);
    await page.waitForTimeout(500);

    // Add fourth peptide
    await page.click('[aria-label*="Add to comparison"]').nth(3);
    await page.waitForTimeout(500);

    // Verify compare count shows 4/4
    await expect(page.locator('text=4/4 to compare')).toBeVisible();

    // Try to add fifth - should be disabled
    const fifthCompareBtn = page.locator('[aria-label*="Add to comparison"]').nth(4);
    if (await fifthCompareBtn.count() > 0) {
      await expect(fifthCompareBtn).toBeDisabled();
    }

    // Test compare now functionality
    await page.click('button:has-text("Compare Now")');
    await page.waitForTimeout(2000);
