        title: 'Healing Peptides',
        description: 'Peptides for tissue repair and recovery',
        peptides: [
          { 
            slug: 'bpc-157', 
            name: 'BPC-157', 
            category: 'healing-peptides',
            description: 'Body Protection Compound for healing',
            type: 'Healing Peptide'
          },
          { 
