import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import Backend from 'i18next-http-backend';
import LanguageDetector from 'i18next-browser-languagedetector';

// Supported languages configuration
const supportedLanguages = ['en', 'es', 'de', 'fr', 'ar', 'pt', 'ru', 'fa', 'ja', 'it', 'pl', 'he'];

// RTL languages configuration
const rtlLanguages = ['ar', 'fa', 'he'];

// Language detection options
const detectionOptions = {
  // Order of language detection methods
  order: [
    'path',           // Check URL path first (/en, /es, etc.)
    'querystring',    // Check ?lng= parameter
    'cookie',         // Check cookie
    'localStorage',   // Check localStorage
    'sessionStorage', // Check sessionStorage
    'navigator',      // Check browser language
    'htmlTag',        // Check html lang attribute
    'subdomain',      // Check subdomain
  ],

  // Look for language code in URL path
  lookupFromPathIndex: 0,

  // Query parameter name for language switching
  lookupQuerystring: 'lng',

  // Cookie name for language persistence
  lookupCookie: 'i18next',

  // LocalStorage key for language persistence
  lookupLocalStorage: 'i18nextLng',

  // SessionStorage key for language persistence
  lookupSessionStorage: 'i18nextLng',

  // Check specific subdomain
  lookupFromSubdomainIndex: 0,

  // Cache user language on
  caches: ['localStorage', 'cookie'],

  // Optional expire and domain for set cookie
  cookieMinutes: 10080, // 7 days
  cookieOptions: { path: '/', sameSite: 'strict' },

  // Only detect languages that are in the whitelist
  checkWhitelist: true,
};

// Backend configuration for loading translation files
const backendOptions = {
  // Path to load resources from
  loadPath: '/locales/{{lng}}/{{ns}}.json',

  // Allow cross domain requests
  crossDomain: false,

  // Allow credentials on cross domain requests
  withCredentials: false,

  // Define a custom xhr function if you need to modify requests
  // This can be useful for adding authentication headers
  requestOptions: {
    cache: 'default',
    credentials: 'same-origin',
    mode: 'cors',
  },

  // Parse data after it's fetched
  parse: function(data) {
    return JSON.parse(data);
  },

  // Allow override of JSON.parse for parsing of loaded resources
  parseLoadPayload: function(namespace, key, fallbackValue) {
    return { [key]: fallbackValue };
  },
};

// i18next configuration
const i18nConfig = {
  // Fallback language
  fallbackLng: 'en',

  // Default language
  lng: 'en',

  // Supported languages
  supportedLngs: supportedLanguages,

  // Check if language is in supported languages list
  checkForDefaultNamespace: false,

  // Default namespace
  defaultNS: 'common',

  // Debug mode (set to false in production)
  debug: process.env.NODE_ENV === 'development',

  // Interpolation options
  interpolation: {
    // React already does escaping
    escapeValue: false,

    // Format function for interpolation
    format: function(value, format, _lng, _options) {
      if (format === 'uppercase') return value.toUpperCase();
      if (format === 'lowercase') return value.toLowerCase();
      if (format === 'capitalize') {
        return value.charAt(0).toUpperCase() + value.slice(1);
      }
      return value;
    },

    // Prefix and suffix for interpolation
    prefix: '{{',
    suffix: '}}',

    // Prefix and suffix for unescaped interpolation
    prefixEscaped: '{{{',
    suffixEscaped: '}}}',

    // Prefix for nesting
    nestingPrefix: '$t(',
    nestingSuffix: ')',

    // Options for nesting
    nestingOptionsSeparator: ',',

    // Maximum replace rounds
    maxReplaces: 1000,

    // Skip interpolation on these variables
    skipOnVariables: true,
  },

  // React specific options
  react: {
    // Use React.Suspense for async loading
    useSuspense: true,

    // Bind i18n instance to context
    bindI18n: 'languageChanged',

    // Bind store to context  
    bindI18nStore: 'added removed',

    // Transform the key before translation
    transEmptyNodeValue: '',

    // Transform the key for missing translations
    transSupportBasicHtmlNodes: true,

    // Keep translateable attributes on elements
    transKeepBasicHtmlNodesFor: ['br', 'strong', 'i', 'em', 'u', 'span', 'a'],

    // Use React.Suspense with translation
    useSuspense: false, // Set to false to avoid Suspense issues during SSR
  },

  // Key separator
  keySeparator: '.',

  // Namespace separator
  nsSeparator: ':',

  // Plural separator
  pluralSeparator: '_',

  // Context separator
  contextSeparator: '_',

  // Options for resource loading
  load: 'languageOnly', // Load only language codes without region codes

  // Preload languages
  preload: supportedLanguages,

  // Clean code - remove region code if not supported
  cleanCode: true,

  // Initialize with resources
  resources: {},

  // Parsing options
  parseMissingKeyHandler: function(key, defaultValue) {
    if (process.env.NODE_ENV === 'development') {
      console.warn(`Missing translation key: ${key}`);
    }
    return defaultValue || key;
  },

  // Missing key handler
  saveMissing: process.env.NODE_ENV === 'development',

  // Update missing keys
  updateMissing: process.env.NODE_ENV === 'development',

  // Missing key formatter
  missingKeyHandler: function(lng, ns, key, _fallbackValue) {
    if (process.env.NODE_ENV === 'development') {
      console.warn(`Missing key "${key}" for language "${lng}" in namespace "${ns}"`);
    }
  },

  // Post processor
  postProcess: ['interval', 'plural'],

  // Return objects for missing keys
  returnObjects: false,

  // Return empty string for missing keys
  returnEmptyString: false,

  // Return null for missing keys
  returnNull: false,

  // Join arrays
  joinArrays: false,

  // Override options
  overloadTranslationOptionHandler: function(args) {
    return {
      defaultValue: args[1] || args[0],
    };
  },
};

// Initialize i18next
i18n
  .use(Backend)
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    ...i18nConfig,
    detection: detectionOptions,
    backend: backendOptions,
  });

// RTL support helper function
export const isRTL = (language) => {
  return rtlLanguages.includes(language);
};

// Get current language direction
export const getDirection = (language = i18n.language) => {
  return isRTL(language) ? 'rtl' : 'ltr';
};

// Set document direction based on language
export const setDocumentDirection = (language = i18n.language) => {
  const direction = getDirection(language);
  document.documentElement.setAttribute('dir', direction);
  document.documentElement.setAttribute('lang', language);
};

// Language change event listener
i18n.on('languageChanged', (lng) => {
  // Update document direction when language changes
  setDocumentDirection(lng);

  // Update document title if needed
  if (document.title) {
    // You can add logic here to update the document title based on the new language
  }

  // Log language change in development
  if (process.env.NODE_ENV === 'development') {
    console.log(`Language changed to: ${lng}`);
  }
});

// Helper function to get available languages
export const getAvailableLanguages = () => {
  return supportedLanguages.map(lng => ({
    code: lng,
    name: getLanguageName(lng),
    rtl: isRTL(lng),
  }));
};

// Helper function to get language name
export const getLanguageName = (code) => {
  const languageNames = {
    en: 'English',
    es: 'EspaÃ±ol',
    de: 'Deutsch',
    fr: 'FranÃ§ais',
    ar: 'Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©',
    pt: 'PortuguÃªs',
    ru: 'Ð ÑƒÑÑÐºÐ¸Ð¹',
    fa: 'ÙØ§Ø±Ø³ÛŒ',
    ja: 'æ—¥æœ¬èªž',
    it: 'Italiano',
    pl: 'Polski',
    he: '×¢×‘×¨×™×ª',
  };

  return languageNames[code] || code;
};

// Helper function to check if language is supported
export const isSupportedLanguage = (language) => {
  return supportedLanguages.includes(language);
};

// Helper function to get fallback language
export const getFallbackLanguage = () => {
  return i18nConfig.fallbackLng;
};

// Initialize document direction on load
if (typeof document !== 'undefined') {
  setDocumentDirection();
}

export default i18n;

