import "./set-public-path";
// Critical styles first for faster FCP
import './styles/critical.css';
import './styles/a11y-fixes.css';
import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import { initMobileOptimizations } from './utils/mobileOptimizations';
import { initializeServiceWorker } from './utils/serviceWorker';
import { BrowserRouter, HashRouter } from "react-router-dom";

// Initialize mobile optimizations ASAP
initMobileOptimizations();

// Clear cache for preview environments to prevent stale chunk issues
if (typeof window !== "undefined" && /replit|janeway|preview/i.test(window.location.hostname)) {
  if ("serviceWorker" in navigator) {
    navigator.serviceWorker.getRegistrations().then(rs => rs.forEach(r => r.unregister()));
  }
  if (window.caches?.keys) {
    caches.keys().then(keys => keys.forEach(k => caches.delete(k)));
  }
}

// Initialize advanced service worker for performance and offline support
initializeServiceWorker().then(registration => {
  if (registration) {
    console.log('Advanced service worker initialized successfully');
  }
}).catch(error => {
  console.warn('Service worker initialization failed:', error);
});

// Import App directly for static generation compatibility
import App from "./App";

// Conditional router selection for static/preview environments
const isStatic = typeof window !== "undefined" && (
  window.location.protocol === "file:" ||
  window.__STATIC_PREVIEW__ === true ||
  /preview|replit/i.test(window.location.hostname)
);

const Router = isStatic ? HashRouter : BrowserRouter;

const root = ReactDOM.createRoot(document.getElementById("root"));

// Hydrate for static generation, render for normal mode
if (typeof window !== 'undefined' && window.__REACT_SNAP_HYDRATE__) {
  // Static generation mode
  ReactDOM.hydrate(
    <React.StrictMode>
      <Router>
        <App />
      </Router>
    </React.StrictMode>,
    document.getElementById("root")
  );
} else {
  // Normal development/production mode
  root.render(
    <React.StrictMode>
      <Router>
        <App />
      </Router>
    </React.StrictMode>
  );
}

