// Blog Articles Master Index
import { safetyProtocolsArticles } from './safety-protocols';
import { educationalGuidesArticles } from './educational-guides';
import { peptideBasicsArticles } from './peptide-basics';

// Export all individual components
export * from './safety-protocols';
export * from './educational-guides';
export * from './peptide-basics';

// Master articles collection
export const allArticles = [
  ...peptideBasicsArticles,
  ...safetyProtocolsArticles,
  ...educationalGuidesArticles
];

// Articles organized by category
export const articlesByCategory = {
  'Peptide Basics': peptideBasicsArticles,
  'Safety Protocols': safetyProtocolsArticles,
  'Educational Guides': educationalGuidesArticles
};

// Articles organized by difficulty level
export const articlesByDifficulty = {
  'Beginner Level': allArticles.filter(article => article.difficulty === 'Beginner Level'),
  'Intermediate Level': allArticles.filter(article => article.difficulty === 'Intermediate Level'),
  'Advanced Level': allArticles.filter(article => article.difficulty === 'Advanced Level'),
  'Professional Level': allArticles.filter(article => article.difficulty === 'Professional Level'),
  'All Levels': allArticles.filter(article => article.difficulty === 'All Levels')
};

// Featured articles for homepage
export const featuredArticles = [
  allArticles.find(article => article.id === 'complete-beginners-guide'),
  allArticles.find(article => article.id === 'complete-peptide-safety-protocols'),
  allArticles.find(article => article.id === 'advanced-research-methodologies')
].filter(Boolean);

// Most recent articles
export const recentArticles = allArticles
  .sort((a, b) => new Date(b.publishDate) - new Date(a.publishDate))
  .slice(0, 5);

// Search function
export const searchArticles = (query) => {
  if (!query) return allArticles;

  const lowercaseQuery = query.toLowerCase();
  return allArticles.filter(article => 
    article.title.toLowerCase().includes(lowercaseQuery) ||
    article.description.toLowerCase().includes(lowercaseQuery) ||
    article.keywords.some(keyword => keyword.toLowerCase().includes(lowercaseQuery)) ||
    article.category.toLowerCase().includes(lowercaseQuery)
  );
};

// Get article by ID
export const getArticleById = (id) => {
  return allArticles.find(article => article.id === id);
};

// Get articles by category
export const getArticlesByCategory = (category) => {
  return allArticles.filter(article => article.category === category);
};

// Get related articles (based on keywords)
export const getRelatedArticles = (articleId, limit = 3) => {
  const article = getArticleById(articleId);
  if (!article) return [];

  const relatedArticles = allArticles
    .filter(a => a.id !== articleId)
    .map(a => ({
      ...a,
      relevanceScore: a.keywords.filter(keyword => article.keywords.includes(keyword)).length
    }))
    .filter(a => a.relevanceScore > 0)
    .sort((a, b) => b.relevanceScore - a.relevanceScore)
    .slice(0, limit);

  return relatedArticles;
};

// Statistics
export const blogStats = {
  totalArticles: allArticles.length,
  totalWordCount: allArticles.reduce((sum, article) => sum + article.wordCount, 0),
  averageReadTime: Math.round(allArticles.reduce((sum, article) => sum + parseInt(article.readTime), 0) / allArticles.length),
  categoryCounts: {
    'Peptide Basics': peptideBasicsArticles.length,
    'Safety Protocols': safetyProtocolsArticles.length,
    'Educational Guides': educationalGuidesArticles.length
  },
  difficultyDistribution: {
    'Beginner Level': articlesByDifficulty['Beginner Level'].length,
    'Intermediate Level': articlesByDifficulty['Intermediate Level'].length,
    'Advanced Level': articlesByDifficulty['Advanced Level'].length,
    'Professional Level': articlesByDifficulty['Professional Level'].length,
    'All Levels': articlesByDifficulty['All Levels'].length
  }
};

