import React from 'react';
import { useData } from '../contexts/DataContext';
import { WifiOff, RotateCcw } from 'lucide-react';

const ApiStatus = () => {
  const { apiConnected, error, apiStatus, retryConnectionNow } = useData();

  // Don't show anything if API is connected
  if (apiConnected && apiStatus === 'connected') {
    return null;
  }

  // Don't show during initial loading
  if (apiStatus === 'loading') {
    return null;
  }

  const getStatusConfig = () => {
    switch (apiStatus) {
      case 'retry':
        return {
          icon: RotateCcw,
          className: 'bg-blue-50 border-blue-200 text-blue-800',
          iconClassName: 'text-blue-600 animate-spin',
          message: 'Reconnecting to live data...'
        };
      case 'fallback':
      default:
        return {
          icon: WifiOff,
          className: 'bg-amber-50 border-amber-200 text-amber-800',
          iconClassName: 'text-amber-600',
          message: error || 'Showing cached peptide data. Live updates will refresh automatically.'
        };
    }
  };

  const { icon: Icon, className, iconClassName, message } = getStatusConfig();

  return (
    <div className={`mx-auto max-w-6xl mb-6 px-4`}>
      <div className={`rounded-lg border p-3 ${className}`}>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Icon className={`h-4 w-4 ${iconClassName}`} />
            <span className="text-sm font-medium">
              {message}
            </span>
          </div>

          {apiStatus === 'fallback' && (
            <button
              onClick={retryConnectionNow}
              className="text-sm font-medium hover:underline focus:outline-none focus:underline"
              aria-label="Retry connection to live data"
            >
              Try Now
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ApiStatus;

