      return (
        <Suspense fallback={<div>Loading...</div>}>
          <CategoryComponent />
        </Suspense>
      );
    } catch (error) {
      return <div>Category not found</div>;
    }
  }

  if (pathParts.length === 3) {
    // Handle both old /blog/category/article and new /{peptideName}/{category}/{blogname} patterns
    let category, article;

    if (pathParts[0] === 'blog') {
      // Traditional /blog/category/article pattern
      category = pathParts[1];
      article = pathParts[2];
    } else {
      // New /{peptideName}/{category}/{blogname} pattern
      const peptideName = pathParts[0];
      const categoryName = pathParts[1];
      const blogName = pathParts[2];

      // Map friendly URL to actual blog path
      const mappedArticle = mapFriendlyUrlToBlogPath(peptideName, categoryName, blogName);

      if (mappedArticle) {
        category = categoryName;
        article = mappedArticle;
      } else {
        return <div>Article not found</div>;
      }
    }

    const articleData = getArticleById(article);

    try {
      const ArticleComponent = dynamicImport(category, article);
      return (
        <>
          {articleData && <BlogSEO article={articleData} />}
          <div className="min-h-screen bg-gray-50">
            {/* Article Actions Bar */}
            {articleData && (
              <div className="bg-white border-b border-gray-200 sticky top-0 z-10">
                <div className="max-w-4xl mx-auto px-6 py-3 flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    <a 
                      href="/resources"
                      className="text-sm text-blue-600 hover:text-blue-700 font-medium"
                    >
                      â† Back to Resources
                    </a>
                    <span className="text-gray-300">|</span>
                    <span className="text-sm text-gray-600">{articleData.category}</span>
                  </div>
                  <div className="flex items-center space-x-4">
                    <PDFDownloadButton article={articleData} />
                    <button
                      onClick={() => window.print()}
                      className="text-sm text-gray-600 hover:text-gray-800 font-medium"
                    >
                      Print
                    </button>
                    <button
                      onClick={() => navigator.share ? navigator.share({
                        title: articleData.title,
                        text: articleData.description,
                        url: window.location.href
                      }) : navigator.clipboard.writeText(window.location.href)}
                      className="text-sm text-gray-600 hover:text-gray-800 font-medium"
                    >
                      Share
                    </button>
                  </div>
                </div>
              </div>
            )}

            {/* Article Content */}
            <div className="article-content">
              <Suspense fallback={<LoadingFallback />}>
                <ArticleComponent />
              </Suspense>
            </div>
          </div>
        </>
      );
    } catch (error) {
      return <div>Article not found</div>;
    }
  }

  // Fallback for unmatched routes
  return <div>Page not found</div>;
};

export default BlogRouter;

