import React, { useState, useEffect } from 'react';

const CalculatorsPage = () => {
  const [activeCalculator, setActiveCalculator] = useState('dosage');
  const [dosageInputs, setDosageInputs] = useState({
    peptide: '',
    weight: '',
    concentration: '',
    targetDose: ''
  });
  const [dilutionInputs, setDilutionInputs] = useState({
    peptideAmount: '',
    totalVolume: '',
    desiredConcentration: ''
  });
  const [conversionInputs, setConversionInputs] = useState({
    value: '',
    fromUnit: 'mg',
    toUnit: 'mcg',
    molecularWeight: ''
  });
  const [stabilityInputs, setStabilityInputs] = useState({
    peptide: '',
    temperature: '',
    pH: '',
    concentration: '',
    solvent: 'bacteriostatic_water'
  });
  const [results, setResults] = useState(null);
  const [errors, setErrors] = useState({});

  const peptideOptions = [
    { value: 'bpc157', label: 'BPC-157', standardDose: '250-500 mcg', molecular_weight: 1419.55 },
    { value: 'tb500', label: 'TB-500', standardDose: '2-5 mg', molecular_weight: 889.0 },
    { value: 'ghkcu', label: 'GHK-Cu', standardDose: '1-3 mg', molecular_weight: 404.0 },
    { value: 'ipamorelin', label: 'Ipamorelin', standardDose: '200-300 mcg', molecular_weight: 711.85 },
    { value: 'cjc1295', label: 'CJC-1295', standardDose: '1-2 mg', molecular_weight: 3368.0 },
    { value: 'ghrp2', label: 'GHRP-2', standardDose: '100-300 mcg', molecular_weight: 817.9 },
    { value: 'sermorelin', label: 'Sermorelin', standardDose: '200-300 mcg', molecular_weight: 3357.9 },
  ];

  const calculatorTypes = [
    {
      id: 'dosage',
      title: 'Dosage Calculator',
      description: 'Calculate precise peptide dosages based on body weight and research protocols',
      icon: 'ðŸ’‰'
    },
    {
      id: 'dilution',
      title: 'Dilution Calculator',
      description: 'Determine proper dilution ratios for peptide reconstitution',
      icon: 'ðŸ§ª'
    },
    {
      id: 'conversion',
      title: 'Unit Converter',
      description: 'Convert between different measurement units (mg, mcg, IU, mL)',
      icon: 'âš–ï¸'
    },
    {
      id: 'stability',
      title: 'Stability Calculator',
      description: 'Estimate peptide stability and storage requirements',
      icon: 'â„ï¸'
    }
  ];

  const unitConversions = {
    mg: { mg: 1, mcg: 1000, g: 0.001 },
    mcg: { mg: 0.001, mcg: 1, g: 0.000001 },
    g: { mg: 1000, mcg: 1000000, g: 1 },
    mL: { mL: 1, L: 0.001, uL: 1000 },
    L: { mL: 1000, L: 1, uL: 1000000 },
    uL: { mL: 0.001, L: 0.000001, uL: 1 }
  };

  const validateInputs = (inputs, requiredFields) => {
    const newErrors = {};
    requiredFields.forEach(field => {
      if (!inputs[field] || inputs[field] === '') {
        newErrors[field] = 'This field is required';
      } else if (isNaN(inputs[field]) && typeof inputs[field] === 'string' && !['peptide', 'fromUnit', 'toUnit', 'solvent'].includes(field)) {
        newErrors[field] = 'Please enter a valid number';
      } else if (parseFloat(inputs[field]) <= 0 && typeof inputs[field] === 'string' && !['peptide', 'fromUnit', 'toUnit', 'solvent'].includes(field)) {
        newErrors[field] = 'Value must be greater than 0';
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const calculateDosage = () => {
    if (!validateInputs(dosageInputs, ['peptide', 'weight', 'concentration', 'targetDose'])) {
      return;
    }

    const { peptide, weight, concentration, targetDose } = dosageInputs;
    const concentrationNum = parseFloat(concentration);
    const targetDoseNum = parseFloat(targetDose);
    const weightNum = parseFloat(weight);

    // Calculate injection volume
    const injectionVolume = targetDoseNum / concentrationNum;

    // Calculate dose per kg
    const dosePerKg = targetDoseNum / weightNum;

    // Calculate frequency recommendations
    const selectedPeptide = peptideOptions.find(p => p.value === peptide);

    // Safety checks
    const warnings = [];
    if (injectionVolume > 2.0) {
      warnings.push('Large injection volume - consider higher concentration');
    }
    if (dosePerKg > 10) {
      warnings.push('High dose per kg - verify dosage calculations');
    }

    setResults({
      type: 'dosage',
      peptide: selectedPeptide.label,
      injectionVolume: injectionVolume.toFixed(3),
      dailyDose: targetDoseNum,
      dosePerKg: dosePerKg.toFixed(3),
      standardDose: selectedPeptide.standardDose,
      frequency: getFrequencyRecommendation(peptide),
      notes: getDosageNotes(peptide),
      warnings
    });
  };

  const calculateDilution = () => {
    if (!validateInputs(dilutionInputs, ['peptideAmount', 'totalVolume'])) {
      return;
    }

    const { peptideAmount, totalVolume } = dilutionInputs;
    const peptideAmountNum = parseFloat(peptideAmount);
    const totalVolumeNum = parseFloat(totalVolume);

    // Calculate final concentration
    const finalConcentration = peptideAmountNum / totalVolumeNum;

    // Calculate water volume needed
    const waterVolume = totalVolumeNum;

    // Safety checks
    const warnings = [];
    if (finalConcentration > 10) {
      warnings.push('Very high concentration - ensure proper dissolution');
    }
    if (totalVolumeNum < 1) {
      warnings.push('Small volume - use precise measuring equipment');
    }

    setResults({
      type: 'dilution',
      peptideAmount: peptideAmountNum,
      waterVolume: waterVolume.toFixed(2),
      finalConcentration: finalConcentration.toFixed(3),
      totalVolume: totalVolumeNum,
      instructions: getDilutionInstructions(),
      warnings
    });
  };

  const calculateConversion = () => {
    if (!validateInputs(conversionInputs, ['value', 'fromUnit', 'toUnit'])) {
      return;
    }

    const { value, fromUnit, toUnit, molecularWeight } = conversionInputs;
    const valueNum = parseFloat(value);

    let convertedValue;
    let conversionNote = '';

    // Handle mass/weight conversions
    if (unitConversions[fromUnit] && unitConversions[fromUnit][toUnit]) {
      convertedValue = valueNum * unitConversions[fromUnit][toUnit];
    }
    // Handle molar conversions (if molecular weight provided)
    else if (molecularWeight && fromUnit === 'mg' && toUnit === 'mol') {
      convertedValue = valueNum / parseFloat(molecularWeight);
      conversionNote = 'Molar conversion using provided molecular weight';
    }
    else if (molecularWeight && fromUnit === 'mol' && toUnit === 'mg') {
      convertedValue = valueNum * parseFloat(molecularWeight);
      conversionNote = 'Molar conversion using provided molecular weight';
    }
    else {
      setErrors({ conversion: 'Cannot convert between these units' });
      return;
    }

    setResults({
      type: 'conversion',
      originalValue: valueNum,
      originalUnit: fromUnit,
      convertedValue: convertedValue.toFixed(6),
      convertedUnit: toUnit,
      conversionFactor: unitConversions[fromUnit]?.[toUnit] || 'Variable',
      note: conversionNote
    });
  };

  const calculateStability = () => {
    if (!validateInputs(stabilityInputs, ['peptide', 'temperature', 'concentration'])) {
      return;
    }

    const { peptide, temperature, pH, concentration, solvent } = stabilityInputs;
    const tempNum = parseFloat(temperature);
    const pHNum = pH ? parseFloat(pH) : 7.0;
    const concNum = parseFloat(concentration);

    // Get peptide stability data
    const selectedPeptide = peptideOptions.find(p => p.value === peptide);

    // Calculate estimated stability based on conditions
    let baseStability = getBaseStability(peptide);
    let stabilityDays = baseStability;

    // Temperature adjustments
    if (tempNum <= -20) stabilityDays *= 365; // Frozen long-term
    else if (tempNum <= -80) stabilityDays *= 730; // Ultra-low freezer
    else if (tempNum <= 4) stabilityDays *= 30; // Refrigerated
    else if (tempNum <= 25) stabilityDays *= 1; // Room temperature
    else stabilityDays *= 0.1; // High temperature degradation

    // pH adjustments
    if (pHNum < 6 || pHNum > 8) stabilityDays *= 0.5;

    // Concentration adjustments
    if (concNum > 5) stabilityDays *= 0.7; // High concentration stress

    // Solvent adjustments
    const solventMultipliers = {
      'bacteriostatic_water': 1.0,
      'sterile_water': 0.8,
      'saline': 0.9,
      'acetate_buffer': 1.2,
      'phosphate_buffer': 1.1
    };
    stabilityDays *= solventMultipliers[solvent] || 1.0;

    const recommendations = getStabilityRecommendations(tempNum, pHNum, peptide);

    setResults({
      type: 'stability',
      peptide: selectedPeptide.label,
      estimatedStability: Math.round(stabilityDays),
      temperature: tempNum,
      pH: pHNum,
      recommendations,
      storageConditions: getOptimalStorage(peptide)
    });
  };

  const getFrequencyRecommendation = (peptide) => {
    const frequencies = {
      bpc157: 'Once or twice daily',
      tb500: '2-3 times per week',
      ghkcu: 'Once daily',
      ipamorelin: '2-3 times daily',
      cjc1295: '2-3 times per week',
      ghrp2: '2-3 times daily',
      sermorelin: 'Once daily before bed'
    };
    return frequencies[peptide] || 'Consult research protocol';
  };

  const getDosageNotes = (peptide) => {
    const notes = {
