  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitError(null);

    try {
      // Validate required fields
      if (!formData.name || !formData.email || !formData.inquiryType || !formData.subject || !formData.message) {
        throw new Error('Please fill in all required fields');
      }

      if (apiConnected) {
        // Submit to backend API
        await submitContactForm(formData);
        console.log('âœ… Contact form submitted to backend successfully');
      } else {
        // Fallback to local submission simulation
        console.log('ðŸ“ Contact form submitted locally (API not available):', formData);
      }

      // Show success message
      setIsSubmitted(true);

      // Reset form after 3 seconds
      setTimeout(() => {
        setIsSubmitted(false);
        setFormData({
          name: '',
          email: '',
          organization: '',
          subject: '',
          inquiryType: '',
          message: ''
        });
      }, 3000);

    } catch (error) {
      console.error('âŒ Failed to submit contact form:', error);
      setSubmitError(error.message || 'Failed to submit form. Please try again.');
