  const [selectedCategory, setSelectedCategory] = useState(SEARCH_CATEGORIES.ALL);
  const [isLoading, setIsLoading] = useState(false);
  const [showDropdown, setShowDropdown] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(-1);

  const navigate = useNavigate();
  const inputRef = useRef(null);
  const dropdownRef = useRef(null);

  // Load recent searches on mount
  useEffect(() => {
    setRecentSearches(getRecentSearches());
  }, []);

  // Focus input when search opens
  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isOpen]);

  // Handle search input changes
  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      if (query.trim().length >= 2) {
        setIsLoading(true);
        const searchResults = searchContent(query, selectedCategory, 10);
        setResults(searchResults);
        setSuggestions(getSearchSuggestions(query));
        setShowDropdown(true);
        setIsLoading(false);
      } else {
        setResults([]);
        setSuggestions([]);
        setShowDropdown(query.length > 0 || recentSearches.length > 0);
      }
    }, 300);

    return () => clearTimeout(debounceTimer);
  }, [query, selectedCategory]);

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (!showDropdown) return;

      const totalItems = results.length + suggestions.length + recentSearches.length;

      switch (e.key) {
        case 'ArrowDown':
          e.preventDefault();
          setSelectedIndex(prev => (prev + 1) % totalItems);
          break;
        case 'ArrowUp':
          e.preventDefault();
          setSelectedIndex(prev => prev <= 0 ? totalItems - 1 : prev - 1);
          break;
        case 'Enter':
          e.preventDefault();
          handleSelectItem();
          break;
        case 'Escape':
          handleClose();
          break;
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleKeyDown);
      return () => document.removeEventListener('keydown', handleKeyDown);
    }
  }, [isOpen, showDropdown, selectedIndex, results, suggestions, recentSearches]);

  const handleSelectItem = () => {
    if (selectedIndex === -1) {
      handleSearch(query);
      return;
    }

    const allItems = [...results, ...suggestions, ...recentSearches];
    const selectedItem = allItems[selectedIndex];

    if (selectedItem) {
      if (selectedItem.url) {
        // Navigate to specific item
        navigate(selectedItem.url);
        saveRecentSearch(selectedItem.name || selectedItem.title || query);
        handleClose();
      } else {
        // Use as search query
        const searchQuery = selectedItem.name || selectedItem.title || selectedItem;
        setQuery(searchQuery);
        handleSearch(searchQuery);
      }
    }
  };

  const handleSearch = (searchQuery = query) => {
    if (searchQuery.trim().length < 2) return;

    saveRecentSearch(searchQuery);
    navigate(`/search?q=${encodeURIComponent(searchQuery)}&category=${selectedCategory}`);
    handleClose();
  };

  const handleClose = () => {
    setQuery('');
    setResults([]);
    setSuggestions([]);
    setShowDropdown(false);
    setSelectedIndex(-1);
    onClose();
  };

  const handleClearRecent = () => {
    clearRecentSearches();
    setRecentSearches([]);
  };

  const getItemIcon = (type) => {
    const iconClass = "h-4 w-4 text-gray-400";
    switch (type) {
      case 'peptide':
        return <span className="text-sm">ðŸ’Š</span>;
      case 'calculator':
        return <span className="text-sm">ðŸ§®</span>;
      case 'blog':
        return <span className="text-sm">ðŸ“</span>;
      case 'page':
        return <span className="text-sm">ðŸ“„</span>;
      default:
        return <Search className={iconClass} />;
    }
  };

  const getCategoryColor = (category) => {
    const colors = {
      'Growth Hormone': 'bg-green-100 text-green-800',
      'GLP-1': 'bg-blue-100 text-blue-800',
      'Healing': 'bg-red-100 text-red-800',
      'Performance': 'bg-purple-100 text-purple-800',
      'Cognitive': 'bg-yellow-100 text-yellow-800',
      'Anti-Aging': 'bg-pink-100 text-pink-800',
      'Cosmetic': 'bg-indigo-100 text-indigo-800',
      'Immune': 'bg-orange-100 text-orange-800'
    };
    return colors[category] || 'bg-gray-100 text-gray-800';
  };

  if (!isOpen) return null;

  return (
    <div className={`${isMobile ? 'fixed inset-0 z-50 bg-white' : 'relative'}`}>
      {isMobile && (
        <div className="absolute inset-0 bg-black bg-opacity-50 z-40" onClick={handleClose} />
      )}

      <div className={`${isMobile ? 'relative z-50 bg-white min-h-screen' : ''}`}>
        {/* Search Header */}
        <div className={`${isMobile ? 'p-4 border-b' : 'relative'}`}>
          <div className="flex items-center space-x-2">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <input
                ref={inputRef}
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                onFocus={() => setShowDropdown(true)}
                placeholder="Search peptides, calculators, articles..."
                className={`w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                  isMobile ? 'text-lg' : ''
                }`}
              />
              {query && (
                <button
                  onClick={() => setQuery('')}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                >
                  <X className="h-4 w-4" />
                </button>
              )}
            </div>

            {isMobile && (
              <button
                onClick={handleClose}
                className="text-gray-600 hover:text-gray-800"
              >
                <X className="h-6 w-6" />
              </button>
            )}
          </div>

          {/* Category Filter */}
          <div className="flex items-center space-x-2 mt-3">
            <Filter className="h-4 w-4 text-gray-400" />
            <div className="flex space-x-1 overflow-x-auto">
              {Object.entries(SEARCH_CATEGORIES).map(([key, value]) => (
                <button
                  key={key}
                  onClick={() => setSelectedCategory(value)}
                  className={`px-3 py-1 text-xs rounded-full whitespace-nowrap ${
                    selectedCategory === value
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
                >
                  {key.charAt(0) + key.slice(1).toLowerCase()}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Search Results Dropdown */}
        {showDropdown && (
          <div 
            ref={dropdownRef}
            className={`${
              isMobile 
                ? 'flex-1 overflow-y-auto' 
                : 'absolute top-full left-0 right-0 bg-white border border-gray-200 rounded-lg shadow-lg max-h-96 overflow-y-auto z-50'
            }`}
          >
            {isLoading && (
              <div className="p-4 text-center text-gray-500">
                <div className="animate-spin inline-block w-4 h-4 border-2 border-blue-500 border-t-transparent rounded-full"></div>
                <span className="ml-2">Searching...</span>
              </div>
            )}

            {/* Search Results */}
            {results.length > 0 && (
              <div className="p-2">
                <div className="text-xs text-gray-500 uppercase tracking-wide px-2 py-1 font-medium">
                  Results ({results.length})
                </div>
                {results.map((item, index) => (
                  <button
                    key={`result-${index}`}
                    onClick={() => {
                      if (item.url) {
                        navigate(item.url);
                        saveRecentSearch(item.name || item.title);
                        handleClose();
                      }
                    }}
                    className={`w-full text-left p-3 rounded-lg hover:bg-gray-50 flex items-start space-x-3 ${
                      selectedIndex === index ? 'bg-blue-50' : ''
                    }`}
                  >
                    <div className="mt-1">
                      {getItemIcon(item.type)}
                    </div>
                    <div className="flex-1 min-w-0">
                      <div className="font-medium text-gray-900 truncate">
                        {item.name || item.title}
                      </div>
                      <div className="text-sm text-gray-500 line-clamp-2">
                        {item.description || item.excerpt}
                      </div>
                      {item.category && (
                        <span className={`inline-block mt-1 px-2 py-1 text-xs rounded-full ${getCategoryColor(item.category)}`}>
                          {item.category}
                        </span>
                      )}
                    </div>
                  </button>
                ))}
              </div>
            )}

            {/* Suggestions */}
            {suggestions.length > 0 && (
              <div className="p-2 border-t">
                <div className="text-xs text-gray-500 uppercase tracking-wide px-2 py-1 font-medium flex items-center">
                  <TrendingUp className="h-3 w-3 mr-1" />
                  Suggestions
                </div>
                {suggestions.map((suggestion, index) => (
                  <button
                    key={`suggestion-${index}`}
                    onClick={() => {
                      setQuery(suggestion);
                      handleSearch(suggestion);
                    }}
                    className={`w-full text-left p-2 rounded hover:bg-gray-50 text-sm ${
                      selectedIndex === results.length + index ? 'bg-blue-50' : ''
                    }`}
                  >
                    <Search className="h-3 w-3 inline mr-2 text-gray-400" />
                    {suggestion}
                  </button>
                ))}
              </div>
            )}

            {/* Recent Searches */}
            {query.length === 0 && recentSearches.length > 0 && (
              <div className="p-2 border-t">
                <div className="text-xs text-gray-500 uppercase tracking-wide px-2 py-1 font-medium flex items-center justify-between">
                  <span className="flex items-center">
                    <Clock className="h-3 w-3 mr-1" />
                    Recent Searches
                  </span>
                  <button
                    onClick={handleClearRecent}
                    className="text-blue-500 hover:text-blue-700 text-xs"
                  >
                    Clear
                  </button>
                </div>
                {recentSearches.map((search, index) => (
                  <button
                    key={`recent-${index}`}
                    onClick={() => {
                      setQuery(search);
                      handleSearch(search);
                    }}
                    className={`w-full text-left p-2 rounded hover:bg-gray-50 text-sm ${
                      selectedIndex === results.length + suggestions.length + index ? 'bg-blue-50' : ''
                    }`}
                  >
                    <Clock className="h-3 w-3 inline mr-2 text-gray-400" />
                    {search}
                  </button>
                ))}
              </div>
            )}

            {/* No Results */}
            {query.length >= 2 && results.length === 0 && suggestions.length === 0 && !isLoading && (
              <div className="p-4 text-center text-gray-500">
                <Search className="h-8 w-8 mx-auto mb-2 text-gray-300" />
                <div className="font-medium">No results found</div>
                <div className="text-sm">Try adjusting your search terms or category filter</div>
              </div>
            )}

            {/* Empty State */}
            {query.length === 0 && results.length === 0 && recentSearches.length === 0 && (
              <div className="p-4 text-center text-gray-500">
                <Search className="h-8 w-8 mx-auto mb-2 text-gray-300" />
                <div className="font-medium">Start typing to search</div>
                <div className="text-sm">Search through peptides, calculators, and articles</div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default GlobalSearch;

