    { name: 'Protocol Dosage Calculator', href: '/calculators/reconstitution', icon: 'ðŸ’‰' },
    { name: 'TRT Calculator', href: '/calculators/trt', icon: 'ðŸ’ª' },
    { name: 'Melanotan Calculator', href: '/calculators/melanotan', icon: 'â˜€ï¸' },
