                className="text-gray-700 hover:text-blue-600 block px-3 py-2 text-base font-medium flex items-center"
              >
                <Search className="h-5 w-5 mr-3" />
                Search
              </button>

              {/* Mobile Calculators Submenu */}
              <div className="border-t pt-2 mt-2">
                <div className="text-gray-500 text-sm font-medium px-3 py-2">Calculators</div>
                {calculatorPages.map((calculator) => (
                  <Link
                    key={calculator.name}
                    to={calculator.href}
                    className="text-gray-700 hover:text-blue-600 block px-6 py-2 text-base font-medium flex items-center"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    <span className="mr-3">{calculator.icon}</span>
                    {calculator.name}
                  </Link>
                ))}
                <Link
                  to="/calculators"
                  className="text-blue-600 hover:text-blue-700 block px-6 py-2 text-base font-medium flex items-center"
                  onClick={() => setIsMenuOpen(false)}
                >
                  <Calculator className="h-4 w-4 mr-3" />
                  View All Calculators
                </Link>
              </div>

              {/* Mobile Company Pages */}
              <div className="border-t pt-2 mt-2">
                <div className="text-gray-500 text-sm font-medium px-3 py-2">Company</div>
                {companyPages.map((page) => (
                  <Link
                    key={page.name}
                    to={page.href}
                    className="text-gray-700 hover:text-blue-600 block px-6 py-2 text-base font-medium"
                    onClick={() => setIsMenuOpen(false)}
                  >
                    {page.name}
                  </Link>
                ))}
              </div>

              <div className="px-3 py-2">
                <Link to="/resources">
                  <Button className="w-full bg-blue-600 hover:bg-blue-700 text-white">
                    Get Free Guide
                  </Button>
                </Link>
              </div>
            </nav>
          </div>
        )}
      </div>

      {/* Global Search Component */}
      <GlobalSearch 
        isOpen={isSearchOpen} 
        onClose={() => setIsSearchOpen(false)}
        isMobile={typeof window !== 'undefined' && window.innerWidth <= 768}
      />
    </header>
  );
};

export default Header;

