frontend/src/components/InteractionCheck.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Badge } from './ui/badge';
import { Button } from './ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from './ui/dialog';
import { AlertTriangle, CheckCircle2, Info, X, Plus, Zap } from 'lucide-react';
import { checkPeptideInteractions } from '../data/peptideInteractions';
import { useData } from '../contexts/DataContext';

const InteractionChecker = ({ open, onClose }) => {
  const { peptideCategories } = useData();
  const [selectedPeptides, setSelectedPeptides] = useState([]);
  const [interactions, setInteractions] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // Get all peptides from all categories
  const allPeptides = Object.values(peptideCategories || {}).flatMap(category => 
    category.peptides?.map(peptide => ({
      ...peptide,
      categoryTitle: category.title
    })) || []
  );

  // Filter peptides based on search
  const filteredPeptides = allPeptides.filter(peptide =>
    peptide.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    peptide.categoryTitle.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Check interactions whenever selected peptides change
  useEffect(() => {
    if (selectedPeptides.length >= 2) {
      const foundInteractions = checkPeptideInteractions(selectedPeptides);
      setInteractions(foundInteractions);
    } else {
      setInteractions([]);
    }
  }, [selectedPeptides]);

  const addPeptide = (peptide) => {
    if (!selectedPeptides.find(p => p.slug === peptide.slug) && selectedPeptides.length < 5) {
      setSelectedPeptides([...selectedPeptides, peptide]);
      setSearchTerm('');
    }
  };

  const removePeptide = (peptideSlug) => {
    setSelectedPeptides(selectedPeptides.filter(p => p.slug !== peptideSlug));
  };

  const clearAll = () => {
    setSelectedPeptides([]);
    setInteractions([]);
  };

  const getInteractionIcon = (level) => {
    switch (level) {
      case 'warning':
        return <AlertTriangle className="h-4 w-4 text-red-500" />;
      case 'caution':
        return <Info className="h-4 w-4 text-yellow-500" />;
      case 'beneficial':
        return <CheckCircle2 className="h-4 w-4 text-green-500" />;
      default:
        return <Info className="h-4 w-4 text-blue-500" />;
    }
  };

  const getInteractionColor = (level) => {
    switch (level) {
      case 'warning':
        return 'bg-red-50 border-red-200 text-red-800';
      case 'caution':
        return 'bg-yellow-50 border-yellow-200 text-yellow-800';
      case 'beneficial':
        return 'bg-green-50 border-green-200 text-green-800';
      default:
        return 'bg-blue-50 border-blue-200 text-blue-800';
    }
  };

  const getInteractionTypeText = (type) => {
    const types = {
      'synergistic': 'Synergistic',
      'complementary': 'Complementary',
      'additive': 'Additive Effects',
      'redundant': 'Potentially Redundant',
      'contraindicated': 'Contraindicated'
    };
    return types[type] || 'Unknown';
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader className="pb-4 border-b">
          <DialogTitle className="text-2xl font-bold text-gray-900 flex items-center">
            <Zap className="h-6 w-6 mr-2 text-blue-500" />
            Peptide Interaction Checker
          </DialogTitle>
          <p className="text-gray-600 mt-2">
            Check for potential interactions between peptides in your research protocol. 
            Add up to 5 peptides to analyze their compatibility.
          </p>
        </DialogHeader>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mt-6">
          {/* Peptide Selection */}
          <div>
            <h3 className="text-lg font-semibold mb-4">Select Peptides</h3>

            {/* Search Box */}
            <div className="mb-4">
              <input
                type="text"
                placeholder="Search peptides..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            {/* Selected Peptides */}
            {selectedPeptides.length > 0 && (
              <div className="mb-4">
                <div className="flex items-center justify-between mb-2">
                  <span className="font-medium text-gray-700">Selected Peptides:</span>
                  <Button
                    onClick={clearAll}
                    variant="outline"
                    size="sm"
                    className="text-red-600 hover:text-red-700"
                  >
                    Clear All
                  </Button>
                </div>
                <div className="space-y-2">
                  {selectedPeptides.map((peptide) => (
                    <div key={peptide.slug} className="flex items-center justify-between bg-blue-50 p-2 rounded-lg">
                      <div>
                        <span className="font-medium text-blue-900">{peptide.name}</span>
                        <div className="text-xs text-blue-600">{peptide.categoryTitle}</div>
                      </div>
                      <button
                        onClick={() => removePeptide(peptide.slug)}
                        className="text-red-500 hover:text-red-700"
                      >
                        <X className="h-4 w-4" />
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Available Peptides */}
            <div className="max-h-96 overflow-y-auto space-y-2">
              {filteredPeptides.map((peptide) => {
                const isSelected = selectedPeptides.find(p => p.slug === peptide.slug);
                const isDisabled = selectedPeptides.length >= 5 && !isSelected;

                return (
                  <div
                    key={peptide.slug}
                    className={`p-3 border rounded-lg cursor-pointer transition-colors ${
                      isSelected 
                        ? 'bg-blue-100 border-blue-300' 
                        : isDisabled
                        ? 'bg-gray-50 border-gray-200 opacity-50 cursor-not-allowed'
                        : 'bg-white border-gray-200 hover:border-blue-300 hover:bg-blue-50'
                    }`}
                    onClick={() => !isSelected && !isDisabled && addPeptide(peptide)}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex-1">
                        <div className="font-medium text-gray-900">{peptide.name}</div>
                        <div className="text-sm text-gray-500">{peptide.categoryTitle}</div>
                        {peptide.fdaApproved && (
                          <Badge className="text-xs mt-1 bg-green-100 text-green-800">
                            FDA Approved
                          </Badge>
                        )}
                      </div>
                      {!isSelected && !isDisabled && (
                        <Plus className="h-4 w-4 text-blue-500" />
                      )}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Interaction Results */}
          <div>
            <h3 className="text-lg font-semibold mb-4">Interaction Analysis</h3>

            {selectedPeptides.length === 0 && (
              <div className="text-center py-8 text-gray-500">
                <Zap className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                <p>Select peptides to check for interactions</p>
              </div>
            )}

            {selectedPeptides.length === 1 && (
              <div className="text-center py-8 text-gray-500">
                <Info className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                <p>Add at least one more peptide to check interactions</p>
              </div>
            )}

            {selectedPeptides.length >= 2 && (
              <div className="space-y-4">
                {interactions.length === 0 ? (
                  <Card className="bg-green-50 border-green-200">
                    <CardContent className="p-4">
                      <div className="flex items-center">
                        <CheckCircle2 className="h-5 w-5 text-green-600 mr-2" />
                        <div>
                          <div className="font-medium text-green-800">No Known Interactions</div>
                          <div className="text-sm text-green-600">
                            No documented interactions found between selected peptides
                          </div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ) : (
                  interactions.map((interaction, index) => (
                    <Card key={index} className={`border ${getInteractionColor(interaction.level)}`}>
                      <CardContent className="p-4">
                        <div className="flex items-start">
                          {getInteractionIcon(interaction.level)}
                          <div className="ml-3 flex-1">
                            <div className="font-medium">
                              {interaction.peptide1} + {interaction.peptide2}
                            </div>
                            <Badge variant="secondary" className="text-xs mb-2">
                              {getInteractionTypeText(interaction.type)}
                            </Badge>
                            <p className="text-sm">{interaction.description}</p>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))
                )}

                {/* Safety Disclaimer */}
                <Card className="bg-yellow-50 border-yellow-200">
                  <CardContent className="p-4">
                    <div className="flex items-start">
                      <AlertTriangle className="h-5 w-5 text-yellow-600 mr-2 mt-0.5" />
                      <div className="text-sm text-yellow-800">
                        <div className="font-medium mb-1">Research Use Only</div>
                        <p>
                          This interaction checker is for research purposes only. 
                          Always consult with qualified healthcare professionals before 
                          combining any compounds. Individual responses may vary.
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </div>
            )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
};

export default InteractionChecker;

