                ></div>
              </div>
            </div>
          </div>

          {currentQuizQuestion < quizQuestions.length && (
            <div className="mb-12">
              <h3 className="text-2xl font-semibold text-gray-900 text-center mb-8">
                {quizQuestions[currentQuizQuestion].question}
              </h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {quizQuestions[currentQuizQuestion].options.map((option) => {
                  return (
                    <div
                      key={option.id}
                      onClick={() => handleQuizAnswer(option.id)}
                      className={`p-6 rounded-xl border-2 cursor-pointer transition-all duration-200 hover:shadow-lg ${
                        selectedQuizOption === option.id
                          ? 'border-blue-500 bg-blue-50'
                          : 'border-gray-200 bg-white hover:border-gray-300'
                      }`}
                    >
                      <div className="flex items-center space-x-4">
                        <div className="flex-shrink-0">
                          <div className={`w-12 h-12 rounded-lg flex items-center justify-center text-2xl ${
