import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { getAvailableLanguages, getLanguageName, setDocumentDirection } from '../i18n';
import './Navigation.css';

const Navigation = () => {
  const { t, i18n } = useTranslation();
  const navigate = useNavigate();
  const location = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isLanguageDropdownOpen, setIsLanguageDropdownOpen] = useState(false);
  const languageDropdownRef = useRef(null);
  const mobileMenuRef = useRef(null);

  // Get available languages
  const availableLanguages = getAvailableLanguages();
  const currentLanguage = i18n.language;

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (languageDropdownRef.current && !languageDropdownRef.current.contains(event.target)) {
        setIsLanguageDropdownOpen(false);
      }
      if (mobileMenuRef.current && !mobileMenuRef.current.contains(event.target)) {
        setIsMobileMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Handle keyboard navigation for mobile menu
  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === 'Escape') {
        setIsMobileMenuOpen(false);
        setIsLanguageDropdownOpen(false);
      }
    };

    if (isMobileMenuOpen || isLanguageDropdownOpen) {
      document.addEventListener('keydown', handleKeyDown);
      return () => {
        document.removeEventListener('keydown', handleKeyDown);
      };
    }
  }, [isMobileMenuOpen, isLanguageDropdownOpen]);

  // Navigation items configuration
  const navigationItems = [
    { key: 'home', path: '/', translationKey: 'navigation.home' },
    { key: 'peptides', path: '/peptides', translationKey: 'navigation.peptides' },
    { key: 'research', path: '/research', translationKey: 'navigation.research' },
    { key: 'blog', path: '/blog', translationKey: 'navigation.blog' },
    { key: 'calculators', path: '/calculators', translationKey: 'navigation.calculators' },
    { key: 'about', path: '/about', translationKey: 'navigation.about' },
  ];

  // Check if a navigation item is active
  const isActiveLink = (path) => {
    if (path === '/') {
      return location.pathname === '/' || location.pathname === `/${currentLanguage}`;
    }
    return location.pathname.includes(path);
  };

  // Handle language change
  const handleLanguageChange = (languageCode) => {
    const currentPath = location.pathname;
    let newPath = currentPath;

    // Remove current language prefix if it exists
    const pathSegments = currentPath.split('/').filter(Boolean);
    if (pathSegments.length > 0 && availableLanguages.some(lang => lang.code === pathSegments[0])) {
      pathSegments.shift(); // Remove language prefix
    }

    // Add new language prefix if not English (default)
    if (languageCode !== 'en') {
      newPath = `/${languageCode}${pathSegments.length > 0 ? '/' + pathSegments.join('/') : ''}`;
    } else {
      newPath = pathSegments.length > 0 ? '/' + pathSegments.join('/') : '/';
    }

    // Change language and navigate
    i18n.changeLanguage(languageCode);
    setDocumentDirection(languageCode);
    navigate(newPath);
    setIsLanguageDropdownOpen(false);
  };

  // Toggle mobile menu
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  // Handle mobile menu item click
  const handleMobileMenuItemClick = () => {
    setIsMobileMenuOpen(false);
  };

  // Generate navigation links with proper language prefix
  const getNavigationPath = (path) => {
    if (currentLanguage === 'en') {
      return path;
    }
    return `/${currentLanguage}${path}`;
  };

  return (
    <nav className="navigation" role="navigation" aria-label={t('navigation.main', 'Main navigation')}>
      <div className="navigation-container">
        {/* Logo */}
        <Link 
          to={getNavigationPath('/')} 
          className="logo-link"
          aria-label={t('site.name', 'Professor Peptides Home')}
        >
          <span className="logo-icon" role="img" aria-label="Peptide molecule">
            ðŸ§¬
          </span>
          <span className="logo-text">
            {t('site.name', 'Professor Peptides')}
          </span>
        </Link>

        {/* Desktop Navigation */}
        <div className="desktop-nav">
          <ul className="nav-list" role="menubar">
            {navigationItems.map((item) => (
              <li key={item.key} role="none">
                <Link
                  to={getNavigationPath(item.path)}
                  className={`nav-link ${isActiveLink(item.path) ? 'active' : ''}`}
                  role="menuitem"
                  aria-current={isActiveLink(item.path) ? 'page' : undefined}
                >
                  {t(item.translationKey, item.key)}
                </Link>
              </li>
            ))}
          </ul>

          <div className="nav-actions">
            {/* Language Selector */}
            <div className="language-selector-container" ref={languageDropdownRef}>
              <button
                className="language-selector"
                onClick={() => setIsLanguageDropdownOpen(!isLanguageDropdownOpen)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter' || e.key === ' ') {
                    e.preventDefault();
                    setIsLanguageDropdownOpen(!isLanguageDropdownOpen);
                  }
                }}
                aria-expanded={isLanguageDropdownOpen}
                aria-haspopup="listbox"
                aria-label={t('navigation.selectLanguage', 'Select language')}
              >
                ðŸŒ {getLanguageName(currentLanguage)}
                <span aria-hidden="true" className="dropdown-arrow">
                  {isLanguageDropdownOpen ? 'â–²' : 'â–¼'}
                </span>
              </button>

              {isLanguageDropdownOpen && (
                <div 
                  className="language-dropdown"
                  role="listbox"
                  aria-label={t('navigation.languageOptions', 'Language options')}
                >
                  {availableLanguages.map((language) => (
                    <button
                      key={language.code}
                      className={`language-option ${language.code === currentLanguage ? 'active' : ''}`}
                      onClick={() => handleLanguageChange(language.code)}
                      onKeyDown={(e) => {
                        if (e.key === 'Enter' || e.key === ' ') {
                          e.preventDefault();
                          handleLanguageChange(language.code);
                        }
                      }}
                      role="option"
                      aria-selected={language.code === currentLanguage}
                      dir={language.rtl ? 'rtl' : 'ltr'}
                    >
                      {language.name}
                      {language.code === currentLanguage && (
                        <span aria-hidden="true" className="check-mark">âœ“</span>
                      )}
                    </button>
                  ))}
                </div>
              )}
            </div>

            {/* CTA Button */}
            <Link 
              to={getNavigationPath('/guide')} 
              className="cta-button"
              aria-label={t('navigation.getFreeGuide', 'Get Free Peptide Guide')}
            >
              {t('navigation.getFreeGuide', 'Get Free Guide')}
            </Link>
          </div>
        </div>

        {/* Mobile Menu Button */}
        <button
          className="mobile-menu-button"
          onClick={toggleMobileMenu}
          onKeyDown={(e) => {
            if (e.key === 'Enter' || e.key === ' ') {
              e.preventDefault();
              toggleMobileMenu();
            }
          }}
          aria-expanded={isMobileMenuOpen}
          aria-controls="mobile-menu"
          aria-label={t('navigation.toggleMenu', 'Toggle navigation menu')}
        >
          <span aria-hidden="true">
            {isMobileMenuOpen ? 'âœ•' : 'â˜°'}
          </span>
        </button>
      </div>

      {/* Mobile Menu */}
      {isMobileMenuOpen && (
        <div 
          className="mobile-menu" 
          id="mobile-menu"
          ref={mobileMenuRef}
          role="menu"
          aria-label={t('navigation.mobileMenu', 'Mobile navigation menu')}
        >
          <div className="mobile-nav-list">
            {navigationItems.map((item) => (
              <Link
                key={item.key}
                to={getNavigationPath(item.path)}
                className={`mobile-nav-link ${isActiveLink(item.path) ? 'active' : ''}`}
                onClick={handleMobileMenuItemClick}
                role="menuitem"
                aria-current={isActiveLink(item.path) ? 'page' : undefined}
              >
                {t(item.translationKey, item.key)}
              </Link>
            ))}

            {/* Mobile CTA Button */}
            <Link 
              to={getNavigationPath('/guide')} 
              className="mobile-cta-button"
              onClick={handleMobileMenuItemClick}
              aria-label={t('navigation.getFreeGuide', 'Get Free Peptide Guide')}
            >
              {t('navigation.getFreeGuide', 'Get Free Guide')}
            </Link>

            {/* Mobile Language Selector */}
            <div className="mobile-language-section">
              <div className="mobile-language-title">
                {t('navigation.language', 'Language')}
              </div>
              <div className="mobile-language-grid">
                {availableLanguages.map((language) => (
                  <button
                    key={language.code}
                    className={`mobile-language-option ${language.code === currentLanguage ? 'active' : ''}`}
                    onClick={() => {
                      handleLanguageChange(language.code);
                      handleMobileMenuItemClick();
                    }}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter' || e.key === ' ') {
                        e.preventDefault();
                        handleLanguageChange(language.code);
                        handleMobileMenuItemClick();
                      }
                    }}
                    role="menuitem"
                    dir={language.rtl ? 'rtl' : 'ltr'}
                  >
                    {language.name}
                    {language.code === currentLanguage && (
                      <span aria-hidden="true" style={{ marginLeft: '0.25rem' }}>âœ“</span>
                    )}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navigation;

frontend/src/components/NewsletterSignup.content:
import React, { useState } from 'react';
import { Button } from './ui/button';
import { Mail, CheckCircle2, AlertCircle } from 'lucide-react';

const NewsletterSignup = () => {
  const [email, setEmail] = useState('');
  const [status, setStatus] = useState('idle'); // idle, loading, success, error
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !email.includes('@')) {
      setStatus('error');
      setMessage('Please enter a valid email address.');
      return;
    }

    setStatus('loading');

    try {
      // Simulate API call to backend newsletter signup
      const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/newsletter/signup`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      if (response.ok) {
        setStatus('success');
        setMessage('Thank you! You\'ll receive the latest peptide research updates.');
        setEmail('');
      } else {
        throw new Error('Signup failed');
      }
    } catch (error) {
      // Fallback to localStorage for now if backend endpoint doesn't exist
      const newsletters = JSON.parse(localStorage.getItem('newsletter-signups') || '[]');
      if (!newsletters.includes(email)) {
        newsletters.push(email);
        localStorage.setItem('newsletter-signups', JSON.stringify(newsletters));
      }

      setStatus('success');
      setMessage('Thank you! You\'ll receive the latest peptide research updates.');
      setEmail('');
    }

    // Reset status after 3 seconds
    setTimeout(() => {
      setStatus('idle');
      setMessage('');
    }, 3000);
  };

  return (
    <div className="bg-gradient-to-r from-blue-600 to-blue-700 rounded-2xl p-6 text-white">
      <div className="flex items-center mb-4">
        <Mail className="h-6 w-6 mr-2" />
        <h3 className="text-xl font-bold">Stay Updated on Peptide Research</h3>
      </div>

      <p className="text-blue-100 mb-4 text-sm">
        Get the latest research findings, safety updates, and new peptide discoveries delivered to your inbox.
      </p>

      <form onSubmit={handleSubmit} className="space-y-3">
        <div className="flex flex-col sm:flex-row gap-2">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email address"
            disabled={status === 'loading'}
            className="flex-1 px-4 py-2 rounded-lg text-gray-900 placeholder-gray-500 border-0 focus:ring-2 focus:ring-blue-300 disabled:opacity-50"
          />
          <Button
            type="submit"
            disabled={status === 'loading'}
            className="bg-white text-blue-700 hover:bg-gray-100 font-semibold px-6"
          >
            {status === 'loading' ? 'Subscribing...' : 'Subscribe'}
          </Button>
        </div>

        {message && (
          <div className={`flex items-center text-sm ${
            status === 'success' ? 'text-green-200' : 'text-red-200'
          }`}>
            {status === 'success' ? (
              <CheckCircle2 className="h-4 w-4 mr-1" />
            ) : (
              <AlertCircle className="h-4 w-4 mr-1" />
            )}
            {message}
          </div>
        )}
      </form>

      <p className="text-xs text-blue-200 mt-3">
        We respect your privacy. Unsubscribe at any time. No spam, just valuable research insights.
      </p>
    </div>
  );
};

export default NewsletterSignup;

frontend/src/components/OptimizedImage.j.content:
import React, { useState, useCallback } from 'react';

// Optimized image component with lazy loading and performance features
const OptimizedImage = ({ 
  src, 
  alt, 
  className = '', 
  width, 
  height,
  priority = false, // for LCP images
  placeholder = 'blur',
  blurDataURL = 'data:image/jpeg;base64,',
  loading = 'lazy',
  ...props 
}) => {
  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(false);

  const handleLoad = useCallback(() => {
    setLoaded(true);
  }, []);

  const handleError = useCallback(() => {
    setError(true);
  }, []);

  // Use priority loading for above-the-fold images
  const actualLoading = priority ? 'eager' : loading;

  return (
    <div className={`relative overflow-hidden ${className}`} style={{ width, height }}>
      {/* Blur placeholder */}
      {!loaded && !error && placeholder === 'blur' && (
        <img
          src={blurDataURL}
          alt=""
          className="absolute inset-0 w-full h-full object-cover filter blur-md scale-110"
          aria-hidden="true"
        />
      )}

      {/* Skeleton placeholder */}
      {!loaded && !error && placeholder === 'skeleton' && (
        <div className="absolute inset-0 bg-gray-200 animate-pulse" />
      )}

      {/* Main image */}
      {!error && (
        <img
          src={src}
          alt={alt}
          width={width}
          height={height}
          loading={actualLoading}
          onLoad={handleLoad}
          onError={handleError}
          className={`
            w-full h-full object-cover transition-opacity duration-300
            ${loaded ? 'opacity-100' : 'opacity-0'}
          `}
          style={{
            // Provide aspect ratio for layout stability
            aspectRatio: width && height ? `${width} / ${height}` : undefined
          }}
          {...props}
        />
      )}

      {/* Error fallback */}
      {error && (
        <div className="absolute inset-0 bg-gray-100 flex items-center justify-center">
          <div className="text-gray-400 text-center">
            <div className="w-8 h-8 mx-auto mb-2 bg-gray-300 rounded"></div>
            <span className="text-sm">Image unavailable</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default OptimizedImage;

