import React, { useState } from 'react';
import { Download, FileText, Loader2 } from 'lucide-react';

const PDFDownloadButton = ({ article, className = "" }) => {
  const [isGenerating, setIsGenerating] = useState(false);

  const generatePDF = async () => {
    setIsGenerating(true);

    try {
      // Create a new window with the article content formatted for PDF
      const printWindow = window.open('', '_blank');
      const articleElement = document.querySelector('.article-content');

      if (!articleElement) {
        throw new Error('Article content not found');
      }

      // PDF-optimized styling
      const pdfStyles = `
        <style>
          body {
            font-family: Georgia, serif;
            line-height: 1.6;
            color: #333;
            max-width: 8.5in;
            margin: 0 auto;
            padding: 1in;
            background: white;
          }
          .pdf-header {
            text-align: center;
            border-bottom: 2px solid #4F46E5;
            padding-bottom: 20px;
            margin-bottom: 30px;
          }
          .pdf-header h1 {
            font-size: 24px;
            font-weight: bold;
            margin: 0 0 10px 0;
            color: #1F2937;
          }
          .pdf-meta {
            font-size: 12px;
            color: #6B7280;
            margin-bottom: 20px;
          }
          .pdf-disclaimer {
            background: #FEF3C7;
            border: 1px solid #F59E0B;
            border-radius: 6px;
            padding: 15px;
            margin: 20px 0;
            font-size: 12px;
          }
          .pdf-content h2 {
            font-size: 20px;
            color: #1F2937;
            border-bottom: 1px solid #E5E7EB;
            padding-bottom: 5px;
            margin: 25px 0 15px 0;
          }
          .pdf-content h3 {
            font-size: 16px;
            color: #374151;
            margin: 20px 0 10px 0;
          }
          .pdf-content p {
            margin-bottom: 15px;
            text-align: justify;
          }
          .pdf-content ul, .pdf-content ol {
            margin: 15px 0;
            padding-left: 25px;
          }
          .pdf-content li {
            margin-bottom: 5px;
          }
          .pdf-highlight-box {
            background: #F3F4F6;
            border-left: 4px solid #4F46E5;
            padding: 15px;
            margin: 20px 0;
            border-radius: 0 6px 6px 0;
          }
          .pdf-footer {
            margin-top: 40px;
            padding-top: 20px;
            border-top: 1px solid #E5E7EB;
            text-align: center;
            font-size: 10px;
            color: #6B7280;
          }
          @media print {
            body { margin: 0; }
            .no-print { display: none; }
          }
        </style>
      `;

      // Create formatted PDF content
      const pdfContent = `
        <!DOCTYPE html>
        <html>
        <head>
          <meta charset="UTF-8">
          <title>${article.title}</title>
          ${pdfStyles}
        </head>
        <body>
          <div class="pdf-header">
            <h1>${article.title}</h1>
            <div class="pdf-meta">
              <strong>Author:</strong> Dr. Sheraz Ahmad, M.D. | 
              <strong>Category:</strong> ${article.category} | 
              <strong>Difficulty:</strong> ${article.difficulty} | 
              <strong>Word Count:</strong> ${article.wordCount} words
            </div>
          </div>

          <div class="pdf-disclaimer">
            <strong>Disclaimer:</strong> This document is provided for educational and research purposes only. 
            All content has been reviewed by Dr. Sheraz Ahmad, M.D. for accuracy. Not intended for clinical 
            or therapeutic use without proper medical supervision.
          </div>

          <div class="pdf-content">
            ${articleElement.innerHTML}
          </div>

          <div class="pdf-footer">
            <p>
              <strong>Professor Peptides Research Guide</strong><br>
              Published: ${new Date(article.publishDate).toLocaleDateString()} | 
              Downloaded: ${new Date().toLocaleDateString()}<br>
              Â© 2025 Professor Peptides. All rights reserved.<br>
              Website: https://www.professorpeptides.org
            </p>
          </div>
        </body>
        </html>
      `;

      printWindow.document.open();
      printWindow.document.write(pdfContent);
      printWindow.document.close();

      // Wait for content to load, then trigger print dialog
      setTimeout(() => {
        printWindow.focus();
        printWindow.print();

        // Close the window after a delay to allow printing
        setTimeout(() => {
          printWindow.close();
        }, 1000);

        setIsGenerating(false);
      }, 500);

    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('Error generating PDF. Please try again.');
      setIsGenerating(false);
    }
  };

  return (
    <button
      onClick={generatePDF}
      disabled={isGenerating}
      className={`inline-flex items-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white rounded-lg transition-colors duration-200 font-medium text-sm ${className}`}
      title="Download article as PDF"
    >
      {isGenerating ? (
        <>
          <Loader2 className="h-4 w-4 animate-spin" />
          Generating PDF...
        </>
      ) : (
        <>
          <Download className="h-4 w-4" />
          Download PDF
        </>
      )}
    </button>
  );
};

export default PDFDownloadButton;

