const PeptideShowcase = ({ searchTerm: propSearchTerm, selectedCategory: propSelectedCategory, showSearch = true }) => {
  const [selectedCategory, setSelectedCategory] = useState(propSelectedCategory || 'all');
  const [searchTerm, setSearchTerm] = useState(propSearchTerm || '');
  const [hoveredPeptide, setHoveredPeptide] = useState(null);
  const [allPeptides, setAllPeptides] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAllPeptides = async () => {
      try {
        setLoading(true);
        // Use static data service instead of direct API calls
        const categoriesData = await import('../services/staticDataService').then(module => 
          module.default.getPeptideCategories()
        );

        // Transform categories data
        const transformedCategories = [];
        const allPeptidesFlat = [];
        let totalCount = 0;

        // Process each category
        Object.entries(categoriesData).forEach(([categoryKey, categoryData]) => {
          const categoryId = categoryKey.replace(/([A-Z])/g, '-$1').toLowerCase().replace(/^-/, '');
          const peptideCount = categoryData.peptides ? categoryData.peptides.length : 0;
          totalCount += peptideCount;

          transformedCategories.push({
            id: categoryId,
            name: getCategoryDisplayName(categoryKey),
            icon: getCategoryIcon(categoryKey),
            count: peptideCount
          });

          // Transform peptides from this category
          if (categoryData.peptides) {
            categoryData.peptides.forEach(peptide => {
              allPeptidesFlat.push(transformPeptideData(peptide, categoryId));
            });
          }
        });

        // Add "All Categories" at the beginning
        transformedCategories.unshift({
          id: 'all',
          name: 'All Categories',
          icon: 'ðŸ§¬',
          count: totalCount
        });

        setCategories(transformedCategories);
        setAllPeptides(allPeptidesFlat);
      } catch (err) {
        console.error('Error fetching peptides:', err);
        setError('Error loading peptides');
        // Use fallback data
        setCategories(getFallbackCategories());
        setAllPeptides(getFallbackPeptides());
      } finally {
        setLoading(false);
      }
    };

    fetchAllPeptides();
  }, []);

  const getCategoryDisplayName = (categoryKey) => {
    const names = {
      'metabolicHealth': 'Weight Management',
      'muscleGrowthRecovery': 'Muscle Growth',
      'tissueRepairRecovery': 'Tissue Repair',
      'cognitiveEnhancement': 'Cognitive',
      'longevityResearch': 'Longevity',
      'skinHealth': 'Skin Health',
      'sexualWellness': 'Sexual Wellness',
      'antiAging': 'Anti-Aging'
    };
    return names[categoryKey] || categoryKey;
  };

  const getCategoryIcon = (categoryKey) => {
    const icons = {
      'metabolicHealth': 'âš–ï¸',
      'muscleGrowthRecovery': 'ðŸ’ª',
      'tissueRepairRecovery': 'ðŸ”¬',
      'cognitiveEnhancement': 'ðŸ§ ',
      'longevityResearch': 'â³',
