import React, { useState, useMemo } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Badge } from './ui/badge';
import { Button } from './ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from './ui/dialog';
import { useData } from '../contexts/DataContext';
import { X, Zap, Target, Clock, Search, Filter, Heart, GitCompare, CheckCircle2, FlaskConical } from 'lucide-react';

const PeptidesSection = () => {
  const { peptideCategories, loading, error } = useData();
  const [selectedPeptide, setSelectedPeptide] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterFDA, setFilterFDA] = useState('all'); // all, approved, research
  const [filterRoute, setFilterRoute] = useState('all'); // all, injection, topical, oral
  const [sortBy, setSortBy] = useState('name'); // name, category, fda
  const [favorites, setFavorites] = useState(() => {
    const saved = localStorage.getItem('peptide-favorites');
    return saved ? JSON.parse(saved) : [];
  });
  const [compareList, setCompareList] = useState([]);
  const [showFilters, setShowFilters] = useState(false);

  // Convert peptideCategories object to array format for rendering
  const categories = Object.values(peptideCategories || {});

  // Get all peptides from all categories for search/filter functionality
  const allPeptides = useMemo(() => {
    return categories.flatMap(category => 
      category.peptides?.map(peptide => ({
        ...peptide,
        categoryTitle: category.title,
        categoryKey: category.key
      })) || []
    );
  }, [categories]);

  // Advanced filtering and search
  const filteredPeptides = useMemo(() => {
    let filtered = allPeptides.filter(peptide => {
      // Search filter
      if (searchTerm) {
        const searchLower = searchTerm.toLowerCase();
        const matchesSearch = 
          peptide.name.toLowerCase().includes(searchLower) ||
          peptide.description?.toLowerCase().includes(searchLower) ||
          peptide.benefits?.some(benefit => benefit.toLowerCase().includes(searchLower)) ||
          peptide.categoryTitle.toLowerCase().includes(searchLower);

        if (!matchesSearch) return false;
      }

      // FDA approval filter
      if (filterFDA !== 'all') {
        if (filterFDA === 'approved' && !peptide.fdaApproved) return false;
        if (filterFDA === 'research' && peptide.fdaApproved) return false;
      }

      // Route of administration filter (basic inference from dosage)
      if (filterRoute !== 'all') {
        const dosage = peptide.dosage?.toLowerCase() || '';
        if (filterRoute === 'injection' && !dosage.includes('inject') && !dosage.includes('subcutaneous')) return false;
        if (filterRoute === 'topical' && !dosage.includes('topical') && !dosage.includes('apply')) return false;
      }

      return true;
    });

    // Sort results
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'name':
          return a.name.localeCompare(b.name);
        case 'category':
          return a.categoryTitle.localeCompare(b.categoryTitle);
        case 'fda':
          if (a.fdaApproved && !b.fdaApproved) return -1;
          if (!a.fdaApproved && b.fdaApproved) return 1;
          return a.name.localeCompare(b.name);
        default:
          return 0;
      }
    });

    return filtered;
  }, [allPeptides, searchTerm, filterFDA, filterRoute, sortBy]);

  // Favorites management
  const toggleFavorite = (peptide) => {
    const newFavorites = favorites.includes(peptide.slug)
      ? favorites.filter(slug => slug !== peptide.slug)
      : [...favorites, peptide.slug];

    setFavorites(newFavorites);
    localStorage.setItem('peptide-favorites', JSON.stringify(newFavorites));
  };

  // Comparison management
  const toggleCompare = (peptide) => {
    const isInCompare = compareList.find(p => p.slug === peptide.slug);
    if (isInCompare) {
      setCompareList(compareList.filter(p => p.slug !== peptide.slug));
    } else if (compareList.length < 3) {
      setCompareList([...compareList, peptide]);
    }
  };

  // FDA status badge component
  const FDAStatusBadge = ({ peptide }) => {
    if (peptide.fdaApproved) {
      return (
        <Badge className="bg-green-100 text-green-800 border-green-200">
          <CheckCircle2 className="w-3 h-3 mr-1" />
          FDA Approved
        </Badge>
      );
    } else {
      return (
        <Badge className="bg-yellow-100 text-yellow-800 border-yellow-200">
          <FlaskConical className="w-3 h-3 mr-1" />
          Research Only
        </Badge>
      );
    }
  };

  const handlePeptideClick = (peptide, category) => {
    setSelectedPeptide({ ...peptide, categoryTitle: category.title });
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
  };

  // Show loading state
  if (loading) {
    return (
      <section id="peptides" className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading peptide database...</p>
          </div>
        </div>
      </section>
    );
  }

  // Show error state
  if (error) {
    return (
      <section id="peptides" className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <p className="text-red-600 mb-4">âš ï¸ {error}</p>
            <p className="text-gray-600">Displaying cached peptide data.</p>
          </div>
        </div>
      </section>
    );
  }

  return (
    <>
      <section id="peptides" className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">
              Comprehensive Peptide Research Database
            </h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Explore our extensive collection of {allPeptides.length} research peptides with advanced search, filtering, and comparison tools
            </p>
          </div>

          {/* Enhanced Search and Filter Controls */}
          <div className="bg-white rounded-2xl shadow-lg p-6 mb-8 border">
            <div className="flex flex-col lg:flex-row gap-4 items-center justify-between mb-4">
              {/* Search Bar */}
              <div className="relative flex-1 max-w-md">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                <input
                  type="text"
                  placeholder="Search peptides, benefits, or categories..."
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>

              {/* Filter Toggle */}
              <Button
                variant="outline"
                onClick={() => setShowFilters(!showFilters)}
                className="flex items-center gap-2"
              >
                <Filter className="h-4 w-4" />
                Filters {showFilters ? 'â–²' : 'â–¼'}
              </Button>

              {/* Compare Button */}
              {compareList.length > 0 && (
                <Button
                  className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700"
                  onClick={() => setSelectedCategory({ title: 'Compare Peptides', compareMode: true, peptides: compareList })}
                >
                  <GitCompare className="h-4 w-4" />
                  Compare ({compareList.length})
                </Button>
              )}
            </div>

            {/* Advanced Filters */}
            {showFilters && (
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 pt-4 border-t border-gray-200">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">FDA Status</label>
                  <select
                    value={filterFDA}
                    onChange={(e) => setFilterFDA(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="all">All Peptides</option>
                    <option value="approved">FDA Approved Only</option>
                    <option value="research">Research Only</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Administration Route</label>
                  <select
                    value={filterRoute}
                    onChange={(e) => setFilterRoute(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="all">All Routes</option>
                    <option value="injection">Injectable</option>
                    <option value="topical">Topical</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Sort By</label>
                  <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    className="w-full p-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="name">Name A-Z</option>
                    <option value="category">Category</option>
                    <option value="fda">FDA Status</option>
                  </select>
                </div>
              </div>
            )}

            {/* Results Summary */}
            <div className="mt-4 text-sm text-gray-600">
              {searchTerm || filterFDA !== 'all' || filterRoute !== 'all' ? (
                <span>Showing {filteredPeptides.length} of {allPeptides.length} peptides</span>
              ) : (
                <span>Showing all {allPeptides.length} peptides across 8 categories</span>
              )}
            </div>
          </div>

          {/* Enhanced Peptide Grid */}
          {searchTerm || filterFDA !== 'all' || filterRoute !== 'all' ? (
            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
              {filteredPeptides.map((peptide, index) => (
                <Card key={index} className="hover:shadow-xl transition-all duration-300 border-0 shadow-lg group">
                  <CardHeader className="pb-3">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <CardTitle className="text-lg font-bold text-gray-900 group-hover:text-blue-600 transition-colors mb-2">
                          {peptide.name}
                        </CardTitle>
                        <FDAStatusBadge peptide={peptide} />
                      </div>

                      <div className="flex flex-col gap-1 ml-2">
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            toggleFavorite(peptide);
                          }}
                          className={`p-1 rounded-full transition-colors ${
                            favorites.includes(peptide.slug)
                              ? 'text-red-500 hover:text-red-600'
                              : 'text-gray-400 hover:text-red-500'
                          }`}
                        >
                          <Heart className={`h-4 w-4 ${favorites.includes(peptide.slug) ? 'fill-current' : ''}`} />
                        </button>

                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            toggleCompare(peptide);
                          }}
                          disabled={compareList.length >= 3 && !compareList.find(p => p.slug === peptide.slug)}
                          className={`p-1 rounded-full transition-colors ${
                            compareList.find(p => p.slug === peptide.slug)
                              ? 'text-blue-500 hover:text-blue-600'
                              : 'text-gray-400 hover:text-blue-500'
                          } disabled:opacity-50`}
                        >
                          <GitCompare className="h-4 w-4" />
                        </button>
                      </div>
                    </div>
                  </CardHeader>

                  <CardContent>
                    <div className="space-y-3">
                      <Badge variant="secondary" className="text-xs">
                        {peptide.categoryTitle}
                      </Badge>

                      <p className="text-sm text-gray-600 line-clamp-3">
                        {peptide.description}
                      </p>

                      {peptide.benefits && peptide.benefits.length > 0 && (
                        <div>
                          <p className="text-xs font-semibold text-gray-700 mb-1">Key Benefits:</p>
                          <div className="flex flex-wrap gap-1">
                            {peptide.benefits.slice(0, 3).map((benefit, i) => (
                              <Badge key={i} variant="outline" className="text-xs">
                                {benefit}
                              </Badge>
                            ))}
                            {peptide.benefits.length > 3 && (
                              <Badge variant="outline" className="text-xs">
                                +{peptide.benefits.length - 3} more
                              </Badge>
                            )}
                          </div>
                        </div>
                      )}

                      <Button
                        variant="outline"
                        size="sm"
                        className="w-full text-blue-600 hover:bg-blue-50"
                        onClick={() => setSelectedPeptide(peptide)}
                      >
                        View Details
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : (
            /* Category View */
            <div className="grid grid-cols-1 lg:grid-cols-2 xl:grid-cols-3 gap-8">
              {categories.map((category, index) => (
                <Card key={index} className="hover:shadow-xl transition-all duration-300 border-0 shadow-lg group cursor-pointer" onClick={() => handleCategoryClick(category)}>
                  <CardHeader className="pb-4">
                    <CardTitle className="text-xl font-bold text-gray-900 group-hover:text-blue-600 transition-colors">
                      {category.title}
                    </CardTitle>
                    {category.description && (
                      <p className="text-sm text-gray-600 mt-2">{category.description}</p>
                    )}
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3">
                      {category.peptides.slice(0, 4).map((peptide, peptideIndex) => (
                        <div 
                          key={peptideIndex}
                          className="flex items-center justify-between p-3 bg-white rounded-lg border hover:border-blue-200 hover:bg-blue-50 cursor-pointer transition-all duration-200"
                          onClick={(e) => {
                            e.stopPropagation();
                            handlePeptideClick(peptide, category);
                          }}
                        >
                          <div className="flex-1">
                            <span className="font-medium text-gray-800 block">
                              {peptide.name}
                            </span>
                            {peptide.fdaApproved !== undefined && (
                              <div className="flex items-center mt-1">
                                {peptide.fdaApproved ? (
                                  <span className="text-xs text-green-600 font-semibold flex items-center">
                                    <div className="w-2 h-2 bg-green-500 rounded-full mr-1"></div>
                                    FDA Approved
                                  </span>
                                ) : (
                                  <span className="text-xs text-orange-600 font-semibold flex items-center">
                                    <div className="w-2 h-2 bg-orange-500 rounded-full mr-1"></div>
                                    Research Only
                                  </span>
                                )}
                              </div>
                            )}
                          </div>
                          <div className="flex items-center gap-1">
                            <button
                              onClick={(e) => {
                                e.stopPropagation();
                                toggleFavorite(peptide);
                              }}
                              className={`p-1 rounded-full transition-colors ${
                                favorites.includes(peptide.slug)
                                  ? 'text-red-500 hover:text-red-600'
                                  : 'text-gray-300 hover:text-red-500'
                              }`}
                            >
                              <Heart className={`h-3 w-3 ${favorites.includes(peptide.slug) ? 'fill-current' : ''}`} />
                            </button>
                            <Badge variant="secondary" className="text-xs">
                              Research
                            </Badge>
                          </div>
                        </div>
                      ))}
                      {category.peptides.length > 4 && (
                        <div className="text-center pt-2">
                          <Button variant="outline" size="sm" className="text-blue-600">
                            View All {category.peptides.length} Peptides
                          </Button>
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {/* Call to Action */}
          <div className="text-center mt-16">
            <div className="bg-gradient-to-r from-blue-600 to-blue-700 text-white rounded-2xl p-8 max-w-4xl mx-auto">
              <h3 className="text-3xl font-bold mb-4">Advanced Peptide Research</h3>
              <p className="text-blue-100 mb-6 text-lg">
                Access our complete database of {allPeptides.length} research peptides with detailed protocols, safety information, and clinical insights from leading experts.
              </p>
              <div className="flex flex-wrap justify-center gap-4 mb-6">
                <div className="flex items-center text-blue-100">
                  <Target className="h-5 w-5 mr-2" />
                  <span>Clinical-Grade Quality</span>
                </div>
                <div className="flex items-center text-blue-100">
                  <Zap className="h-5 w-5 mr-2" />
                  <span>Expert-Verified Data</span>
                </div>
                <div className="flex items-center text-blue-100">
                  <Clock className="h-5 w-5 mr-2" />
                  <span>Regular Updates</span>
                </div>
              </div>
              <Button size="lg" variant="secondary" className="bg-white text-blue-700 hover:bg-gray-100 font-semibold">
                Access Full Research Database
              </Button>
            </div>
          </div>
        </div>
      </section>

      {/* Category Detail Modal */}
      {selectedCategory && (
        <Dialog open={!!selectedCategory} onOpenChange={() => setSelectedCategory(null)}>
          <DialogContent className="max-w-6xl max-h-[80vh] overflow-y-auto">
            <DialogHeader className="pb-4 border-b">
              <DialogTitle className="text-2xl font-bold text-gray-900">
                {selectedCategory.title}
              </DialogTitle>
              {selectedCategory.description && (
                <p className="text-gray-600 mt-2">{selectedCategory.description}</p>
              )}
            </DialogHeader>

            {selectedCategory.compareMode ? (
              /* Comparison View */
              <div className="mt-6">
                <div className="overflow-x-auto">
                  <table className="w-full border-collapse">
                    <thead>
                      <tr className="border-b">
                        <th className="text-left py-3 px-4 font-semibold">Characteristic</th>
                        {selectedCategory.peptides.map((peptide, index) => (
                          <th key={index} className="text-left py-3 px-4 font-semibold border-l">
                            <div className="flex items-center justify-between">
                              {peptide.name}
                              <button
                                onClick={() => toggleCompare(peptide)}
                                className="text-red-500 hover:text-red-700 ml-2"
                              >
                                <X className="h-4 w-4" />
                              </button>
                            </div>
                            <FDAStatusBadge peptide={peptide} />
                          </th>
                        ))}
                      </tr>
                    </thead>
                    <tbody>
                      <tr className="border-b bg-gray-50">
                        <td className="py-3 px-4 font-medium">Category</td>
                        {selectedCategory.peptides.map((peptide, index) => (
                          <td key={index} className="py-3 px-4 border-l">{peptide.categoryTitle}</td>
                        ))}
                      </tr>
                      <tr className="border-b">
                        <td className="py-3 px-4 font-medium">Description</td>
                        {selectedCategory.peptides.map((peptide, index) => (
                          <td key={index} className="py-3 px-4 border-l text-sm">
                            {peptide.description?.substring(0, 150)}...
                          </td>
                        ))}
                      </tr>
                      <tr className="border-b bg-gray-50">
                        <td className="py-3 px-4 font-medium">Key Benefits</td>
                        {selectedCategory.peptides.map((peptide, index) => (
                          <td key={index} className="py-3 px-4 border-l">
                            <div className="space-y-1">
                              {peptide.benefits?.slice(0, 4).map((benefit, i) => (
                                <div key={i} className="text-sm flex items-center">
                                  <div className="w-1.5 h-1.5 bg-blue-500 rounded-full mr-2"></div>
                                  {benefit}
                                </div>
                              ))}
                            </div>
                          </td>
                        ))}
                      </tr>
                      <tr className="border-b">
                        <td className="py-3 px-4 font-medium">Mechanism</td>
                        {selectedCategory.peptides.map((peptide, index) => (
                          <td key={index} className="py-3 px-4 border-l text-sm">
                            {peptide.mechanism?.substring(0, 100)}...
                          </td>
                        ))}
                      </tr>
                      <tr className="border-b bg-gray-50">
                        <td className="py-3 px-4 font-medium">Dosage</td>
                        {selectedCategory.peptides.map((peptide, index) => (
                          <td key={index} className="py-3 px-4 border-l text-sm">
                            {peptide.dosage}
                          </td>
                        ))}
                      </tr>
                      <tr className="border-b">
                        <td className="py-3 px-4 font-medium">Safety Note</td>
                        {selectedCategory.peptides.map((peptide, index) => (
                          <td key={index} className="py-3 px-4 border-l text-xs text-orange-600">
                            {peptide.safetyNote}
                          </td>
                        ))}
                      </tr>
                    </tbody>
                  </table>
                </div>
                <div className="mt-6 text-center">
                  <Button
                    onClick={() => setCompareList([])}
                    variant="outline"
                    className="mr-4"
                  >
                    Clear Comparison
                  </Button>
                  <Button onClick={() => setSelectedCategory(null)}>
                    Close Comparison
                  </Button>
                </div>
              </div>
            ) : (
              /* Regular Category View */
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6">
                {selectedCategory.peptides.map((peptide, index) => (
                  <Card key={index} className="hover:shadow-md transition-shadow cursor-pointer" onClick={() => handlePeptideClick(peptide, selectedCategory)}>
                    <CardContent className="p-4">
                      <div className="flex justify-between items-start mb-2">
                        <h4 className="font-bold text-lg text-gray-900">{peptide.name}</h4>
                        <div className="flex flex-col items-end">
                          <FDAStatusBadge peptide={peptide} />
                          <div className="flex items-center gap-1 mt-1">
                            <button
                              onClick={(e) => {
                                e.stopPropagation();
                                toggleFavorite(peptide);
                              }}
                              className={`p-1 rounded-full transition-colors ${
                                favorites.includes(peptide.slug)
                                  ? 'text-red-500 hover:text-red-600'
                                  : 'text-gray-300 hover:text-red-500'
                              }`}
                            >
                              <Heart className={`h-3 w-3 ${favorites.includes(peptide.slug) ? 'fill-current' : ''}`} />
                            </button>
                            <button
                              onClick={(e) => {
                                e.stopPropagation();
                                toggleCompare(peptide);
                              }}
                              disabled={compareList.length >= 3 && !compareList.find(p => p.slug === peptide.slug)}
                              className={`p-1 rounded-full transition-colors ${
                                compareList.find(p => p.slug === peptide.slug)
                                  ? 'text-blue-500 hover:text-blue-600'
                                  : 'text-gray-300 hover:text-blue-500'
                              } disabled:opacity-50`}
                            >
                              <GitCompare className="h-3 w-3" />
                            </button>
                          </div>
                        </div>
                      </div>
                      {peptide.description && (
                        <p className="text-sm text-gray-600 mb-3 line-clamp-2">
                          {peptide.description}
                        </p>
                      )}
                      {peptide.benefits && peptide.benefits.length > 0 && (
                        <div className="flex flex-wrap gap-1">
                          {peptide.benefits.slice(0, 3).map((benefit, idx) => (
                            <Badge key={idx} variant="outline" className="text-xs">
                              {benefit}
                            </Badge>
                          ))}
                          {peptide.benefits.length > 3 && (
                            <Badge variant="outline" className="text-xs">
                              +{peptide.benefits.length - 3} more
                            </Badge>
                          )}
                        </div>
                      )}
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </DialogContent>
        </Dialog>
      )}

      {/* Peptide Detail Modal */}
      {selectedPeptide && (
        <Dialog open={!!selectedPeptide} onOpenChange={() => setSelectedPeptide(null)}>
          <DialogContent className="max-w-2xl">
            <DialogHeader className="pb-4 border-b">
              <div className="flex justify-between items-start">
                <div className="flex-1">
                  <DialogTitle className="text-2xl font-bold text-gray-900">
                    {selectedPeptide.name}
                  </DialogTitle>
                  <div className="flex items-center gap-3 mt-2">
                    <p className="text-blue-600 font-medium">{selectedPeptide.categoryTitle}</p>
                    {selectedPeptide.fdaApproved !== undefined && (
                      <Badge 
                        variant={selectedPeptide.fdaApproved ? "default" : "secondary"}
                        className={`${
                          selectedPeptide.fdaApproved 
                            ? "bg-green-100 text-green-700 border-green-300" 
                            : "bg-orange-100 text-orange-700 border-orange-300"
                        }`}
                      >
                        {selectedPeptide.fdaApproved ? "FDA Approved" : "Research Only"}
                      </Badge>
                    )}
                  </div>
                </div>
              </div>
            </DialogHeader>

            <div className="space-y-6 mt-6">
              {/* FDA Approval Status */}
              {selectedPeptide.fdaApproved !== undefined && (
                <div className={`p-4 rounded-lg border-l-4 ${
                  selectedPeptide.fdaApproved 
                    ? "bg-green-50 border-green-400 border-l-green-500" 
                    : "bg-orange-50 border-orange-400 border-l-orange-500"
                }`}>
                  <h4 className="font-semibold text-gray-900 mb-2">Regulatory Status</h4>
                  {selectedPeptide.fdaApproved ? (
                    <div>
                      <p className="text-green-800 font-medium mb-1">âœ… FDA Approved</p>
                      {selectedPeptide.approvedFor && (
                        <div>
                          <p className="text-sm text-green-700 mb-1">Approved for:</p>
                          <ul className="text-sm text-green-700 ml-4">
                            {selectedPeptide.approvedFor.map((indication, idx) => (
                              <li key={idx} className="list-disc">{indication}</li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  ) : (
                    <div>
                      <p className="text-orange-800 font-medium mb-1">âš ï¸ Research Compound Only</p>
                      <p className="text-sm text-orange-700">{selectedPeptide.researchStatus}</p>
                      {selectedPeptide.safetyNote && (
                        <p className="text-xs text-orange-600 mt-2 font-medium">{selectedPeptide.safetyNote}</p>
                      )}
                    </div>
                  )}
                </div>
              )}

              {selectedPeptide.description && (
                <div>
                  <h4 className="font-semibold text-gray-900 mb-2">Description</h4>
                  <p className="text-gray-700">{selectedPeptide.description}</p>
                </div>
              )}

              {selectedPeptide.mechanism && (
                <div>
                  <h4 className="font-semibold text-gray-900 mb-2">Mechanism of Action</h4>
                  <p className="text-gray-700">{selectedPeptide.mechanism}</p>
                </div>
              )}

              {selectedPeptide.benefits && selectedPeptide.benefits.length > 0 && (
                <div>
                  <h4 className="font-semibold text-gray-900 mb-3">Key Benefits</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                    {selectedPeptide.benefits.map((benefit, index) => (
                      <div key={index} className="flex items-center text-sm text-gray-700">
                        <div className="w-2 h-2 bg-blue-500 rounded-full mr-2"></div>
                        {benefit}
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {selectedPeptide.researchFindings && (
                <div>
                  <h4 className="font-semibold text-gray-900 mb-3">Research Findings</h4>
                  <div className="space-y-2">
                    {selectedPeptide.researchFindings.map((finding, index) => (
                      <div key={index} className="flex items-start text-sm text-gray-700">
                        <div className="w-2 h-2 bg-blue-500 rounded-full mr-2 mt-2"></div>
                        <span>{finding}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {selectedPeptide.sideEffects && (
                <div>
                  <h4 className="font-semibold text-gray-900 mb-3">Common Side Effects</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                    {selectedPeptide.sideEffects.map((effect, index) => (
                      <div key={index} className="flex items-center text-sm text-red-600">
                        <div className="w-2 h-2 bg-red-500 rounded-full mr-2"></div>
                        {effect}
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {selectedPeptide.contraindications && (
                <div>
                  <h4 className="font-semibold text-gray-900 mb-3">Contraindications</h4>
                  <div className="space-y-1">
                    {selectedPeptide.contraindications.map((contraindication, index) => (
                      <div key={index} className="flex items-start text-sm text-red-700">
                        <div className="w-2 h-2 bg-red-600 rounded-full mr-2 mt-2"></div>
                        <span>{contraindication}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {selectedPeptide.dosage && (
                <div>
                  <h4 className="font-semibold text-gray-900 mb-2">
                    {selectedPeptide.fdaApproved ? "Clinical Dosage" : "Research Protocols"}
                  </h4>
                  <div className={`border rounded-lg p-3 ${
                    selectedPeptide.fdaApproved 
                      ? "bg-blue-50 border-blue-200" 
                      : "bg-yellow-50 border-yellow-200"
                  }`}>
                    <p className={`text-sm ${
                      selectedPeptide.fdaApproved ? "text-blue-800" : "text-yellow-800"
                    }`}>
                      {selectedPeptide.dosage}
                    </p>
                    <p className={`text-xs mt-1 font-medium ${
                      selectedPeptide.fdaApproved ? "text-blue-600" : "text-yellow-600"
                    }`}>
                      {selectedPeptide.fdaApproved 
                        ? "* Consult healthcare provider for proper prescription and monitoring."
                        : "* For research purposes only. Not approved for human consumption."
                      }
                    </p>
                  </div>
                </div>
              )}

              <div className="flex gap-3 pt-4 border-t">
                <Button className="flex-1 bg-blue-600 hover:bg-blue-700">
                  {selectedPeptide.fdaApproved ? "Clinical Studies" : "Research Data"}
                </Button>
                <Button variant="outline" className="flex-1">
                  Safety Profile
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </>
  );
};

export default PeptidesSection;

frontend/src/components/PrivacyPolicySec.content:
import React from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Shield, Eye, Lock, UserCheck, AlertTriangle, Mail, FileText } from 'lucide-react';

const PrivacyPolicySection = () => {
  const sections = [
    {
      title: "Information We Collect",
      icon: <Eye className="h-5 w-5" />,
      content: [
        {
          subtitle: "Personal Information",
          details: "When you use our services, we may collect personal information such as your name, email address, professional credentials, institution affiliation, and contact details when you register for an account, subscribe to our newsletter, or contact us."
        },
        {
          subtitle: "Professional Information", 
          details: "For healthcare professionals, we may collect medical license numbers, specialties, institution affiliations, and professional certifications to verify credentials and provide appropriate access to medical content."
        },
        {
          subtitle: "Usage Data",
          details: "We automatically collect information about how you interact with our website, including pages visited, time spent on pages, search queries, and technical information such as IP address, browser type, and device information."
        },
        {
          subtitle: "Research Data",
          details: "If you participate in research studies or clinical surveys, we may collect study-specific data as outlined in separate research consent forms."
        }
      ]
    },
    {
      title: "How We Use Your Information",
      icon: <UserCheck className="h-5 w-5" />,
      content: [
        {
          subtitle: "Educational Services",
          details: "To provide access to our peptide research database, educational materials, and personalized content recommendations based on your professional interests and specialization."
        },
        {
          subtitle: "Communication",
          details: "To send you important updates about peptide research, FDA approvals, safety alerts, newsletter content, and respond to your inquiries and support requests."
