import React, { createContext, useContext, useState, useEffect } from 'react';
import staticDataService from '../services/staticDataService';
// Import mock data as fallback
import { peptideCategories as mockPeptideCategories, teamMembers as mockTeamMembers, recentPosts as mockRecentPosts } from '../mock/data';

const DataContext = createContext();

export const useData = () => {
  const context = useContext(DataContext);
  if (!context) {
    throw new Error('useData must be used within a DataProvider');
  }
  return context;
};

export const DataProvider = ({ children }) => {
  const [peptideCategories, setPeptideCategories] = useState(mockPeptideCategories);
  const [teamMembers, setTeamMembers] = useState(mockTeamMembers);
  const [blogPosts, setBlogPosts] = useState(mockRecentPosts);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [apiConnected, setApiConnected] = useState(false);
  const [apiStatus, setApiStatus] = useState('static'); // Use static mode for frontend-only operation

  // Initialize static data service
  useEffect(() => {
    const initializeStaticData = async () => {
      try {
        setLoading(true);
        console.log('ðŸ“‹ Initializing static data service...');

        // Load data from static service
        const [categoriesData, teamData, blogData] = await Promise.all([
          staticDataService.getPeptideCategories(),
          staticDataService.getTeamMembers(),
          staticDataService.getBlogPosts()
        ]);

        setPeptideCategories(categoriesData);
        setTeamMembers(teamData.team || teamData);
        setBlogPosts(blogData.posts || blogData);

        setApiConnected(true);
        setApiStatus('static');
        console.log('âœ… Static data loaded successfully');
        console.log(`ðŸ“Š Build info:`, staticDataService.getBuildInfo());

      } catch (error) {
        console.error('âŒ Failed to load static data:', error);
        setApiStatus('fallback');
        setError('Using fallback data. Some features may be limited.');

        // Fallback to mock data
        setPeptideCategories(mockPeptideCategories);
        setTeamMembers(mockTeamMembers);
        setBlogPosts(mockRecentPosts);
      } finally {
        setLoading(false);
      }
    };

    initializeStaticData();
  }, []);

  const initializeData = async () => {
    try {
      setLoading(true);
      setError(null);
      setApiStatus('loading');

      // Test API connection first with health check
      if (process.env.NODE_ENV === 'development') {
        console.log('ðŸ” Testing API connection...');
      }
      await apiService.healthCheck();
      setApiConnected(true);
      setApiStatus('connected');
      if (process.env.NODE_ENV === 'development') {
        console.log('âœ… Backend API connected successfully');
      }

      // Fetch all data in parallel
      if (process.env.NODE_ENV === 'development') {
        console.log('ðŸ“¡ Fetching data from API...');
      }
      const [categoriesData, teamData, blogData] = await Promise.all([
        apiService.getPeptideCategories().catch(err => {
          console.warn('Failed to fetch peptide categories from API, using mock data:', err);
          return mockPeptideCategories;
        }),
        apiService.getTeamMembers().catch(err => {
          console.warn('Failed to fetch team members from API, using mock data:', err);
          return mockTeamMembers;
        }),
        apiService.getBlogPosts().catch(err => {
          console.warn('Failed to fetch blog posts from API, using mock data:', err);
          return mockRecentPosts;
        })
      ]);

      // Update state with API data
      setPeptideCategories(categoriesData);
      setTeamMembers(teamData);
      setBlogPosts(blogData);

      console.log('âœ… All data loaded successfully from API');
    } catch (error) {
      console.error('âŒ Failed to connect to backend API:', error);
      setApiConnected(false);
      setApiStatus('fallback');

      // More user-friendly error message based on error type
      let errorMessage = 'Showing cached peptide data. Live updates will refresh automatically.';

      if (error.code === 'ECONNABORTED') {
        errorMessage = 'API response timeout. Using cached data while we restore the connection.';
      } else if (error.code === 'NETWORK_ERROR') {
        errorMessage = 'Network connectivity issue detected. Displaying cached data.';
      } else if (error.response && error.response.status >= 500) {
        errorMessage = 'Server temporarily unavailable. Showing latest cached data.';
      }

      setError(errorMessage);

      // Use mock data as fallback
      setPeptideCategories(mockPeptideCategories);
      setTeamMembers(mockTeamMembers);
      setBlogPosts(mockRecentPosts);

      console.log('ðŸ“‹ Using cached data as fallback');

      // Attempt to reconnect after 30 seconds
      setTimeout(() => {
        console.log('ðŸ”„ Attempting to reconnect to API...');
        retryConnection();
      }, 30000);
    } finally {
      setLoading(false);
    }
  };

  // Retry connection function
  const retryConnection = async () => {
    try {
      setApiStatus('retry');
      console.log('ðŸ”„ Retrying API connection...');

      await apiService.healthCheck();
      setApiConnected(true);
      setApiStatus('connected');
      setError(null);

      // Refresh data after successful reconnection
      const categoriesData = await apiService.getPeptideCategories();
      setPeptideCategories(categoriesData);

      console.log('âœ… API reconnected successfully, data refreshed');
    } catch (error) {
      console.log('âŒ Reconnection failed, will retry again in 60 seconds');
      setApiStatus('fallback');

      // Try again in 60 seconds
      setTimeout(() => {
        retryConnection();
      }, 60000);
    }
  };

  // Function to submit contact form
  const submitContactForm = async (formData) => {
    try {
      if (!apiConnected) {
        throw new Error('Backend API not available');
      }

      const result = await apiService.submitContactForm(formData);
      console.log('âœ… Contact form submitted successfully:', result);
      return result;
    } catch (error) {
      console.error('âŒ Failed to submit contact form:', error);
      throw error;
    }
  };

  // Function to submit newsletter signup
  const submitNewsletterSignup = async (signupData) => {
    try {
      if (!apiConnected) {
        throw new Error('Backend API not available');
      }

      const result = await apiService.submitNewsletterSignup(signupData);
      console.log('âœ… Newsletter signup submitted successfully:', result);
      return result;
    } catch (error) {
      console.error('âŒ Failed to submit newsletter signup:', error);
      throw error;
    }
  };

  // Function to refresh data
  const refreshData = async () => {
    await initializeData();
  };

  // Function to manually retry connection
  const retryConnectionNow = async () => {
    await retryConnection();
  };

  const value = {
    // Data
    peptideCategories,
    teamMembers,
    blogPosts,

    // State
    loading,
    error,
    apiConnected,
    apiStatus, // 'loading', 'connected', 'fallback', 'retry'

    // Functions
    submitContactForm,
    submitNewsletterSignup,
    refreshData,
    retryConnectionNow,

    // Individual data fetchers (for specific use cases)
    fetchPeptideCategory: staticDataService.getPeptideCategory,
    fetchPeptide: staticDataService.getPeptide,
    calculatePeptide: staticDataService.calculatePeptide
  };

  return <DataContext.Provider value={value}>{children}</DataContext.Provider>;
};

export default DataContext;

