// Interaction checking function
export const checkPeptideInteractions = (selectedPeptides) => {
  const interactions = [];

  for (let i = 0; i < selectedPeptides.length; i++) {
    for (let j = i + 1; j < selectedPeptides.length; j++) {
      const peptide1 = selectedPeptides[i];
      const peptide2 = selectedPeptides[j];

      // Check if interaction exists in either direction
      const interaction1 = peptideInteractions[peptide1.slug]?.interactions?.[peptide2.slug];
      const interaction2 = peptideInteractions[peptide2.slug]?.interactions?.[peptide1.slug];

      const interaction = interaction1 || interaction2;

      if (interaction) {
        interactions.push({
          peptide1: peptide1.name,
          peptide2: peptide2.name,
          type: interaction.type,
          level: interaction.level,
          description: interaction.description
        });
      }
    }
  }

  return interactions;
};

export default peptideInteractions;

