frontend/src/pages/BMICalculatorPage.jsx.content:
import React, { useState, useEffect, useRef } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MetaTags from '../components/seo/MetaTags';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../components/ui/select';
import { Badge } from '../components/ui/badge';
import { Progress } from '../components/ui/progress';
import { 
  Calculator, 
  Heart, 
  Activity,
  TrendingUp,
  AlertTriangle,
  CheckCircle,
  Target,
  Zap,
  Scale,
  Gauge,
  Info
} from 'lucide-react';

const BMICalculatorPage = () => {
  const [formData, setFormData] = useState({
    age: '',
    sex: '',
    height_cm: '',
    weight_kg: '',
    activity_level: '',
    height_ft: '',
    height_in: '',
    weight_lbs: ''
  });

  const [unitSystem, setUnitSystem] = useState('metric'); // 'metric' or 'imperial'
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showResults, setShowResults] = useState(false);
  const [animatedBMI, setAnimatedBMI] = useState(0);
  const [animatedBMR, setAnimatedBMR] = useState(0);
  const [animatedTDEE, setAnimatedTDEE] = useState(0);
  const animationFrameRef = useRef(null);

  // Medical-grade unit conversion functions
  const lbsToKg = (lbs) => lbs / 2.20462;
  const kgToLbs = (kg) => kg * 2.20462;
  const ftInToCm = (ft, inches) => (ft * 12 + inches) * 2.54;
  const cmToInches = (cm) => cm / 2.54;
  const cmToFtIn = (cm) => {
    const totalInches = cm / 2.54;
    const feet = Math.floor(totalInches / 12);
    const inches = totalInches % 12;
    return { feet, inches };
  };

  // BMI Calculation Functions
  const calculateBMI = (weightKg, heightCm) => {
    const heightM = heightCm / 100;
    return weightKg / (heightM * heightM);
  };

  const calculateBMIImperial = (weightLbs, heightInches) => {
    return (weightLbs / (heightInches * heightInches)) * 703;
  };

  // BMI Classification Functions
  const getBMIClassifications = (bmi) => {
    return {
      who: {
        category: bmi < 18.5 ? 'Underweight' : 
                 bmi < 25 ? 'Normal' :
                 bmi < 30 ? 'Overweight' : 'Obese',
        risk: bmi < 18.5 ? 'Increased health risks' :
              bmi < 25 ? 'Minimal health risks' :
              bmi < 30 ? 'Increased health risks' : 'High health risks',
        color: bmi < 18.5 ? 'text-blue-600' :
               bmi < 25 ? 'text-green-600' :
               bmi < 30 ? 'text-yellow-600' : 'text-red-600'
      },
      asian: {
        category: bmi < 18.5 ? 'Underweight' :
                 bmi < 23 ? 'Normal' :
                 bmi < 25 ? 'Overweight' : 'Obese',
        risk: bmi < 18.5 ? 'Increased health risks' :
              bmi < 23 ? 'Minimal health risks' :
              bmi < 25 ? 'Moderate health risks' : 'High health risks',
        color: bmi < 18.5 ? 'text-blue-600' :
               bmi < 23 ? 'text-green-600' :
               bmi < 25 ? 'text-yellow-600' : 'text-red-600'
      },
      singapore: {
        category: bmi < 18.5 ? 'Low BMI' :
                 bmi < 23 ? 'Low Risk' :
                 bmi < 27.5 ? 'Moderate Risk' : 'High Risk',
        risk: bmi < 18.5 ? 'Nutritional deficiency risk' :
              bmi < 23 ? 'Low risk for CVD and diabetes' :
              bmi < 27.5 ? 'Moderate risk for CVD and diabetes' : 'High risk for CVD and diabetes',
        color: bmi < 18.5 ? 'text-blue-600' :
               bmi < 23 ? 'text-green-600' :
               bmi < 27.5 ? 'text-yellow-600' : 'text-red-600'
      }
    };
  };

  // BMR Calculation Functions
  const calculateMifflinStJeor = (weightKg, heightCm, age, sex) => {
    const baseBMR = 10 * weightKg + 6.25 * heightCm - 5 * age;
    return sex === 'male' ? baseBMR + 5 : baseBMR - 161;
  };

  const calculateHarrisBenedict = (weightLbs, heightInches, age, sex) => {
    if (sex === 'male') {
      return 66 + (6.23 * weightLbs) + (12.7 * heightInches) - (6.8 * age);
    } else {
      return 655 + (4.35 * weightLbs) + (4.7 * heightInches) - (4.7 * age);
    }
  };

  // TDEE Calculation with all activity levels
  const calculateTDEE = (bmr) => {
    return {
      sedentary: bmr * 1.2,
      lightlyActive: bmr * 1.375,
      moderatelyActive: bmr * 1.55,
      veryActive: bmr * 1.725,
      extremelyActive: bmr * 1.9
    };
  };

  // Activity level descriptions
  const activityLevels = {
    sedentary: { label: 'Sedentary', description: 'Little or no exercise', multiplier: 1.2 },
    lightlyActive: { label: 'Lightly Active', description: 'Light exercise 1-3 days/week', multiplier: 1.375 },
    moderatelyActive: { label: 'Moderately Active', description: 'Moderate exercise 3-5 days/week', multiplier: 1.55 },
    veryActive: { label: 'Very Active', description: 'Hard exercise 6-7 days/week', multiplier: 1.725 },
    extremelyActive: { label: 'Extremely Active', description: 'Very hard exercise, physical job', multiplier: 1.9 }
  };

  // Healthy weight range calculation
  const calculateHealthyWeightRange = (heightCm) => {
    const heightM = heightCm / 100;
    const minWeight = 18.5 * (heightM * heightM);
    const maxWeight = 24.9 * (heightM * heightM);
    return { min: minWeight, max: maxWeight };
  };

  const handleUnitChange = (newUnit) => {
    setUnitSystem(newUnit);

    if (newUnit === 'imperial' && formData.height_cm && formData.weight_kg) {
      // Convert from metric to imperial
      const { feet, inches } = cmToFtIn(parseFloat(formData.height_cm));
      const lbs = kgToLbs(parseFloat(formData.weight_kg));

      setFormData(prev => ({
        ...prev,
        height_ft: Math.floor(feet).toString(),
        height_in: Math.round(inches).toString(),
        weight_lbs: Math.round(lbs).toString()
      }));
    } else if (newUnit === 'metric' && formData.height_ft && formData.height_in && formData.weight_lbs) {
      // Convert from imperial to metric
      const cm = ftInToCm(parseFloat(formData.height_ft), parseFloat(formData.height_in));
      const kg = lbsToKg(parseFloat(formData.weight_lbs));

      setFormData(prev => ({
        ...prev,
        height_cm: Math.round(cm).toString(),
        weight_kg: Math.round(kg).toString()
      }));
    }
  };

  const handleCalculation = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Get values based on unit system
      let height_cm, weight_kg, weight_lbs, height_inches;

      if (unitSystem === 'imperial') {
        height_inches = parseFloat(formData.height_ft) * 12 + parseFloat(formData.height_in);
        weight_lbs = parseFloat(formData.weight_lbs);
        height_cm = ftInToCm(parseFloat(formData.height_ft), parseFloat(formData.height_in));
        weight_kg = lbsToKg(weight_lbs);
      } else {
        height_cm = parseFloat(formData.height_cm);
        weight_kg = parseFloat(formData.weight_kg);
        weight_lbs = kgToLbs(weight_kg);
        height_inches = cmToInches(height_cm);
      }

      const age = parseInt(formData.age);
      const sex = formData.sex;

      // Calculate BMI using both methods
      const bmi_metric = calculateBMI(weight_kg, height_cm);
      const bmi_imperial = calculateBMIImperial(weight_lbs, height_inches);
      const bmi = bmi_metric; // Use metric as primary

      // Get all BMI classifications
      const classifications = getBMIClassifications(bmi);

      // Calculate BMR using both methods
      const bmr_mifflin = calculateMifflinStJeor(weight_kg, height_cm, age, sex);
      const bmr_harris = calculateHarrisBenedict(weight_lbs, height_inches, age, sex);

      // Calculate TDEE for all activity levels
      const tdee_mifflin = calculateTDEE(bmr_mifflin);
      const tdee_harris = calculateTDEE(bmr_harris);

      // Calculate healthy weight range
      const healthyRange = calculateHealthyWeightRange(height_cm);

      // Calculate selected activity level TDEE
      const selectedTDEE = formData.activity_level ? 
        bmr_mifflin * activityLevels[formData.activity_level]?.multiplier || bmr_mifflin * 1.2 : 
        bmr_mifflin * 1.2;

      const result = {
        // Basic measurements
        height_cm: height_cm,
        weight_kg: weight_kg,
        height_inches: height_inches,
        weight_lbs: weight_lbs,
        age: age,
        sex: sex,

        // BMI Results
        bmi: bmi,
        bmi_metric: bmi_metric,
        bmi_imperial: bmi_imperial,
        bmi_category: classifications.who.category,

        // All BMI Classifications
        bmi_classifications: classifications,

        // BMR Results (both methods)
        bmr_mifflin: bmr_mifflin,
        bmr_harris: bmr_harris,
        bmr: bmr_mifflin, // Primary BMR

        // TDEE Results
        tdee_mifflin: tdee_mifflin,
        tdee_harris: tdee_harris,
        tdee: selectedTDEE, // Primary TDEE based on selected activity
        selected_activity_level: formData.activity_level,

        // Healthy weight range
        healthy_weight_min: healthyRange.min,
        healthy_weight_max: healthyRange.max,

        // Additional info
        activity_levels: activityLevels,
        calculation_id: `calc_${Date.now()}`
      };

      setResults(result);

    } catch (err) {
      console.error('Calculation error:', err);
      setError('Calculation error. Please check your inputs and try again.');
    } finally {
      setLoading(false);
    }
  };

  const getBMIColor = (bmi) => {
    if (bmi < 18.5) return 'text-blue-600';
    if (bmi < 25) return 'text-green-600';
    if (bmi < 30) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getBMIProgress = (bmi) => {
    // Scale BMI (15-40) to 0-100% for progress bar
    return Math.min(Math.max((bmi - 15) * 4, 0), 100);
  };

  // Animation functions
  const animateNumber = (start, end, setter, duration = 2000) => {
    const startTime = Date.now();
    const animate = () => {
      const elapsed = Date.now() - startTime;
      const progress = Math.min(elapsed / duration, 1);

      // Easing function (ease-out)
      const easeOut = 1 - Math.pow(1 - progress, 3);
      const current = start + (end - start) * easeOut;

      setter(Math.round(current * 10) / 10); // Round to 1 decimal place

      if (progress < 1) {
        animationFrameRef.current = requestAnimationFrame(animate);
      }
    };
    animate();
  };

  // Calculate needle angle for BMI gauge (0-180 degrees)
  const getNeedleAngle = (bmi) => {
    // Map BMI (15-40) to angle (0-180)
    const minBMI = 15;
    const maxBMI = 40;
    const clampedBMI = Math.min(Math.max(bmi, minBMI), maxBMI);
    return ((clampedBMI - minBMI) / (maxBMI - minBMI)) * 180;
  };

  // Get section colors and glow effects
  const getBMISection = (bmi) => {
    if (bmi < 18.5) return { color: '#3B82F6', glow: '#3B82F630', label: 'Underweight' };
    if (bmi < 25) return { color: '#10B981', glow: '#10B98130', label: 'Normal' };
    if (bmi < 30) return { color: '#F59E0B', glow: '#F59E0B30', label: 'Overweight' };
    return { color: '#EF4444', glow: '#EF444430', label: 'Obese' };
  };

  // Trigger animations when results change
  useEffect(() => {
    if (results) {
      // Cancel any existing animations
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }

      // Reset animated values
      setAnimatedBMI(0);
      setAnimatedBMR(0);
      setAnimatedTDEE(0);
      setShowResults(false);

      // Start animations with delay
      setTimeout(() => {
        setShowResults(true);
        animateNumber(0, results.bmi, setAnimatedBMI, 1500);
        animateNumber(0, results.bmr, setAnimatedBMR, 2000);
        animateNumber(0, results.tdee, setAnimatedTDEE, 2500);
      }, 300);
    }

    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, [results]);

  return (
    <>
      <style jsx>{`
        @keyframes slideInBounce {
          0% { opacity: 0; transform: translateY(30px) scale(0.9); }
          50% { opacity: 0.8; transform: translateY(-10px) scale(1.05); }
          100% { opacity: 1; transform: translateY(0) scale(1); }
        }

        @keyframes slideInLeft {
          0% { opacity: 0; transform: translateX(-50px) scale(0.9); }
          60% { opacity: 0.8; transform: translateX(5px) scale(1.05); }
          100% { opacity: 1; transform: translateX(0) scale(1); }
        }

        @keyframes slideInRight {
          0% { opacity: 0; transform: translateX(50px) scale(0.9); }
          60% { opacity: 0.8; transform: translateX(-5px) scale(1.05); }
          100% { opacity: 1; transform: translateX(0) scale(1); }
        }

        @keyframes pulse {
          0%, 100% { opacity: 1; transform: scale(1); }
          50% { opacity: 0.8; transform: scale(1.05); }
        }

        @keyframes glow {
          0%, 100% { box-shadow: 0 0 5px rgba(59, 130, 246, 0.5); }
          50% { box-shadow: 0 0 20px rgba(59, 130, 246, 0.8); }
        }

        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-5px); }
        }

        @keyframes fadeInUp {
          0% { opacity: 0; transform: translateY(20px); }
          100% { opacity: 1; transform: translateY(0); }
        }

        .animate-needle {
          transform-origin: center bottom;
          transition: transform 2.5s cubic-bezier(0.68, -0.55, 0.265, 1.55);
        }

        .animate-glow {
          animation: glow 2s ease-in-out infinite;
        }

        .animate-float {
          animation: float 3s ease-in-out infinite;
        }

        .animate-fade-in-up {
          animation: fadeInUp 0.8s ease-out forwards;
        }

        .medical-pattern {
          background-image: 
            radial-gradient(circle at 20% 50%, rgba(59, 130, 246, 0.05) 0%, transparent 70%),
            radial-gradient(circle at 80% 20%, rgba(16, 185, 129, 0.05) 0%, transparent 70%),
            radial-gradient(circle at 40% 80%, rgba(139, 92, 246, 0.05) 0%, transparent 70%);
          background-size: 300px 300px, 400px 400px, 200px 200px;
          background-position: 0 0, 100px 100px, 200px 0;
        }

        .card-gradient {
          background: linear-gradient(135deg, rgba(255,255,255,0.9) 0%, rgba(248,250,252,0.9) 100%);
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255,255,255,0.2);
        }

        .icon-gradient {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          background-clip: text;
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
        }
      `}</style>

      <MetaTags 
        title="Comprehensive Medical BMI Calculator | Multiple Scales, BMR & TDEE"
        description="Professional medical-grade BMI calculator with WHO, Asian, and Singapore scales. Dual BMR methods (Mifflin-St Jeor & Harris-Benedict), complete TDEE breakdown, and precise unit conversions."
        keywords="medical BMI calculator, WHO BMI scale, Asian BMI scale, Singapore BMI scale, Mifflin-St Jeor, Harris-Benedict, BMR calculator, TDEE calculator, medical grade calculator, comprehensive BMI analysis"
        canonical="https://www.professorpeptides.org/calculators/bmi"
        type="article"
      />

      <Header />

      <div className="min-h-screen bg-gray-50 medical-pattern py-20">
        <div className="max-w-7xl mx-auto px-4">
          {/* Page Header */}
          <div className="text-center mb-12 animate-fade-in-up">
            <div className="flex justify-center items-center mb-4">
              <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center animate-float shadow-lg">
                <Heart className="h-8 w-8 text-blue-600" />
              </div>
            </div>
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Comprehensive Medical BMI Calculator
            </h1>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Professional-grade BMI analysis with multiple classification scales, dual BMR methods, complete TDEE breakdown, and precise unit conversions for medical and fitness applications
            </p>
            <Badge className="mt-4 bg-blue-100 text-blue-700">
              <CheckCircle className="h-4 w-4 mr-1" />
              FREE Calculator
            </Badge>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Calculator Form */}
            <Card className="shadow-xl">
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Calculator className="h-5 w-5 mr-2 text-blue-600" />
                  BMI & Metabolic Calculator
                </CardTitle>
                <p className="text-gray-600">
                  Enter your details to calculate comprehensive metabolic metrics
                </p>
              </CardHeader>
              <CardContent>
                <form onSubmit={handleCalculation} className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Age (years)
                      </label>
                      <Input
                        type="number"
                        value={formData.age}
                        onChange={(e) => setFormData({...formData, age: e.target.value})}
                        placeholder="25"
                        min="18"
                        max="120"
                        required
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Sex
                      </label>
                      <Select onValueChange={(value) => setFormData({...formData, sex: value})}>
                        <SelectTrigger>
                          <SelectValue placeholder="Select sex" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="male">Male</SelectItem>
                          <SelectItem value="female">Female</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                  </div>

                  {/* Unit System Toggle */}
                  <div className="flex justify-center mb-4">
                    <div className="bg-gray-100 rounded-lg p-1 flex">
                      <button
                        type="button"
                        onClick={() => handleUnitChange('metric')}
                        className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                          unitSystem === 'metric' 
                            ? 'bg-blue-600 text-white' 
                            : 'text-gray-600 hover:text-gray-900'
                        }`}
                      >
                        Metric (kg, cm)
                      </button>
                      <button
                        type="button"
                        onClick={() => handleUnitChange('imperial')}
                        className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                          unitSystem === 'imperial' 
                            ? 'bg-blue-600 text-white' 
                            : 'text-gray-600 hover:text-gray-900'
                        }`}
                      >
                        Imperial (lbs, ft/in)
                      </button>
                    </div>
                  </div>

                  {/* Height and Weight Fields - Dynamic based on unit system */}
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        {unitSystem === 'metric' ? 'Height (cm)' : 'Height (ft/in)'}
                      </label>
                      {unitSystem === 'metric' ? (
                        <Input
                          type="number"
                          step="0.1"
                          value={formData.height_cm}
                          onChange={(e) => setFormData({...formData, height_cm: e.target.value})}
                          placeholder="175.0"
                          min="100"
                          max="250"
                          required
                        />
                      ) : (
                        <div className="flex gap-2">
                          <Input
                            type="number"
                            value={formData.height_ft}
                            onChange={(e) => setFormData({...formData, height_ft: e.target.value})}
                            placeholder="5"
                            min="4"
                            max="8"
                            required
                            className="flex-1"
                          />
                          <span className="self-center text-sm text-gray-500">ft</span>
                          <Input
                            type="number"
                            value={formData.height_in}
                            onChange={(e) => setFormData({...formData, height_in: e.target.value})}
                            placeholder="9"
                            min="0"
                            max="11"
                            required
                            className="flex-1"
                          />
                          <span className="self-center text-sm text-gray-500">in</span>
                        </div>
                      )}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        {unitSystem === 'metric' ? 'Weight (kg)' : 'Weight (lbs)'}
                      </label>
                      {unitSystem === 'metric' ? (
                        <Input
                          type="number"
                          step="0.1"
                          value={formData.weight_kg}
                          onChange={(e) => setFormData({...formData, weight_kg: e.target.value})}
                          placeholder="70.0"
                          min="30"
                          max="300"
                          required
                        />
                      ) : (
                        <Input
                          type="number"
                          step="0.1"
                          value={formData.weight_lbs}
                          onChange={(e) => setFormData({...formData, weight_lbs: e.target.value})}
                          placeholder="154.0"
                          min="66"
                          max="660"
                          required
                        />
                      )}
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Activity Level
                    </label>
                    <Select onValueChange={(value) => setFormData({...formData, activity_level: value})}>
                      <SelectTrigger>
                        <SelectValue placeholder="Select activity level" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="sedentary">Sedentary (Ã—1.2) - Little or no exercise</SelectItem>
