frontend/src/pages/BlogLandingPage.jsx.content:
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MetaTags from '../components/seo/MetaTags';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Badge } from '../components/ui/badge';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { 
  Calendar, 
  Clock, 
  User, 
  ArrowRight, 
  Search,
  Filter,
  BookOpen,
  TrendingUp,
  Star,
  Eye,
  ChevronLeft,
  ChevronRight
} from 'lucide-react';

const BlogPage = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const postsPerPage = 9;

  const categories = [
    { id: 'all', name: 'All Posts', count: 0 },
    { id: 'research', name: 'Research', count: 0 },
    { id: 'guides', name: 'Guides', count: 0 },
    { id: 'safety', name: 'Safety', count: 0 },
    { id: 'protocols', name: 'Protocols', count: 0 }
  ];

  const featuredTopics = [
    {
      title: "GLP-1 Agonists",
      description: "Complete guide to semaglutide, tirzepatide, and related peptides",
      postCount: 8,
      color: "blue"
    },
    {
      title: "Healing Peptides", 
      description: "BPC-157, TB-500, and tissue repair mechanisms",
      postCount: 6,
      color: "green"
    },
    {
      title: "Safety Protocols",
      description: "Best practices for research peptide handling and use",
      postCount: 12,
      color: "red"
    },
    {
      title: "Cognitive Enhancement",
      description: "Nootropic peptides and brain health research", 
      postCount: 5,
      color: "blue"
    }
  ];

  useEffect(() => {
    fetchBlogPosts();
  }, []);

  const fetchBlogPosts = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/blog`);
      if (response.ok) {
        const blogPosts = await response.json();
        setPosts(blogPosts);
      }
    } catch (error) {
      console.error('Error fetching blog posts:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredPosts = posts.filter(post => {
    const matchesSearch = !searchTerm || 
      post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      post.excerpt.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesCategory = selectedCategory === 'all' || 
      post.category.toLowerCase() === selectedCategory;

    return matchesSearch && matchesCategory;
  });

  const totalPages = Math.ceil(filteredPosts.length / postsPerPage);
  const currentPosts = filteredPosts.slice(
    (currentPage - 1) * postsPerPage,
    currentPage * postsPerPage
  );

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const calculateReadingTime = (content) => {
    if (!content) return '5 min read';
    const words = content.split(' ').length;
    const readingTime = Math.ceil(words / 200);
    return `${readingTime} min read`;
  };

  const getTopicColorClass = (color) => {
    const colors = {
      blue: "bg-blue-50 border-blue-200 hover:bg-blue-100",
      green: "bg-green-50 border-green-200 hover:bg-green-100", 
      red: "bg-red-50 border-red-200 hover:bg-red-100",
      purple: "bg-blue-50 border-blue-200 hover:bg-blue-100"
    };
    return colors[color] || colors.blue;
  };

  if (loading) {
    return (
      <>
        <Header />
        <div className="min-h-screen bg-gray-50 flex items-center justify-center">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600">Loading blog posts...</p>
          </div>
        </div>
        <Footer />
      </>
    );
  }

  return (
    <>
      <MetaTags 
        title="Peptide Research Blog | Latest Insights & Scientific Updates"
        description="Stay updated with the latest peptide research, safety guidelines, protocol guides, and scientific breakthroughs. Expert insights from leading researchers and medical professionals."
        keywords="peptide blog, research insights, peptide safety, protocol guides, scientific updates, peptide research"
        canonical="https://www.professorpeptides.org/blog"
        type="website"
      />

      <Header />

      <main className="min-h-screen bg-gray-50" role="main" aria-label="Blog articles and research insights">
        {/* Hero Section */}
        <section className="bg-gradient-to-r from-blue-600 to-blue-700 text-white py-16">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="text-center">
              <h1 className="text-4xl md:text-5xl font-bold mb-6">
                Research Insights & Updates
              </h1>
              <p className="text-xl text-blue-100 max-w-3xl mx-auto mb-8 leading-relaxed">
                Stay informed with the latest peptide research, safety protocols, 
                and expert insights from leading scientists and medical professionals.
              </p>

              <div className="flex flex-wrap justify-center gap-6 text-blue-100">
                <div className="flex items-center">
                  <BookOpen className="h-5 w-5 mr-2" />
                  <span>Expert Authored</span>
                </div>
                <div className="flex items-center">
                  <TrendingUp className="h-5 w-5 mr-2" />
                  <span>Latest Research</span>
                </div>
                <div className="flex items-center">
                  <Star className="h-5 w-5 mr-2" />
                  <span>Evidence-Based</span>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Search and Filter Section */}
        <section className="py-8">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="bg-white rounded-2xl shadow-lg p-6">
              <div className="flex flex-col lg:flex-row gap-4 items-center justify-between">
                {/* Search Bar */}
                <div className="relative flex-1 max-w-md">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                  <Input
                    type="text"
                    placeholder="Search articles..."
                    className="pl-10 pr-4 py-2"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>

                {/* Category Filter */}
                <div className="flex gap-2 flex-wrap">
                  {categories.map((category) => (
                    <Button
                      key={category.id}
                      variant={selectedCategory === category.id ? "default" : "outline"}
                      size="sm"
                      onClick={() => setSelectedCategory(category.id)}
                      className="text-sm"
                    >
                      {category.name}
                    </Button>
                  ))}
                </div>
              </div>

              {/* Results Summary */}
              <div className="mt-4 text-sm text-gray-600">
                {searchTerm || selectedCategory !== 'all' ? (
                  <span>Showing {filteredPosts.length} of {posts.length} articles</span>
                ) : (
                  <span>Showing all {posts.length} articles</span>
                )}
              </div>
            </div>
          </div>
        </section>

        {/* Featured Topics */}
        <section className="pb-8">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="text-center mb-8">
              <h2 className="text-2xl font-bold text-gray-900 mb-4">
                Featured Topics
              </h2>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {featuredTopics.map((topic, index) => (
                <div 
                  key={index}
                  className={`p-4 rounded-lg border-2 transition-colors cursor-pointer ${getTopicColorClass(topic.color)}`}
                >
                  <h3 className="font-semibold text-gray-900 mb-2">{topic.title}</h3>
                  <p className="text-sm text-gray-600 mb-3">{topic.description}</p>
                  <div className="text-xs text-gray-500">
                    {topic.postCount} articles
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* Blog Posts Grid */}
        <section className="pb-16">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            {currentPosts.length === 0 ? (
              <div className="text-center py-16">
                <BookOpen className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                <h3 className="text-xl font-semibold text-gray-900 mb-2">No articles found</h3>
                <p className="text-gray-600">Try adjusting your search terms or filters.</p>
              </div>
            ) : (
              <>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-12">
                  {currentPosts.map((post) => (
                    <Card key={post.id} className="group hover:shadow-xl transition-all duration-300 border-0 shadow-lg overflow-hidden">
                      <Link to={`/blog/${post.slug}`}>
                        {/* Featured Image */}
                        <div className="relative">
                          {post.image ? (
                            <img 
                              src={post.image} 
                              alt={post.featured_image_alt || post.title}
                              className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
                            />
                          ) : (
                            <div className="w-full h-48 bg-gradient-to-br from-blue-100 to-blue-200 flex items-center justify-center group-hover:from-blue-200 group-hover:to-blue-300 transition-colors">
                              <BookOpen className="h-12 w-12 text-blue-600" />
                            </div>
                          )}
                          <Badge className="absolute top-4 left-4 bg-blue-600 text-white">
                            {post.category}
                          </Badge>
                        </div>

                        <CardHeader className="pb-2">
                          <CardTitle className="text-lg font-bold text-gray-900 leading-tight group-hover:text-blue-600 transition-colors line-clamp-2">
                            {post.title}
                          </CardTitle>
                        </CardHeader>

                        <CardContent className="pt-0">
                          <p className="text-gray-600 text-sm leading-relaxed mb-4 line-clamp-3">
                            {post.excerpt}
                          </p>

                          {/* Post Metadata */}
                          <div className="flex items-center justify-between text-xs text-gray-500 mb-4">
                            <div className="flex items-center space-x-4">
                              <div className="flex items-center">
                                <Calendar className="h-3 w-3 mr-1" />
                                {formatDate(post.date)}
                              </div>
                              <div className="flex items-center">
                                <Clock className="h-3 w-3 mr-1" />
                                {post.reading_time || calculateReadingTime(post.content)}
                              </div>
                            </div>
                          </div>

                          {/* Author Info */}
                          <div className="flex items-center justify-between">
                            <div className="flex items-center">
                              <div className="w-6 h-6 bg-blue-600 rounded-full flex items-center justify-center mr-2">
                                <User className="h-3 w-3 text-white" />
                              </div>
                              <span className="text-xs text-gray-600 font-medium">{post.author}</span>
                            </div>
                            <div className="flex items-center text-blue-600 text-xs font-medium group-hover:text-blue-700">
                              Read More <ArrowRight className="h-3 w-3 ml-1 group-hover:translate-x-1 transition-transform" />
                            </div>
                          </div>
                        </CardContent>
                      </Link>
                    </Card>
                  ))}
                </div>

                {/* Pagination */}
                {totalPages > 1 && (
                  <div className="flex items-center justify-center space-x-4">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(currentPage - 1)}
                      disabled={currentPage === 1}
                      className="flex items-center"
                    >
                      <ChevronLeft className="h-4 w-4 mr-1" /> Previous
                    </Button>

                    <div className="flex space-x-2">
                      {Array.from({ length: totalPages }, (_, i) => (
                        <Button
                          key={i + 1}
                          variant={currentPage === i + 1 ? "default" : "outline"}
                          size="sm"
                          onClick={() => setCurrentPage(i + 1)}
                          className="w-8 h-8 p-0"
                        >
                          {i + 1}
                        </Button>
                      ))}
                    </div>

                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setCurrentPage(currentPage + 1)}
                      disabled={currentPage === totalPages}
                      className="flex items-center"
                    >
                      Next <ChevronRight className="h-4 w-4 ml-1" />
                    </Button>
                  </div>
                )}
              </>
            )}
          </div>
        </section>

        {/* Newsletter Signup */}
        <section className="pb-16">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="bg-gradient-to-r from-blue-600 to-blue-700 rounded-2xl p-8 text-center text-white">
              <h3 className="text-2xl font-bold mb-4">Stay Updated</h3>
              <p className="text-blue-100 mb-6 max-w-2xl mx-auto">
                Get the latest research insights, safety updates, and protocol guides 
                delivered directly to your inbox.
              </p>
              <div className="flex flex-col sm:flex-row gap-4 max-w-md mx-auto">
                <Input
                  type="email"
                  placeholder="Enter your email"
                  className="bg-white text-gray-900"
                />
                <Button className="bg-white text-blue-600 hover:bg-gray-100">
                  Subscribe
                </Button>
              </div>
            </div>
          </div>
        </section>
      </main>

      <Footer />
    </>
  );
};

export default BlogPage;

