import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Helmet } from 'react-helmet-async';
import { Clock, Calendar, User, ArrowLeft, ExternalLink } from 'lucide-react';
import ReactMarkdown from 'react-markdown';

const BlogPage = () => {
  const { slug } = useParams();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/blog/${slug}`);
        if (response.ok) {
          const postData = await response.json();
          setPost(postData);
        } else {
          setError('Blog post not found');
        }
      } catch (err) {
        setError('Failed to load blog post');
      } finally {
        setLoading(false);
      }
    };

    if (slug) {
      fetchPost();
    }
  }, [slug]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading blog post...</p>
        </div>
      </div>
    );
  }

  if (error || !post) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-900 mb-4">Blog Post Not Found</h1>
          <p className="text-gray-600 mb-6">{error}</p>
          <Link 
            to="/" 
            className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
          >
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Home
          </Link>
        </div>
      </div>
    );
  }

  // Create structured data for the blog post
  const structuredData = {
    "@context": "https://schema.org",
    "@graph": [
      {
        "@type": "Article",
        "@id": `${post.canonical}#article`,
        "isPartOf": { "@id": `${post.canonical}#webpage` },
        "author": {
          "@type": "Person",
          "name": post.author,
          "description": post.author_bio
        },
        "headline": post.title,
        "datePublished": post.date,
        "dateModified": post.last_updated,
        "mainEntityOfPage": { "@id": `${post.canonical}#webpage` },
        "wordCount": post.content ? post.content.split(' ').length : 0,
        "commentCount": 0,
        "publisher": {
          "@type": "Organization",
          "name": "Peptide Professor",
          "@id": "https://www.professorpeptides.org/#organization"
        },
        "image": {
          "@type": "ImageObject",
          "url": `https://www.professorpeptides.org${post.image}`,
          "width": 1200,
          "height": 630
        },
        "thumbnailUrl": `https://www.professorpeptides.org${post.image}`,
        "articleSection": post.category,
        "inLanguage": "en-US"
      },
      {
        "@type": "WebPage",
        "@id": `${post.canonical}#webpage`,
        "url": post.canonical,
        "name": post.meta_title,
        "isPartOf": { "@id": "https://www.professorpeptides.org/#website" },
        "datePublished": post.date,
        "dateModified": post.last_updated,
        "description": post.meta_description,
        "breadcrumb": { "@id": `${post.canonical}#breadcrumb` },
        "inLanguage": "en-US",
        "potentialAction": [{
          "@type": "ReadAction",
          "target": [post.canonical]
        }]
      },
      {
        "@type": "BreadcrumbList",
        "@id": `${post.canonical}#breadcrumb`,
        "itemListElement": [
          {
            "@type": "ListItem",
            "position": 1,
            "name": "Home",
            "item": "https://www.professorpeptides.org/"
          },
          {
            "@type": "ListItem",
            "position": 2,
            "name": "Blog",
            "item": "https://www.professorpeptides.org/blog"
          },
          {
            "@type": "ListItem",
            "position": 3,
            "name": post.title
          }
        ]
      }
    ]
  };

  return (
    <>
      <Helmet>
        <title>{post.meta_title}</title>
        <meta name="description" content={post.meta_description} />
