        {/* Open Graph */}
        <meta property="og:title" content={post.meta_title} />
        <meta property="og:description" content={post.meta_description} />
        <meta property="og:image" content={`https://www.professorpeptides.org${post.image}`} />
        <meta property="og:url" content={post.canonical} />
        <meta property="og:type" content="article" />
        <meta property="article:published_time" content={post.date} />
        <meta property="article:modified_time" content={post.last_updated} />
        <meta property="article:author" content={post.author} />
        <meta property="article:section" content={post.category} />

        {/* Twitter Card */}
        <meta name="twitter:card" content="summary_large_image" />
        <meta name="twitter:title" content={post.meta_title} />
        <meta name="twitter:description" content={post.meta_description} />
        <meta name="twitter:image" content={`https://www.professorpeptides.org${post.image}`} />

        {/* Structured Data */}
        <script type="application/ld+json">
          {JSON.stringify(structuredData)}
        </script>
      </Helmet>

      <div className="min-h-screen bg-white">
        {/* Hero Section */}
        <div className="bg-gradient-to-r from-blue-600 to-blue-700 text-white py-12">
          <div className="max-w-4xl mx-auto px-4">
            <Link 
              to="/" 
              className="inline-flex items-center text-blue-100 hover:text-white mb-6 transition-colors"
            >
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to Home
            </Link>

            <div className="mb-6">
              <span className="inline-block px-3 py-1 bg-blue-500 bg-opacity-30 rounded-full text-sm font-medium mb-4">
                {post.category}
              </span>
              <h1 className="text-4xl md:text-5xl font-bold leading-tight mb-4">
                {post.title}
              </h1>
              <p className="text-xl text-blue-100 leading-relaxed">
                {post.excerpt}
              </p>
            </div>

            <div className="flex flex-wrap items-center gap-6 text-sm text-blue-100">
              <div className="flex items-center">
                <User className="h-4 w-4 mr-2" />
                {post.author}
              </div>
              <div className="flex items-center">
                <Calendar className="h-4 w-4 mr-2" />
                {new Date(post.date).toLocaleDateString('en-US', { 
                  year: 'numeric', 
                  month: 'long', 
                  day: 'numeric' 
                })}
              </div>
              <div className="flex items-center">
                <Clock className="h-4 w-4 mr-2" />
                {post.reading_time}
              </div>
            </div>
          </div>
        </div>

        {/* Article Content */}
        <div className="max-w-4xl mx-auto px-4 py-12">
          <div className="bg-white rounded-lg shadow-sm">
            {/* Featured Image */}
            {post.image && (
              <div className="mb-8">
                <img 
                  src={post.image} 
                  alt={post.featured_image_alt}
                  className="w-full h-64 md:h-80 object-cover rounded-lg shadow-md"
                />
              </div>
            )}

            {/* Article Body */}
            <div className="prose prose-lg max-w-none">
              <ReactMarkdown
                components={{
                  // Custom link rendering to open external links in new tab
                  a: ({ href, children, ...props }) => {
                    const isExternal = href?.startsWith('http') && !href.includes('peptide-professor');
                    const isInternal = href?.startsWith('/');

                    if (isExternal) {
                      return (
                        <a 
                          href={href} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="text-blue-600 hover:text-blue-800 inline-flex items-center"
                          {...props}
                        >
                          {children}
                          <ExternalLink className="h-3 w-3 ml-1" />
                        </a>
                      );
                    } else if (isInternal) {
                      return (
                        <Link 
                          to={href} 
                          className="text-blue-600 hover:text-blue-800"
                          {...props}
                        >
                          {children}
                        </Link>
                      );
                    }
                    return <a href={href} {...props}>{children}</a>;
                  },
                  // Custom heading rendering for better SEO
                  h1: ({ children, ...props }) => (
                    <h1 className="text-3xl font-bold text-gray-900 mt-8 mb-4" {...props}>
                      {children}
                    </h1>
                  ),
                  h2: ({ children, ...props }) => (
                    <h2 className="text-2xl font-bold text-gray-900 mt-8 mb-4" {...props}>
                      {children}
                    </h2>
                  ),
                  h3: ({ children, ...props }) => (
                    <h3 className="text-xl font-semibold text-gray-900 mt-6 mb-3" {...props}>
                      {children}
                    </h3>
                  ),
                }}
              >
                {post.content}
              </ReactMarkdown>
            </div>

            {/* Author Bio */}
            {post.author_bio && (
              <div className="mt-12 pt-8 border-t border-gray-200">
                <div className="bg-gray-50 rounded-lg p-6">
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">About the Author</h3>
                  <div className="flex items-start space-x-4">
                    <div className="w-12 h-12 bg-blue-600 rounded-full flex items-center justify-center">
                      <User className="h-6 w-6 text-white" />
                    </div>
                    <div>
                      <p className="font-medium text-gray-900">{post.author}</p>
                      <p className="text-gray-600 mt-1">{post.author_bio}</p>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {/* Article Footer */}
            <div className="mt-8 pt-6 border-t border-gray-200 text-sm text-gray-500">
              <p>
                Last updated: {new Date(post.last_updated).toLocaleDateString('en-US', { 
                  year: 'numeric', 
                  month: 'long', 
                  day: 'numeric' 
                })}
              </p>
            </div>
          </div>
        </div>

        {/* Call to Action */}
        <div className="bg-gray-50 py-12">
          <div className="max-w-4xl mx-auto px-4 text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">
              Ready to Explore Peptide Calculators?
            </h2>
            <p className="text-gray-600 mb-6">
              Use our professional-grade calculators to optimize your peptide research and protocols.
            </p>
            <div className="flex flex-wrap justify-center gap-4">
              <Link 
                to="/calculators/bmi"
                className="bg-blue-600 text-white px-6 py-3 rounded-md hover:bg-blue-700 transition-colors"
              >
                BMI Calculator
              </Link>
              <Link 
                to="/calculators/glp1"
                className="bg-blue-600 text-white px-6 py-3 rounded-md hover:bg-blue-700 transition-colors"
              >
                GLP-1 Calculator
              </Link>
              <Link 
                to="/calculators/reconstitution"
                className="bg-green-600 text-white px-6 py-3 rounded-md hover:bg-green-700 transition-colors"
              >
                Reconstitution Calculator
              </Link>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default BlogPage;

