import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet-async';
import MetaTags from '../components/seo/MetaTags';
import { useLocation } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import AboutUsSection from '../components/AboutUsSection';
import TeamSection from '../components/TeamSection';
import ContactSection from '../components/ContactSection';
import PrivacyPolicySection from '../components/PrivacyPolicySection';
import TermsOfServiceSection from '../components/TermsOfServiceSection';
import { Button } from '../components/ui/button';
import { 
  Building, 
  Users, 
  Mail, 
  Shield, 
  FileText, 
  ArrowLeft,
  ChevronRight 
} from 'lucide-react';

const CompanyPages = () => {
  const location = useLocation();
  const [currentPage, setCurrentPage] = useState('about');

  // Extract page from URL hash or default to 'about'
  useEffect(() => {
    const hash = location.hash.replace('#', '');
    if (hash && ['about', 'team', 'contact', 'privacy', 'terms'].includes(hash)) {
      setCurrentPage(hash);
    } else {
      setCurrentPage('about');
    }
  }, [location]);

  const pages = [
    {
      id: 'about',
      title: 'About Us',
      icon: <Building className="h-5 w-5" />,
      component: <AboutUsSection />,
      description: 'Learn about our mission, values, and commitment to peptide research education'
    },
    {
      id: 'team',
      title: 'Our Team',
      icon: <Users className="h-5 w-5" />,
      component: <TeamSection />,
      description: 'Meet our world-class team of medical experts and researchers'
    },
    {
      id: 'contact',
      title: 'Contact',
      icon: <Mail className="h-5 w-5" />,
      component: <ContactSection />,
      description: 'Get in touch with our team for support, partnerships, or inquiries'
    },
    {
      id: 'privacy',
      title: 'Privacy Policy',
      icon: <Shield className="h-5 w-5" />,
      component: <PrivacyPolicySection />,
      description: 'Understand how we protect and handle your personal information'
    },
    {
      id: 'terms',
      title: 'Terms of Service',
      icon: <FileText className="h-5 w-5" />,
      component: <TermsOfServiceSection />,
      description: 'Review our terms of service and user responsibilities'
    }
  ];

  const currentPageData = pages.find(page => page.id === currentPage) || pages[0];

  const handlePageChange = (pageId) => {
    setCurrentPage(pageId);
    window.location.hash = pageId;
  };

  const getMetaData = () => {
    const baseUrl = 'https://www.professorpeptides.org';

    switch(currentPage) {
      case 'about':
        return {
          title: 'About Peptide Professor | Leading Peptide Research Platform',
          description: 'Learn about our mission to provide reliable peptide research and calculator tools. Meet our team of experts dedicated to advancing peptide science.',
          canonical: `${baseUrl}/about`,
          keywords: 'about peptide professor, peptide research team, peptide science mission'
        };
      case 'team':
        return {
          title: 'Our Expert Team | Peptide Professor',
          description: 'Meet our world-class team of medical experts and researchers specializing in peptide science and therapeutic applications.',
          canonical: `${baseUrl}/team`,
          keywords: 'peptide research team, medical experts, peptide scientists, research professionals'
        };
      case 'contact':
        return {
          title: 'Contact Peptide Professor | Get in Touch',
          description: 'Contact our team for support, partnerships, or inquiries about peptide research and calculator tools.',
          canonical: `${baseUrl}/contact`,
          keywords: 'contact peptide professor, peptide research support, partnership inquiries'
        };
      case 'privacy':
        return {
          title: 'Privacy Policy | Peptide Professor',
          description: 'Understand how Peptide Professor protects and handles your personal information. Our commitment to privacy and data security.',
          canonical: `${baseUrl}/privacy`,
          keywords: 'privacy policy, data protection, information security'
        };
      case 'terms':
        return {
          title: 'Terms of Service | Peptide Professor',
          description: 'Review our terms of service and user responsibilities for using Peptide Professor research tools and calculators.',
          canonical: `${baseUrl}/terms`,
          keywords: 'terms of service, user agreement, service terms'
        };
      default:
        return {
          title: 'Company Information | Peptide Professor',
          description: 'Learn more about Peptide Professor, our team, and how to get in touch for peptide research needs.',
          canonical: `${baseUrl}/company`,
          keywords: 'peptide professor company, about us, contact information'
        };
    }
  };

  const metaData = getMetaData();

  return (
    <>
      <MetaTags 
        title={metaData.title}
        description={metaData.description}
        keywords={metaData.keywords}
        canonical={metaData.canonical}
        type="website"
      />
      <div className="min-h-screen bg-white">
      <Header />

      {/* Page Navigation */}
      <div className="bg-gray-50 border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="py-4">
            {/* Breadcrumb */}
            <div className="flex items-center text-sm text-gray-600 mb-4">
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={() => window.location.href = '/'}
                className="text-blue-600 hover:text-blue-700 p-0"
              >
                <ArrowLeft className="h-4 w-4 mr-1" />
                Back to Home
              </Button>
              <ChevronRight className="h-4 w-4 mx-2" />
              <span>Company</span>
              <ChevronRight className="h-4 w-4 mx-2" />
              <span className="text-gray-900 font-medium">{currentPageData.title}</span>
            </div>

            {/* Page Navigation Tabs */}
            <div className="flex flex-wrap gap-2">
              {pages.map((page) => (
                <Button
                  key={page.id}
                  variant={currentPage === page.id ? "default" : "outline"}
                  size="sm"
                  onClick={() => handlePageChange(page.id)}
                  className={`flex items-center ${
                    currentPage === page.id 
                      ? 'bg-blue-600 text-white' 
                      : 'text-gray-700 hover:text-blue-600'
                  }`}
                >
                  {page.icon}
                  <span className="ml-2">{page.title}</span>
                </Button>
              ))}
            </div>

            {/* Page Description */}
            <div className="mt-4 p-4 bg-white rounded-lg border">
              <p className="text-gray-600 text-sm">
                {currentPageData.description}
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Page Content */}
      <main>
        {currentPageData.component}
      </main>

      <Footer />
    </div>
    </>
  );
};

export default CompanyPages;

