  const filteredCategories = faqCategories.map(category => ({
    ...category,
    questions: category.questions.filter(q =>
      q.question.toLowerCase().includes(searchTerm.toLowerCase()) ||
      q.answer.toLowerCase().includes(searchTerm.toLowerCase())
    )
  })).filter(category => category.questions.length > 0);

  const totalQuestions = faqCategories.reduce((acc, cat) => acc + cat.questions.length, 0);

  return (
    <>
      <Helmet>
        <title>FAQ - Frequently Asked Questions | Peptide Professor</title>
        <meta name="description" content="Find answers to frequently asked questions about peptides, calculators, research, and how to use Peptide Professor's educational tools and resources." />
