      red: 'text-red-600 bg-red-50',
      yellow: 'text-yellow-600 bg-yellow-50',
      pink: 'text-pink-600 bg-pink-50'
    };
    return colors[peptideOptions[peptide]?.color] || colors.blue;
  };

