import React, { useState } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MetaTags from '../components/seo/MetaTags';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Badge } from '../components/ui/badge';
import { Alert, AlertDescription } from '../components/ui/alert';
import { 
  Microscope, 
  AlertTriangle, 
  CheckCircle, 
  Clock,
  Shield,
  Plus,
  X,
  Info
} from 'lucide-react';

const PeptideCompatibilityPage = () => {
  const [selectedPeptides, setSelectedPeptides] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState(null);

  const availablePeptides = [
    { id: 'semaglutide', name: 'Semaglutide', category: 'GLP-1 Agonist' },
    { id: 'tirzepatide', name: 'Tirzepatide', category: 'GLP-1/GIP Agonist' },
    { id: 'bpc157', name: 'BPC-157', category: 'Healing' },
    { id: 'tb500', name: 'TB-500', category: 'Healing' },
    { id: 'cjc1295', name: 'CJC-1295', category: 'Growth Hormone' },
    { id: 'ipamorelin', name: 'Ipamorelin', category: 'Growth Hormone' },
    { id: 'semax', name: 'Semax', category: 'Nootropic' },
    { id: 'selank', name: 'Selank', category: 'Nootropic' }
  ];

  const filteredPeptides = availablePeptides.filter(peptide =>
    peptide.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
    !selectedPeptides.find(selected => selected.id === peptide.id)
  );

  const addPeptide = (peptide) => {
    if (selectedPeptides.length < 5) {
      setSelectedPeptides([...selectedPeptides, peptide]);
    }
  };

  const removePeptide = (peptideId) => {
    setSelectedPeptides(selectedPeptides.filter(p => p.id !== peptideId));
  };

  const analyzeCompatibility = () => {
    if (selectedPeptides.length < 2) {
      return;
    }

    // Simulate compatibility analysis
    const compatibility = {
      overall: 'Good',
      warnings: [
        'Consider spacing injections by at least 30 minutes when combining multiple peptides',
        'Monitor for enhanced effects when combining healing peptides'
      ],
      synergies: [
        'BPC-157 and TB-500 work synergistically for tissue repair',
        'CJC-1295 and Ipamorelin have complementary GH release patterns'
      ],
      interactions: [],
      recommendations: [
        'Start with lower doses when combining multiple peptides',
        'Monitor closely for first 2 weeks of combination therapy',
        'Consider cycling protocols to prevent desensitization'
      ]
    };

    setResults(compatibility);
  };

  React.useEffect(() => {
    if (selectedPeptides.length >= 2) {
      analyzeCompatibility();
    } else {
      setResults(null);
    }
  }, [selectedPeptides]);

  return (
    <>
      <MetaTags 
        title="Peptide Compatibility Checker | Safe Peptide Stacking Tool"
        description="Analyze peptide combinations for safety, interactions, and synergistic effects. Professional compatibility checker for research peptide protocols."
        keywords="peptide compatibility, peptide stacking, drug interactions, peptide combinations, safety checker"
        canonical="https://www.professorpeptides.org/peptides/compatibility"
        type="article"
      />

      <Header />

      <div className="min-h-screen bg-gray-50 py-20">
        <div className="max-w-6xl mx-auto px-4">
          {/* Header */}
          <div className="text-center mb-12">
            <div className="flex justify-center items-center mb-4">
              <div className="w-16 h-16 bg-emerald-100 rounded-full flex items-center justify-center">
                <Microscope className="h-8 w-8 text-emerald-600" />
              </div>
            </div>
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              Peptide Compatibility Checker
            </h1>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto">
              Analyze peptide combinations for potential interactions, synergistic effects, 
              and safety considerations to optimize your research protocols.
            </p>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Peptide Selection */}
            <Card className="shadow-xl">
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Plus className="h-5 w-5 mr-2 text-emerald-600" />
                  Select Peptides to Analyze
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">
                <div>
                  <Input
                    type="text"
                    placeholder="Search peptides..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="mb-4"
                  />

                  <div className="space-y-2 max-h-64 overflow-y-auto">
                    {filteredPeptides.map((peptide) => (
                      <div key={peptide.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg hover:bg-gray-100">
                        <div>
                          <div className="font-medium">{peptide.name}</div>
                          <div className="text-sm text-gray-600">{peptide.category}</div>
                        </div>
                        <Button size="sm" onClick={() => addPeptide(peptide)}>
                          Add
                        </Button>
                      </div>
                    ))}
                  </div>
                </div>

                <div>
                  <h3 className="font-semibold mb-3">Selected Peptides ({selectedPeptides.length}/5)</h3>
                  <div className="space-y-2">
                    {selectedPeptides.map((peptide) => (
                      <div key={peptide.id} className="flex items-center justify-between p-3 bg-blue-50 rounded-lg">
                        <div>
                          <div className="font-medium">{peptide.name}</div>
                          <Badge variant="outline">{peptide.category}</Badge>
                        </div>
                        <Button size="sm" variant="outline" onClick={() => removePeptide(peptide.id)}>
                          <X className="h-4 w-4" />
                        </Button>
                      </div>
                    ))}
                  </div>

                  {selectedPeptides.length === 0 && (
                    <p className="text-gray-500 text-center py-8">
                      Select at least 2 peptides to analyze compatibility
                    </p>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Results */}
            <Card className="shadow-xl">
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Shield className="h-5 w-5 mr-2 text-blue-600" />
                  Compatibility Analysis
                </CardTitle>
              </CardHeader>
              <CardContent>
                {results ? (
                  <div className="space-y-6">
                    <div className="text-center">
                      <div className={`inline-flex items-center px-4 py-2 rounded-full text-lg font-semibold ${
                        results.overall === 'Good' ? 'bg-green-100 text-green-800' :
                        results.overall === 'Caution' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-red-100 text-red-800'
                      }`}>
                        <CheckCircle className="h-5 w-5 mr-2" />
                        Overall Compatibility: {results.overall}
                      </div>
                    </div>

                    {results.warnings.length > 0 && (
                      <Alert>
                        <AlertTriangle className="h-4 w-4" />
                        <AlertDescription>
                          <h4 className="font-semibold mb-2">Important Warnings:</h4>
                          <ul className="space-y-1">
                            {results.warnings.map((warning, index) => (
                              <li key={index} className="text-sm">â€¢ {warning}</li>
                            ))}
                          </ul>
                        </AlertDescription>
                      </Alert>
                    )}

                    {results.synergies.length > 0 && (
                      <div className="bg-green-50 border border-green-200 rounded-lg p-4">
                        <h4 className="font-semibold text-green-800 mb-2">Potential Synergies:</h4>
                        <ul className="space-y-1">
                          {results.synergies.map((synergy, index) => (
                            <li key={index} className="text-sm text-green-700">âœ“ {synergy}</li>
                          ))}
                        </ul>
                      </div>
                    )}

                    {results.recommendations.length > 0 && (
                      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                        <h4 className="font-semibold text-blue-800 mb-2">Recommendations:</h4>
                        <ul className="space-y-1">
                          {results.recommendations.map((rec, index) => (
                            <li key={index} className="text-sm text-blue-700">â†’ {rec}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <Microscope className="h-16 w-16 text-gray-300 mx-auto mb-4" />
                    <p className="text-gray-500">
                      Select at least 2 peptides to see compatibility analysis
                    </p>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Educational Section */}
          <div className="mt-12 bg-white rounded-lg shadow-lg p-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Understanding Peptide Compatibility</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div>
                <h3 className="font-semibold text-gray-900 mb-3">What We Analyze:</h3>
                <ul className="text-gray-600 space-y-2">
                  <li><strong>Receptor Interactions:</strong> Overlapping or competing pathways</li>
                  <li><strong>Timing Considerations:</strong> Optimal spacing between injections</li>
                  <li><strong>Synergistic Effects:</strong> Enhanced benefits from combinations</li>
                  <li><strong>Safety Warnings:</strong> Known contraindications or risks</li>
                </ul>
              </div>

              <div>
                <h3 className="font-semibold text-gray-900 mb-3">Best Practices:</h3>
                <ul className="text-gray-600 space-y-2">
                  <li>Start with individual peptides before combining</li>
                  <li>Space injections appropriately (15-30 minutes)</li>
                  <li>Monitor response carefully with combinations</li>
                  <li>Consider cycling to prevent desensitization</li>
                </ul>
              </div>
            </div>
          </div>

          {/* Disclaimer */}
          <div className="mt-8 bg-red-50 border border-red-200 rounded-lg p-6">
            <div className="flex items-start">
              <AlertTriangle className="h-6 w-6 text-red-600 mt-0.5 mr-3 flex-shrink-0" />
              <div>
                <h3 className="font-semibold text-red-900 mb-2">âš ï¸ Important Disclaimer:</h3>
                <p className="text-red-800 text-sm">
                  This compatibility checker is for research purposes only and should not replace 
                  professional medical advice. Always consult qualified healthcare professionals 
                  before combining peptides or starting any research protocol.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </>
  );
};

export default PeptideCompatibilityPage;

