import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MetaTags from '../components/seo/MetaTags';
import AuthorCredentials from '../components/trust/AuthorCredentials';
import Breadcrumbs from '../components/seo/Breadcrumbs';
import { 
  ArrowLeft, 
  Star, 
  Shield, 
  AlertTriangle, 
  CheckCircle, 
  Clock,
  Users,
  BookOpen,
  Calculator,
  ExternalLink,
  Heart,
  Share2,
  TrendingUp,
  Activity
} from 'lucide-react';

// Simple UI components
const Card = ({ children, className = "" }) => (
  <div className={`bg-white rounded-lg shadow-md border ${className}`}>{children}</div>
);
const CardHeader = ({ children, className = "" }) => (
  <div className={`p-6 ${className}`}>{children}</div>
);
const CardContent = ({ children, className = "" }) => (
  <div className={`p-6 pt-0 ${className}`}>{children}</div>
);
const CardTitle = ({ children, className = "" }) => (
  <h3 className={`text-lg font-semibold ${className}`}>{children}</h3>
);
const Button = ({ children, className = "", size = "default", variant = "default", onClick, ...props }) => {
  let sizeClasses = "px-4 py-2 text-sm";
  if (size === "lg") sizeClasses = "px-6 py-3 text-base";
  if (size === "sm") sizeClasses = "px-3 py-1.5 text-sm";
  let variantClasses = "bg-blue-600 text-white hover:bg-blue-700";
  if (variant === "outline") variantClasses = "border border-gray-300 bg-white text-gray-700 hover:bg-gray-50";
  return (
    <button className={`inline-flex items-center justify-center rounded-md font-medium transition-colors ${sizeClasses} ${variantClasses} ${className}`} onClick={onClick} {...props}>
      {children}
    </button>
  );
};
const Badge = ({ children, className = "", variant = "default" }) => {
  let variantClasses = "bg-gray-100 text-gray-800";
  if (variant === "secondary") variantClasses = "bg-gray-100 text-gray-800";
  return (
    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${variantClasses} ${className}`}>
      {children}
    </span>
  );
};

const PeptideDetailPage = () => {
  const { peptideId } = useParams();
  const [peptideData, setPeptideData] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [isFavorited, setIsFavorited] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPeptideData = async () => {
      try {
        setLoading(true);
        // Try to fetch from API first
        const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/peptides/${peptideId}`);
        if (response.ok) {
          const data = await response.json();
          setPeptideData(transformApiData(data));
        } else {
          // Fallback to hardcoded data if API fails
          const fallbackData = hardcodedPeptideDatabase[peptideId];
          if (fallbackData) {
            setPeptideData(fallbackData);
          } else {
            setError('Peptide not found');
          }
        }
      } catch (err) {
        console.error('Error fetching peptide data:', err);
        // Fallback to hardcoded data on error
        const fallbackData = hardcodedPeptideDatabase[peptideId];
        if (fallbackData) {
          setPeptideData(fallbackData);
        } else {
          setError('Error loading peptide data');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchPeptideData();
  }, [peptideId]);

  // Transform API data to match component format
  const transformApiData = (apiData) => {
    return {
      id: apiData.slug || peptideId,
      name: apiData.name,
      category: apiData.category || 'Research Peptides',
      description: apiData.description,
      status: apiData.fdaApproved ? 'FDA Approved' : 'Research Only',
      approvalDate: apiData.fdaApproved ? 'FDA Approved' : 'Not FDA approved for human use',
      mechanism: apiData.mechanism || 'Consult research literature for mechanism details',
      halfLife: '~1-7 days', // Default, could be in API data
      bioavailability: 'Variable', // Default, could be in API data
      dosing: {
        research: apiData.dosage || 'Consult healthcare provider',
        notes: apiData.safetyNote || 'For research purposes only'
      },
      benefits: apiData.benefits || ['Refer to research literature for potential benefits'],
      sideEffects: apiData.sideEffects || ['Varies by individual', 'Consult research literature', 'Limited human data may be available'],
      chemicalMakeup: apiData.chemicalMakeup || 'Consult research literature for chemical structure',
      contraindications: ['Pregnancy and breastfeeding', 'Active cancer (for growth-promoting peptides)', 'Consult healthcare provider'],
      interactions: ['Consult healthcare provider', 'May interact with medications'],
      clinicalData: {
        studies: 'Multiple studies available',
        participants: 'Research ongoing',
        efficacy: Array.isArray(apiData.researchFindings) ? apiData.researchFindings.join(', ') : (apiData.researchFindings || 'Under investigation'),
        safety: apiData.safetyNote || 'Research compound - consult literature for safety data'
      },
