import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MetaTags from '../components/seo/MetaTags';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Badge } from '../components/ui/badge';
import { 
  Search, 
  Filter, 
  CheckCircle, 
  ArrowLeft,
  BookOpen,
  Target,
  Eye,
  Settings
} from 'lucide-react';

const PeptidesBrowsePage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedStatus, setSelectedStatus] = useState('all');
  const [selectedWADA, setSelectedWADA] = useState('all');
  const [comparisonMode, setComparisonMode] = useState(false);
  const [selectedForComparison, setSelectedForComparison] = useState([]);
  const [allPeptides, setAllPeptides] = useState([]);
  const [loading, setLoading] = useState(false);

  const categories = [
    'Weight Management', 'Tissue Repair', 'Muscle Growth', 'Cognitive',
    'Longevity', 'Skin Health', 'Performance', 'Healing and Recovery',
    'Sleep and Cognition', 'Sexual Wellness', 'Anti-Aging', 'Immune Support', 'Pain Relief'
  ];

  const statusOptions = [
    { id: 'all', name: 'All Status' },
    { id: 'fda', name: 'FDA Approved' },
    { id: 'research', name: 'Research Only' }
  ];

  const wadaOptions = [
    { id: 'all', name: 'All WADA Status' },
    { id: 'safe', name: 'WADA Safe' },
    { id: 'banned', name: 'WADA Banned' }
  ];

  useEffect(() => {
    // Load peptides from API
    const fetchPeptides = async () => {
      setLoading(true);
      try {
        const response = await fetch(`${process.env.REACT_APP_BACKEND_URL}/api/peptide-categories`);
        if (response.ok) {
          const categoriesData = await response.json();
          const allPeptidesFlat = [];

          Object.entries(categoriesData).forEach(([categoryKey, categoryData]) => {
            if (categoryData.peptides) {
              categoryData.peptides.forEach(peptide => {
                allPeptidesFlat.push({
                  id: peptide.slug,
                  name: peptide.name,
                  description: peptide.description,
                  category: categoryData.title,
                  status: peptide.fdaApproved ? 'FDA Approved' : 'Research Only',
                  wadaBanned: peptide.wadaBanned || false,
                  benefits: peptide.benefits || [],
                  mechanism: peptide.mechanism || 'Consult research literature'
                });
              });
            }
          });

          setAllPeptides(allPeptidesFlat);
        }
      } catch (err) {
        console.error('Error loading peptides:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchPeptides();
  }, []);

  const filteredPeptides = allPeptides.filter(peptide => {
    const matchesSearch = !searchTerm || 
      peptide.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      peptide.description.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesCategory = selectedCategories.length === 0 || 
      selectedCategories.some(cat => peptide.category.includes(cat));

    const matchesStatus = selectedStatus === 'all' || 
      (selectedStatus === 'fda' && peptide.status === 'FDA Approved') ||
      (selectedStatus === 'research' && peptide.status === 'Research Only');

    const matchesWADA = selectedWADA === 'all' ||
      (selectedWADA === 'safe' && !peptide.wadaBanned) ||
      (selectedWADA === 'banned' && peptide.wadaBanned);

    return matchesSearch && matchesCategory && matchesStatus && matchesWADA;
  });

  const handleCategoryToggle = (category) => {
    setSelectedCategories(prev => 
      prev.includes(category) 
        ? prev.filter(c => c !== category)
        : [...prev, category]
    );
  };

  const toggleComparison = (peptide) => {
    if (selectedForComparison.find(p => p.id === peptide.id)) {
      setSelectedForComparison(prev => prev.filter(p => p.id !== peptide.id));
    } else if (selectedForComparison.length < 3) {
      setSelectedForComparison(prev => [...prev, peptide]);
    }
  };

  const structuredData = {
    "@context": "https://schema.org",
    "@graph": [
      {
        "@type": "WebApplication",
        "name": "Peptide Browser",
        "applicationCategory": "ScienceApplication",
        "operatingSystem": "Web",
        "url": "https://www.professorpeptides.org/peptides/browse",
        "isAccessibleForFree": true,
        "offers": { "@type": "Offer", "price": "0", "priceCurrency": "USD" },
        "description": "Advanced search, filtering, and comparison tool for research peptides."
      },
      {
        "@type": "CollectionPage",
        "name": "Peptide Browser",
        "url": "https://www.professorpeptides.org/peptides/browse",
        "about": "Interactive advanced browser for research peptides."
      },
      {
        "@type": "BreadcrumbList",
        "itemListElement": [
          {
            "@type": "ListItem",
            "position": 1,
            "name": "Home",
            "item": "https://www.professorpeptides.org/"
          },
          {
            "@type": "ListItem",
            "position": 2,
            "name": "Peptides",
            "item": "https://www.professorpeptides.org/peptides"
          },
          {
            "@type": "ListItem",
            "position": 3,
            "name": "Advanced Search",
            "item": "https://www.professorpeptides.org/peptides/browse"
          }
        ]
      }
    ]
  };

  return (
    <>
      <MetaTags 
        title="Advanced Peptide Search | Filter and Compare Peptides"
        description="Power user browser with advanced filters and comparison. Educational use only."
        keywords="advanced peptide search, peptide browser, filter peptides, compare peptides, peptide comparison tool"
        canonical="https://www.professorpeptides.org/peptides/browse"
        type="website"
      />

      <script type="application/ld+json">
        {JSON.stringify(structuredData)}
      </script>

      <Header />

      <main id="main-content" className="min-h-screen bg-gray-50">
        {/* Header */}
        <section className="bg-gradient-to-r from-indigo-600 to-blue-700 text-white py-16">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="text-center">
              <Link 
                to="/peptides" 
                className="inline-flex items-center text-white hover:text-gray-200 mb-6"
              >
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back to Simple Search
              </Link>

              <h1 className="text-4xl md:text-5xl font-bold mb-6">
                Peptide Browser Advanced
              </h1>
              <p className="text-xl text-indigo-100 max-w-3xl mx-auto mb-8 leading-relaxed">
                Extensive search, filters, and comparison for power users.
              </p>
            </div>
          </div>
        </section>

        {/* Advanced Filters */}
        <section className="py-8 bg-white shadow-sm" role="region" aria-label="Advanced filters">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            {/* Search Bar */}
            <div className="mb-6">
              <div className="relative max-w-lg mx-auto">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                <Input
                  type="text"
                  placeholder="Search peptides advanced"
                  className="pl-12 pr-4 py-3 text-lg"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  aria-label="Search peptides advanced"
                />
              </div>
            </div>

            {/* Filter Controls */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-6">
              {/* Category Filter */}
              <div>
                <h3 className="font-semibold text-gray-900 mb-3">Categories</h3>
                <div className="space-y-2 max-h-48 overflow-y-auto">
                  {categories.map((category) => (
                    <label key={category} className="flex items-center">
                      <input
                        type="checkbox"
                        checked={selectedCategories.includes(category)}
                        onChange={() => handleCategoryToggle(category)}
                        className="rounded border-gray-300 text-blue-600 mr-2"
                      />
                      <span className="text-sm text-gray-700">{category}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* Status Filter */}
              <div>
                <h3 className="font-semibold text-gray-900 mb-3">Regulatory Status</h3>
                <div className="space-y-2">
                  {statusOptions.map((option) => (
                    <label key={option.id} className="flex items-center">
                      <input
                        type="radio"
                        name="status"
                        value={option.id}
                        checked={selectedStatus === option.id}
                        onChange={(e) => setSelectedStatus(e.target.value)}
                        className="text-blue-600 mr-2"
                      />
                      <span className="text-sm text-gray-700">{option.name}</span>
                    </label>
                  ))}
                </div>
              </div>

              {/* WADA Filter */}
              <div>
                <h3 className="font-semibold text-gray-900 mb-3">WADA Status</h3>
                <div className="space-y-2">
                  {wadaOptions.map((option) => (
                    <label key={option.id} className="flex items-center">
                      <input
                        type="radio"
                        name="wada"
                        value={option.id}
                        checked={selectedWADA === option.id}
                        onChange={(e) => setSelectedWADA(e.target.value)}
                        className="text-blue-600 mr-2"
                      />
                      <span className="text-sm text-gray-700">{option.name}</span>
                    </label>
                  ))}
                </div>
              </div>
            </div>

            {/* Comparison Toggle */}
            <div className="flex items-center justify-between">
              <div className="text-sm text-gray-600">
                {loading ? 'Loading...' : `${filteredPeptides.length} results`}
              </div>
              <button
                onClick={() => setComparisonMode(!comparisonMode)}
                className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                  comparisonMode 
                    ? 'bg-blue-600 text-white' 
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
                aria-label="Toggle comparison view"
              >
                {comparisonMode ? 'Exit Comparison' : 'Compare Mode'} {selectedForComparison.length > 0 && `(${selectedForComparison.length})`}
              </button>
            </div>
          </div>
        </section>

        {/* Results Grid or Empty State */}
        <section className="py-16">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            {filteredPeptides.length === 0 && !loading ? (
              <div className="text-center py-16">
                <div className="text-6xl mb-4">ðŸ”</div>
                <h3 className="text-2xl font-bold text-gray-900 mb-2">Start by selecting a category or typing a peptide name</h3>
                <p className="text-gray-600 mb-6">Use the filters above to explore our research peptide database</p>
                <Link to="/peptides">
                  <Button>
                    <ArrowLeft className="h-4 w-4 mr-2" />
                    Back to Simple Search
                  </Button>
                </Link>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {filteredPeptides.map((peptide) => (
                  <Card key={peptide.id} className="hover:shadow-xl transition-all duration-300 border-0 shadow-lg">
                    <CardHeader className="pb-4">
                      <div className="flex items-start justify-between mb-4">
                        <div className="flex-1">
                          <CardTitle className="text-xl font-bold text-gray-900 mb-2">
                            {peptide.name}
                          </CardTitle>

                          <div className="flex items-center gap-2 mb-3">
                            <Badge className={peptide.status === 'FDA Approved' ? 'bg-green-100 text-green-800' : 'bg-orange-100 text-orange-800'}>
                              {peptide.status}
                            </Badge>
                            {peptide.wadaBanned && (
                              <Badge className="bg-red-100 text-red-800">
                                WADA Banned
                              </Badge>
                            )}
                          </div>
                        </div>

                        {comparisonMode && (
                          <button
                            onClick={() => toggleComparison(peptide)}
                            className={`p-2 rounded-lg ${
                              selectedForComparison.find(p => p.id === peptide.id)
                                ? 'bg-blue-600 text-white'
                                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                            }`}
                          >
                            <CheckCircle className="h-4 w-4" />
                          </button>
                        )}
                      </div>
                    </CardHeader>

                    <CardContent className="pt-0">
                      <p className="text-gray-600 text-sm mb-4 leading-relaxed line-clamp-3">
                        {peptide.description}
                      </p>

                      <div className="mb-4">
                        <div className="text-xs text-gray-500 mb-1">Category:</div>
                        <div className="text-sm font-medium text-gray-900">{peptide.category}</div>
                      </div>

                      <Link 
                        to={`/peptide/${peptide.id}`}
                        aria-label={`View details for ${peptide.name} research profile`}
                      >
                        <Button className="w-full" size="sm">
                          <Eye className="h-4 w-4 mr-2" />
                          View Details
                        </Button>
                      </Link>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        </section>

        {/* Comparison Panel */}
        {comparisonMode && selectedForComparison.length > 0 && (
          <section className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 p-4 shadow-xl">
            <div className="max-w-7xl mx-auto">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-4">
                  <span className="font-semibold">Compare Selected ({selectedForComparison.length}/3):</span>
                  <div className="flex gap-2">
                    {selectedForComparison.map((peptide) => (
                      <Badge key={peptide.id} variant="outline" className="text-sm">
                        {peptide.name}
                        <button
                          onClick={() => toggleComparison(peptide)}
                          className="ml-2 text-red-500 hover:text-red-700"
                        >
                          Ã—
                        </button>
                      </Badge>
                    ))}
                  </div>
                </div>
                <Button 
                  disabled={selectedForComparison.length < 2}
                  className="bg-blue-600 hover:bg-blue-700"
                >
                  Compare {selectedForComparison.length} Peptides
                </Button>
              </div>
            </div>
          </section>
        )}

        {/* How To Use */}
        <section className="py-16 bg-white">
          <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
            <h2 className="text-3xl font-bold text-gray-900 text-center mb-8">How to Use</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="text-center">
                <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mx-auto mb-3">
                  <Filter className="h-6 w-6 text-blue-600" />
                </div>
                <p className="text-gray-600 text-sm">Filter by category, regulatory status, or WADA</p>
              </div>
              <div className="text-center">
                <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mx-auto mb-3">
                  <Target className="h-6 w-6 text-blue-600" />
                </div>
                <p className="text-gray-600 text-sm">Compare up to 3 peptides side by side</p>
              </div>
              <div className="text-center">
                <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center mx-auto mb-3">
                  <BookOpen className="h-6 w-6 text-green-600" />
                </div>
                <p className="text-gray-600 text-sm">Open any profile for citations and mechanisms</p>
              </div>
            </div>
          </div>
        </section>

        {/* Educational Notice */}
        <section className="py-8 bg-gray-50">
          <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
            <p className="text-gray-600 text-sm">
              Educational content for research use only. Not medical advice. Not for human use.
            </p>
          </div>
        </section>
      </main>

      <Footer />
    </>
  );
};

export default PeptidesBrowsePage;

