import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MetaTags from '../components/seo/MetaTags';
import PeptideShowcase from '../components/PeptideShowcase';
import { Search, Settings, ArrowRight } from 'lucide-react';

const PeptidesPage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  const categories = [
    { id: 'all', name: 'All Categories', count: 47 },
    { id: 'weight-management', name: 'Weight Management', count: 6 },
    { id: 'tissue-repair', name: 'Tissue Repair', count: 3 },
    { id: 'muscle-growth', name: 'Muscle Growth', count: 8 },
    { id: 'cognitive', name: 'Cognitive', count: 2 },
    { id: 'longevity', name: 'Longevity', count: 3 },
    { id: 'skin-health', name: 'Skin Health', count: 4 }
  ];

  const structuredData = {
    "@context": "https://schema.org",
    "@graph": [
      {
        "@type": "CollectionPage",
        "@id": "https://www.professorpeptides.org/peptides#webpage",
        "url": "https://www.professorpeptides.org/peptides",
        "name": "Research Peptides Database",
        "description": "Browse 47 plus research peptides with evidence summaries, categories, and citations. Educational use only.",
        "isPartOf": {
          "@id": "https://www.professorpeptides.org/#website"
        },
        "datePublished": "2025-08-25",
        "dateModified": "2025-08-25",
        "inLanguage": "en",
        "about": "Evidence mapped research peptides with citations and mechanisms."
      },
      {
        "@type": "ItemList",
        "name": "Research Peptides Database",
        "itemListOrder": "http://schema.org/ItemListOrderAscending",
        "numberOfItems": 47,
        "itemListElement": Array.from({length: 47}, (_, i) => ({
          "@type": "ListItem",
          "position": i + 1,
          "item": {
            "@type": "Dataset",
            "name": `Peptide ${i + 1} research profile`,
            "url": `https://www.professorpeptides.org/peptide/peptide-${i + 1}`
          }
        }))
      },
      {
        "@type": "BreadcrumbList",
        "itemListElement": [
          {
            "@type": "ListItem",
            "position": 1,
            "name": "Home",
            "item": "https://www.professorpeptides.org/"
          },
          {
            "@type": "ListItem",
            "position": 2,
            "name": "Peptides",
            "item": "https://www.professorpeptides.org/peptides"
          }
        ]
      },
      {
        "@type": "FAQPage",
        "mainEntity": [
          {
            "@type": "Question",
            "name": "What is a research peptide",
            "acceptedAnswer": {
              "@type": "Answer",
              "text": "A peptide studied in laboratory or clinical contexts for mechanism and safety. Content here is educational and not medical advice."
            }
          },
          {
            "@type": "Question",
            "name": "Do you sell peptides",
            "acceptedAnswer": {
              "@type": "Answer",
              "text": "No. This is an educational database with citations and mechanisms."
            }
          },
          {
            "@type": "Question",
            "name": "Where do citations come from",
            "acceptedAnswer": {
              "@type": "Answer",
              "text": "Peer reviewed literature listed on each peptide page."
            }
          }
        ]
      }
    ]
  };

  return (
    <>
      <MetaTags 
        title="Research Peptides Database | Explore 47 plus Evidence Mapped Profiles"
        description="Simple peptide search with categories, mechanisms, and citations. Educational use only."
        keywords="research peptides, peptide research database, peptide profiles, peptide mechanisms, peptide citations"
        canonical="https://www.professorpeptides.org/peptides"
        type="website"
      />

      <script type="application/ld+json">
        {JSON.stringify(structuredData)}
      </script>

      <Header />

      <main id="main-content">
        {/* Hero Section */}
        <section className="bg-gradient-to-r from-blue-600 to-blue-700 text-white py-16">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="text-center">
              <h1 className="text-4xl md:text-5xl font-bold mb-6">
                Research Peptides Database
              </h1>
              <p className="text-xl text-blue-100 max-w-3xl mx-auto mb-8 leading-relaxed">
                Browse 47 plus research peptides with evidence summaries, categories, and citations. Educational use only.
              </p>

              {/* Simple Search */}
              <div className="relative max-w-md mx-auto mb-8">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                <input
                  type="text"
                  placeholder="Search peptides"
                  className="w-full pl-12 pr-4 py-3 text-lg rounded-lg text-gray-900"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  aria-label="Search peptides"
                />
              </div>

              <div className="flex justify-center">
                <Link 
                  to="/peptides/browse"
                  className="inline-flex items-center px-6 py-3 bg-white/20 backdrop-blur-sm rounded-lg hover:bg-white/30 transition-colors text-white font-medium"
                >
                  <Settings className="h-4 w-4 mr-2" />
                  Open extensive peptide search
                  <ArrowRight className="h-4 w-4 ml-2" />
                </Link>
              </div>
            </div>
          </div>
        </section>

            <div className="bg-white py-8 sticky top-0 z-10 shadow-sm">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex flex-wrap gap-3 justify-center">
                  {categories.map((category) => (
                    <button
                      key={category.id}
                      onClick={() => setSelectedCategory(category.id)}
                      className={`px-4 py-2 rounded-full border-2 transition-all ${
                        selectedCategory === category.id
                          ? 'bg-blue-600 text-white border-blue-600'
                          : 'bg-white text-gray-700 border-gray-300 hover:border-blue-500'
                      }`}
                      role="button"
                      aria-label={`Filter by ${category.name}`}
                    >
                      {category.name} ({category.count})
                    </button>
                  ))}
                </div>
              </div>
            </div>

        {/* Unified Grid */}
        <PeptideShowcase 
          searchTerm={searchTerm} 
          selectedCategory={selectedCategory} 
          showSearch={false}
          onSearchChange={setSearchTerm}
          onCategoryChange={setSelectedCategory}
        />

        {/* Educational Notice */}
        <section className="py-8 bg-gray-50">
          <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
            <p className="text-gray-600 text-sm">
              Educational content for research use only. Not medical advice. Not for human use.
            </p>
          </div>
        </section>
      </main>

      <Footer />
    </>
  );
};

export default PeptidesPage;

frontend/src/pages/QuizResultsPage.jsx.content:
import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate, Link } from 'react-router-dom';
import Header from '../components/Header';
import { 
  Share2, 
  Download, 
  RefreshCw, 
  Star, 
  CheckCircle, 
  ArrowRight,
  BookOpen,
  Calculator,
  Shield,
  Target,
  TrendingUp,
  Award
} from 'lucide-react';

const Button = ({ children, className = "", size = "default", variant = "default", onClick, ...props }) => {
  let sizeClasses = "px-4 py-2 text-sm";
  if (size === "lg") sizeClasses = "px-6 py-3 text-base";
  if (size === "sm") sizeClasses = "px-3 py-1.5 text-sm";

  let variantClasses = "bg-blue-700 text-white hover:bg-blue-800";
  if (variant === "outline") variantClasses = "border border-gray-400 bg-white text-gray-900 hover:bg-gray-50";
  if (variant === "secondary") variantClasses = "bg-gray-200 text-gray-900 hover:bg-gray-300";

  return (
    <button 
      className={`inline-flex items-center justify-center rounded-md font-medium transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-600 ${sizeClasses} ${variantClasses} ${className}`}
      onClick={onClick}
      {...props}
    >
      {children}
    </button>
  );
};

const QuizResultsPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [copied, setCopied] = useState(false);

  // Get quiz answers from URL params or redirect if missing
  const searchParams = new URLSearchParams(location.search);
  const interest = searchParams.get('interest');
  const experience = searchParams.get('experience');
