  useEffect(() => {
    const loadResearchData = () => {
      try {
        setLoading(true);
        const data = researchData[category] || [];
        setStudies(data);
      } catch (err) {
        console.error('Error loading research data:', err);
      } finally {
        setLoading(false);
      }
    };

    loadResearchData();
  }, [category]);

  const filteredStudies = studies.filter(study => {
    const matchesSearch = !searchTerm || 
      study.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (study.description && study.description.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesFilter = filterBy === 'all' || 
      (study.status && study.status.toLowerCase() === filterBy) ||
      (study.phase && study.phase.toLowerCase().includes(filterBy)) ||
      (study.studyType && study.studyType.toLowerCase().includes(filterBy));

    return matchesSearch && matchesFilter;
  });

  const getColorClasses = (color) => {
    const colors = {
      blue: 'from-blue-500 to-blue-600',
      red: 'from-red-500 to-red-600',
      purple: 'from-blue-500 to-blue-600',
      green: 'from-green-500 to-green-600',
      orange: 'from-orange-500 to-orange-600'
    };
    return colors[color] || colors.blue;
  };

  const getStatusColor = (status) => {
    if (status === 'Completed') return 'bg-green-100 text-green-800';
    if (status === 'Active') return 'bg-blue-100 text-blue-800';
    return 'bg-gray-100 text-gray-800';
  };

  const IconComponent = config.icon;

  if (loading) {
    return (
      <>
        <Header />
        <div className="min-h-screen bg-gray-50 flex items-center justify-center">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600">Loading research data...</p>
          </div>
        </div>
        <Footer />
      </>
    );
  }

  return (
    <>
      <MetaTags 
        title={`${config.title} | Peptide Professor Research`}
        description={config.description}
        keywords={`peptide research, ${category}, clinical studies, safety data, publications, medical research, scientific literature`}
        canonical={`https://www.professorpeptides.org/research/${category}`}
        type="website"
      />

      {/* Enhanced Structured Data for Research Category Pages */}
      <script type="application/ld+json">
        {JSON.stringify({
          "@context": "https://schema.org",
          "@type": "CollectionPage",
          "name": config.title,
          "description": config.description,
          "url": `https://www.professorpeptides.org/research/${category}`,
          "mainEntity": {
            "@type": "ItemList",
            "name": config.title,
            "numberOfItems": studies.length,
            "itemListElement": studies.slice(0, 10).map((study, index) => ({
              "@type": "ListItem",
              "position": index + 1,
              "item": {
                "@type": category === 'publications' ? "ScholarlyArticle" : category === 'clinical-studies' ? "MedicalStudy" : "Article",
                "name": study.title,
                "description": study.description,
                "url": study.link || `https://www.professorpeptides.org/research/${category}#${study.id}`,
                ...(study.authors && { "author": study.authors }),
                ...(study.journal && { "publisher": study.journal }),
                ...(study.year && { "datePublished": study.year.toString() }),
                ...(study.participants && { "studySubject": `${study.participants} participants` }),
                ...(study.institution && { "sourceOrganization": study.institution })
              }
            }))
          },
          "breadcrumb": {
            "@type": "BreadcrumbList",
            "itemListElement": [
              {
                "@type": "ListItem",
                "position": 1,
                "name": "Home",
                "item": "https://www.professorpeptides.org"
              },
              {
                "@type": "ListItem", 
                "position": 2,
                "name": "Research",
                "item": "https://www.professorpeptides.org/research"
              },
              {
                "@type": "ListItem",
                "position": 3,
                "name": config.title,
                "item": `https://www.professorpeptides.org/research/${category}`
              }
            ]
          },
          "publisher": {
            "@type": "Organization",
            "name": "Professor Peptides",
            "url": "https://www.professorpeptides.org"
          }
        })}
      </script>

      <Header />

      <main id="main-content" className="min-h-screen bg-gray-50">
        {/* Hero Section */}
        <div className={`bg-gradient-to-r ${getColorClasses(config.color)} text-white py-16`}>
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex items-center mb-4">
              <Link 
                to="/research" 
                className="inline-flex items-center text-white hover:text-gray-200 mb-6"
              >
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back to Research Hub
              </Link>
            </div>

            <div className="text-center">
              <div className="flex justify-center items-center mb-6">
                <div className="w-20 h-20 bg-white bg-opacity-20 rounded-full flex items-center justify-center">
                  <IconComponent className="h-10 w-10 text-white" />
                </div>
              </div>

              <h1 className="text-4xl md:text-5xl font-bold mb-6">
                {config.title}
              </h1>
              <p className="text-xl text-white opacity-90 max-w-3xl mx-auto mb-8 leading-relaxed">
                {config.description}
              </p>

              {/* Stats */}
              <div className="grid grid-cols-2 md:grid-cols-4 gap-6 max-w-4xl mx-auto">
                <div className="text-center">
                  <div className="text-3xl font-bold">{config.totalStudies || config.totalUpdates || 'N/A'}</div>
                  <div className="text-white opacity-80">{category === 'updates' ? 'Total Updates' : 'Total Studies'}</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold">{config.activeTrials || config.safetyReports || config.knownInteractions || config.recentPapers || config.thisMonth || 'N/A'}</div>
                  <div className="text-white opacity-80">{category === 'clinical-studies' ? 'Active Trials' : category === 'safety-profiles' ? 'Safety Reports' : category === 'drug-interactions' ? 'Known Interactions' : category === 'publications' ? 'Recent Papers' : 'This Month'}</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold">{config.completedStudies || config.adverseEvents || config.contraindications || config.journals || config.breakingNews || 'N/A'}</div>
                  <div className="text-white opacity-80">{category === 'clinical-studies' ? 'Completed' : category === 'safety-profiles' ? 'Adverse Events' : category === 'drug-interactions' ? 'Contraindications' : category === 'publications' ? 'Journals' : 'Breaking News'}</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold">{config.institutions || config.sources || 'N/A'}</div>
                  <div className="text-white opacity-80">{category === 'updates' ? 'Sources' : 'Institutions'}</div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Search and Filter Section */}
        <div className="bg-white py-8 shadow-sm">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex flex-col lg:flex-row gap-4 items-center justify-between">
              {/* Search Bar */}
              <div className="relative flex-1 max-w-md">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                <Input
                  type="text"
                  placeholder="Search research..."
                  className="pl-10 pr-4 py-2"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>

              {/* Filter Options */}
              <div className="flex gap-2">
                {category === 'clinical-studies' && (
                  <>
                    <Button size="sm" variant={filterBy === 'all' ? 'default' : 'outline'} onClick={() => setFilterBy('all')}>All</Button>
                    <Button size="sm" variant={filterBy === 'active' ? 'default' : 'outline'} onClick={() => setFilterBy('active')}>Active</Button>
                    <Button size="sm" variant={filterBy === 'completed' ? 'default' : 'outline'} onClick={() => setFilterBy('completed')}>Completed</Button>
                    <Button size="sm" variant={filterBy === 'phase iii' ? 'default' : 'outline'} onClick={() => setFilterBy('phase iii')}>Phase III</Button>
                  </>
                )}
                {category === 'safety-profiles' && (
                  <>
                    <Button size="sm" variant={filterBy === 'all' ? 'default' : 'outline'} onClick={() => setFilterBy('all')}>All</Button>
                    <Button size="sm" variant={filterBy === 'safety' ? 'default' : 'outline'} onClick={() => setFilterBy('safety')}>Safety</Button>
                    <Button size="sm" variant={filterBy === 'review' ? 'default' : 'outline'} onClick={() => setFilterBy('review')}>Reviews</Button>
                  </>
                )}
              </div>
            </div>

            {/* Results Summary */}
            <div className="mt-4 text-sm text-gray-600">
              Showing {filteredStudies.length} of {studies.length} research items
            </div>
          </div>
        </div>

        {/* Content Grid */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
          {filteredStudies.length === 0 ? (
            <div className="text-center py-16">
              <IconComponent className="h-16 w-16 text-gray-300 mx-auto mb-4" />
              <h3 className="text-2xl font-bold text-gray-900 mb-2">No research found</h3>
              <p className="text-gray-600 mb-6">Try adjusting your search or filter criteria</p>
              <Button onClick={() => { setSearchTerm(''); setFilterBy('all'); }}>
                Clear Filters
              </Button>
            </div>
          ) : (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {filteredStudies.map((study) => (
                <Card key={study.id} className="hover:shadow-xl transition-all duration-300 border-0 shadow-lg">
                  <CardHeader className="pb-4">
                    <div className="flex items-start justify-between mb-4">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          {study.status && (
                            <Badge className={getStatusColor(study.status)}>
                              {study.status}
                            </Badge>
                          )}
                          {study.phase && (
                            <Badge variant="outline">
                              {study.phase}
                            </Badge>
                          )}
                          {study.studyType && (
                            <Badge variant="outline">
                              {study.studyType}
                            </Badge>
                          )}
                          {study.type && (
                            <Badge variant="outline">
                              {study.type}
                            </Badge>
                          )}
                          {study.impact && (
                            <Badge className={study.impact === 'Very High' || study.impact === 'High' ? 'bg-red-100 text-red-800' : 'bg-yellow-100 text-yellow-800'}>
                              {study.impact} Impact
                            </Badge>
                          )}
                          {study.significance && (
                            <Badge className={study.significance === 'High' ? 'bg-red-100 text-red-800' : 'bg-yellow-100 text-yellow-800'}>
                              {study.significance} Impact
                            </Badge>
                          )}
                        </div>

                        <CardTitle className="text-lg font-bold text-gray-900 leading-tight mb-3">
                          {study.title}
                        </CardTitle>

                        <p className="text-gray-600 text-sm leading-relaxed mb-4">
                          {study.description}
                        </p>
                      </div>
                    </div>
                  </CardHeader>

                  <CardContent className="pt-0">
                    {/* Study Details */}
                    <div className="space-y-3 mb-6">
                      {study.date && (
                        <div className="flex items-center justify-between text-sm">
                          <span className="text-gray-500">Date:</span>
                          <span className="font-medium">{new Date(study.date).toLocaleDateString()}</span>
                        </div>
                      )}
                      {study.source && (
                        <div className="flex items-center justify-between text-sm">
                          <span className="text-gray-500">Source:</span>
                          <span className="font-medium">{study.source}</span>
                        </div>
                      )}
                      {study.readTime && (
                        <div className="flex items-center justify-between text-sm">
                          <span className="text-gray-500">Read Time:</span>
                          <span className="font-medium">{study.readTime}</span>
                        </div>
                      )}
                      {study.participants && (
                        <div className="flex items-center justify-between text-sm">
                          <span className="text-gray-500">Participants:</span>
                          <span className="font-medium">{study.participants.toLocaleString()}</span>
                        </div>
                      )}
                      {study.institution && (
                        <div className="flex items-center justify-between text-sm">
                          <span className="text-gray-500">Institution:</span>
                          <span className="font-medium">{study.institution}</span>
                        </div>
                      )}
                      {study.peptide && (
                        <div className="flex items-center justify-between text-sm">
                          <span className="text-gray-500">Peptide:</span>
                          <Link to={`/peptide/${study.peptide.toLowerCase()}`} className="font-medium text-blue-600 hover:text-blue-700">
                            {study.peptide}
                          </Link>
                        </div>
                      )}
                      {study.peptides && (
                        <div className="flex items-start justify-between text-sm">
                          <span className="text-gray-500">Peptides:</span>
                          <div className="text-right">
                            {study.peptides.map((peptide, idx) => (
                              <Link key={idx} to={`/peptide/${peptide.toLowerCase()}`} className="block font-medium text-blue-600 hover:text-blue-700">
                                {peptide}
                              </Link>
                            ))}
                          </div>
                        </div>
                      )}
                    </div>

                    {/* Results or Key Findings */}
                    {study.summary && (
                      <div className="bg-gray-50 border border-gray-200 rounded-lg p-4 mb-4">
                        <h4 className="font-semibold text-gray-900 mb-2">Summary:</h4>
                        <p className="text-gray-700 text-sm">{study.summary}</p>
                      </div>
                    )}

                    {study.results && (
                      <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-4">
                        <h4 className="font-semibold text-green-900 mb-2">Key Results:</h4>
                        <p className="text-green-800 text-sm">{study.results}</p>
                      </div>
                    )}

                    {study.keyFindings && (
                      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-4">
                        <h4 className="font-semibold text-blue-900 mb-2">Key Findings:</h4>
                        <p className="text-blue-800 text-sm">{study.keyFindings}</p>
                      </div>
                    )}

                    {/* Action Buttons */}
                    <div className="flex gap-2">
                      {study.link && (
                        <a 
                          href={study.link}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="flex-1"
                        >
                          <Button size="sm" className="w-full">
                            <ExternalLink className="h-3 w-3 mr-1" />
                            View Study
                          </Button>
                        </a>
                      )}
                      {study.doi && (
                        <a 
                          href={`https://doi.org/${study.doi}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="flex-1"
                        >
                          <Button size="sm" variant="outline" className="w-full">
                            <FileText className="h-3 w-3 mr-1" />
                            DOI
                          </Button>
                        </a>
                      )}
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}

          {/* Back to Research Hub */}
          <div className="text-center mt-12">
            <Link to="/research">
              <Button size="lg" variant="outline">
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back to Research Hub
              </Button>
            </Link>
          </div>
        </div>
      </main>

      <Footer />
    </>
  );
};

export default ResearchCategoryPage;

