import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MetaTags from '../components/seo/MetaTags';
import { 
  FileText, 
  Calculator, 
  Shield, 
  Book, 
  Download,
  ChevronRight,
  HelpCircle,
  Users,
  Star,
  AlertTriangle,
  Zap,
  Search
} from 'lucide-react';

const ResourcesPage = () => {
  const [activeCategory, setActiveCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  const resourceCategories = [
    {
      id: 'safety',
      title: "Safety Guidelines",
      description: "Comprehensive safety protocols and best practices",
      icon: Shield,
      color: "red",
      resources: [
        { name: "Peptide Safety Manual", type: "PDF", size: "2.4 MB", downloads: "15.2K" },
        { name: "Lab Safety Protocols", type: "PDF", size: "1.8 MB", downloads: "8.7K" },
        { name: "Emergency Procedures", type: "PDF", size: "950 KB", downloads: "12.1K" },
        { name: "Storage Guidelines", type: "PDF", size: "1.2 MB", downloads: "9.3K" }
      ]
    },
    {
      id: 'tools',
      title: "Research Tools",
      description: "Calculators and analysis tools for research",
      icon: Calculator,
      color: "blue",
      resources: [
        { name: "BMI & Metabolic Calculator", type: "Tool", link: "/calculators/bmi" },
        { name: "GLP-1 Dose Calculator", type: "Tool", link: "/calculators/glp1" },
        { name: "Reconstitution Calculator", type: "Tool", link: "/calculators/reconstitution" },
        { name: "Protocol Generator", type: "Tool", link: "/calculators/protocol" }
      ]
    },
    {
      id: 'guides',
      title: "Educational Guides",
      description: "In-depth guides covering fundamentals and advanced topics",
      icon: Book,
      color: "green",
      resources: [
        { name: "Peptides 101: Complete Beginner's Guide", type: "PDF", size: "5.2 MB", downloads: "28.4K" },
        { name: "Advanced Peptide Protocols", type: "PDF", size: "3.7 MB", downloads: "19.1K" },
        { name: "Regulatory Compliance Guide", type: "PDF", size: "2.1 MB", downloads: "14.8K" },
        { name: "Research Methodology Manual", type: "PDF", size: "4.3 MB", downloads: "11.2K" }
      ]
    },
    {
      id: 'reference',
      title: "Reference Materials",
      description: "Quick reference charts, tables, and clinical data", 
      icon: FileText,
      color: "purple",
      resources: [
        { name: "Peptide Properties Chart", type: "PDF", size: "1.5 MB", downloads: "22.7K" },
        { name: "Interaction Reference Table", type: "PDF", size: "890 KB", downloads: "16.3K" },
        { name: "Dosage Quick Reference", type: "PDF", size: "670 KB", downloads: "25.1K" },
        { name: "FDA Status Summary", type: "PDF", size: "1.1 MB", downloads: "18.9K" }
      ]
    }
  ];

  const quickTools = [
    { name: "BMI Calculator", link: "/calculators/bmi", icon: Calculator, color: "blue", description: "Health Assessment" },
    { name: "GLP-1 Calculator", link: "/calculators/glp1", icon: Calculator, color: "blue", description: "Dosage & Timeline" },
    { name: "Reconstitution", link: "/calculators/reconstitution", icon: Calculator, color: "green", description: "Dose Calculation" },
    { name: "Interactions", link: "/peptides/interactions", icon: AlertTriangle, color: "red", description: "Safety Checker" }
  ];

  const faqCategories = [
    {
      category: "Getting Started",
      questions: [
        {
          q: "What are research peptides and how are they different from drugs?",
          a: "Research peptides are synthetic compounds designed for scientific study and are not approved for human consumption..."
        },
        {
          q: "How do I choose the right peptide for my research?",
          a: "Selection should be based on your research objectives, available literature, and safety considerations..."
        },
        {
          q: "What safety precautions should I take when handling peptides?",
          a: "Always use appropriate PPE, work in a controlled environment, and follow proper storage protocols..."
        }
      ]
    },
    {
      category: "Safety & Protocols",
      questions: [
        {
          q: "How should peptides be stored to maintain stability?",
          a: "Most peptides should be stored at -20Â°C or below, protected from light and moisture..."
        },
        {
          q: "What are the signs of peptide degradation?",
          a: "Visual changes in color, texture, or clarity can indicate degradation, along with unusual odors..."
        },
        {
          q: "How do I properly dispose of peptide waste?",
          a: "Follow your institution's biohazard disposal protocols and local regulatory requirements..."
        }
      ]
    },
    {
      category: "Calculations & Dosing",
      questions: [
        {
          q: "How do I calculate peptide concentrations after reconstitution?",
          a: "Use the formula: Concentration = Peptide amount (mg) Ã· Reconstitution volume (mL)..."
        },
        {
          q: "What's the difference between mg and IU measurements?",
          a: "Milligrams measure mass while International Units measure biological activity..."
        },
        {
          q: "How do I account for peptide purity in calculations?",
          a: "Multiply the stated amount by the purity percentage to get the actual peptide content..."
        }
      ]
    }
  ];

  const expertReviews = [
    {
      name: "Dr. Sheraz Ahmad, M.D.",
      title: "Medical Advisor - Board-Certified Family Medicine",
      rating: 5,
      review: "Exceptional resource quality and scientific accuracy. The safety guidelines are comprehensive and evidence-based."
    },
    {
      name: "Verified Researcher",
      title: "Peptide Research Specialist", 
      rating: 5,
      review: "The calculation tools are incredibly useful for research planning. Saves hours of manual calculations."
    },
    {
      name: "Research Professional",
      title: "Clinical Research Coordinator",
      rating: 5,
      review: "Well-organized resources with excellent educational content. Perfect for both beginners and experts."
    }
  ];

  const getColorClasses = (color) => {
    const colors = {
      red: "from-red-500 to-red-600",
      blue: "from-blue-500 to-blue-600", 
      green: "from-green-500 to-green-600",
      purple: "from-purple-500 to-purple-600"
    };
    return colors[color] || colors.blue;
  };

  const getBadgeColors = (color) => {
    const colors = {
      red: "bg-red-100 text-red-700 border-red-200",
      blue: "bg-blue-100 text-blue-700 border-blue-200",
      green: "bg-green-100 text-green-700 border-green-200",
      purple: "bg-purple-100 text-purple-700 border-purple-200"
    };
    return colors[color] || colors.blue;
  };

  // Filter resources based on active category and search term
  const filteredCategories = resourceCategories.filter(category => {
    if (activeCategory !== 'all' && category.id !== activeCategory) return false;
    if (searchTerm) {
      return category.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
             category.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
             category.resources.some(resource => 
               resource.name.toLowerCase().includes(searchTerm.toLowerCase())
             );
    }
    return true;
  });

  return (
    <>
      <MetaTags 
        title="Peptide Research Resources | Free Guides, Tools & Safety Information"
        description="Access comprehensive peptide research resources including safety guidelines, calculation tools, educational guides, and expert reviews. Free downloads and interactive tools for researchers."
        keywords="peptide resources, safety guidelines, research tools, peptide calculators, educational guides, free downloads"
        canonical="https://www.professorpeptides.org/resources"
        type="website"
      />

      <Header />

      <main className="min-h-screen bg-gradient-to-b from-slate-50 to-white">
        {/* Compact Hero Section */}
        <section className="bg-gradient-to-r from-green-600 to-blue-700 text-white py-12">
          <div className="max-w-7xl mx-auto px-6">
            <div className="text-center">
              <h1 className="text-3xl md:text-4xl font-bold mb-4">
                Research Resources Hub
              </h1>
              <p className="text-lg text-green-100 max-w-2xl mx-auto mb-6">
                Access comprehensive guides, safety protocols, calculation tools, and expert resources for peptide research.
              </p>

              <div className="grid grid-cols-4 gap-4 max-w-2xl mx-auto">
                <div className="text-center">
                  <div className="text-2xl font-bold">50+</div>
                  <div className="text-xs text-green-100">Resources</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold">15</div>
                  <div className="text-xs text-green-100">Tools</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold">200K+</div>
                  <div className="text-xs text-green-100">Downloads</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold">24/7</div>
                  <div className="text-xs text-green-100">Access</div>
                </div>
              </div>
            </div>
          </div>
        </section>

        <div className="max-w-7xl mx-auto px-6 py-8">
          {/* Quick Tools Bar */}
          <div className="bg-white rounded-2xl shadow-sm border p-4 mb-6">
            <h3 className="font-semibold text-gray-900 mb-3 flex items-center">
              <Zap className="h-4 w-4 mr-2 text-blue-600" />
              Quick Access Tools
            </h3>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
              {quickTools.map((tool, index) => {
                const IconComponent = tool.icon;
                return (
                  <Link 
                    key={index}
                    to={tool.link}
                    className={`p-3 text-center rounded-lg transition-all group border-2 border-transparent hover:shadow-md bg-gradient-to-br ${getColorClasses(tool.color)} text-white`}
                  >
                    <IconComponent className="h-6 w-6 mx-auto mb-1 group-hover:scale-110 transition-transform" />
                    <div className="font-medium text-sm">{tool.name}</div>
                    <div className="text-xs opacity-90">{tool.description}</div>
                  </Link>
                );
              })}
            </div>
          </div>

          {/* Search and Filter Bar */}
          <div className="bg-white rounded-2xl shadow-sm border p-4 mb-6">
            <div className="flex flex-col md:flex-row gap-3">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-2.5 h-4 w-4 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search resources..."
                  className="w-full pl-9 pr-3 py-2 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <div className="flex gap-2">
                <button
                  onClick={() => setActiveCategory('all')}
                  className={`px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                    activeCategory === 'all' 
                      ? 'bg-blue-600 text-white' 
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  All
                </button>
                {resourceCategories.map((category) => (
                  <button
                    key={category.id}
                    onClick={() => setActiveCategory(category.id)}
                    className={`px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                      activeCategory === category.id 
                        ? 'bg-blue-600 text-white' 
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    {category.title.split(' ')[0]}
                  </button>
                ))}
              </div>
            </div>
          </div>

          {/* Compact Resource Grid */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
            {filteredCategories.map((category, index) => {
              const IconComponent = category.icon;
              return (
                <div key={index} className="bg-white rounded-2xl shadow-sm border hover:shadow-md transition-shadow">
                  <div className="p-4 border-b border-gray-100">
                    <div className="flex items-center">
                      <div className={`p-2 rounded-lg bg-gradient-to-br ${getColorClasses(category.color)} text-white mr-3`}>
                        <IconComponent className="h-5 w-5" />
                      </div>
                      <div>
                        <h3 className="font-semibold text-gray-900">{category.title}</h3>
                        <p className="text-sm text-gray-600">{category.description}</p>
                      </div>
                    </div>
                  </div>

                  <div className="p-4">
                    <div className="space-y-2">
                      {category.resources.map((resource, idx) => (
                        <div key={idx} className="flex items-center justify-between p-2 rounded-lg hover:bg-gray-50 transition-colors">
                          <div className="flex-1 min-w-0">
                            <h4 className="font-medium text-gray-900 text-sm truncate">{resource.name}</h4>
                            <div className="flex items-center gap-2 mt-1">
                              <span className={`px-2 py-0.5 rounded text-xs font-medium border ${getBadgeColors(category.color)}`}>
                                {resource.type}
                              </span>
                              {resource.size && (
                                <span className="text-xs text-gray-500">{resource.size}</span>
                              )}
                              {resource.downloads && (
                                <span className="text-xs text-gray-500">{resource.downloads}</span>
                              )}
                            </div>
                          </div>
                          {resource.link ? (
                            <Link 
                              to={resource.link}
                              className="ml-2 px-3 py-1 bg-blue-600 text-white text-xs rounded-lg hover:bg-blue-700 transition-colors flex items-center"
                            >
                              <Zap className="h-3 w-3 mr-1" />
                              Use
                            </Link>
                          ) : (
                            <button className="ml-2 px-3 py-1 bg-gray-600 text-white text-xs rounded-lg hover:bg-gray-700 transition-colors flex items-center">
                              <Download className="h-3 w-3 mr-1" />
                              Get
                            </button>
                          )}
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Compact FAQ Section */}
          <div className="bg-white rounded-2xl shadow-sm border p-6 mb-8">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-xl font-semibold text-gray-900 flex items-center">
                <HelpCircle className="h-5 w-5 mr-2 text-blue-600" />
                Frequently Asked Questions
              </h3>
              <Link 
                to="/faq"
                className="text-blue-600 hover:text-blue-700 text-sm font-medium flex items-center"
              >
                View All
                <ChevronRight className="h-4 w-4 ml-1" />
              </Link>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {faqCategories.map((faqCategory, index) => (
                <div key={index}>
                  <h4 className="font-medium text-gray-900 mb-3 text-sm">{faqCategory.category}</h4>
                  <div className="space-y-3">
                    {faqCategory.questions.slice(0, 2).map((faq, idx) => (
                      <div key={idx} className="border-l-2 border-blue-200 pl-3">
                        <h5 className="font-medium text-gray-900 text-xs mb-1 leading-tight">{faq.q}</h5>
                        <p className="text-gray-600 text-xs leading-relaxed line-clamp-2">{faq.a}</p>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Compact Expert Reviews */}
          <div className="bg-white rounded-2xl shadow-sm border p-6">
            <h3 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
              <Users className="h-5 w-5 mr-2 text-green-600" />
              Expert Reviews
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {expertReviews.map((review, index) => (
                <div key={index} className="p-4 bg-gray-50 rounded-lg">
                  <div className="flex justify-center mb-2">
                    {[...Array(review.rating)].map((_, i) => (
                      <Star key={i} className="h-3 w-3 text-yellow-400 fill-current" />
                    ))}
                  </div>
                  <p className="text-gray-700 text-xs mb-3 italic leading-relaxed">"{review.review}"</p>
                  <div className="text-center">
                    <div className="font-medium text-gray-900 text-sm">{review.name}</div>
                    <div className="text-xs text-gray-600">{review.title}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </main>

      <Footer />
    </>
  );
};

export default ResourcesPage;

