import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate, Link } from 'react-router-dom';
import { Search, Filter, Clock, TrendingUp, ArrowRight, FileText, Calculator, Pill, Users } from 'lucide-react';
import Header from '../components/Header';
import { searchContent, getRecentSearches, saveRecentSearch, SEARCH_CATEGORIES } from '../utils/searchUtils';

const SearchResultsPage = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const navigate = useNavigate();

  const [query, setQuery] = useState(searchParams.get('q') || '');
  const [category, setCategory] = useState(searchParams.get('category') || SEARCH_CATEGORIES.ALL);
  const [results, setResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [recentSearches, setRecentSearches] = useState([]);
  const [resultStats, setResultStats] = useState({});

  // Load recent searches
  useEffect(() => {
    setRecentSearches(getRecentSearches());
  }, []);

  // Perform search when query or category changes
  useEffect(() => {
    if (query.trim()) {
      performSearch(query, category);
    } else {
      setResults([]);
      setResultStats({});
    }
  }, [query, category]);

  // Update URL when search parameters change
  useEffect(() => {
    const params = new URLSearchParams();
    if (query) params.set('q', query);
    if (category !== SEARCH_CATEGORIES.ALL) params.set('category', category);
    setSearchParams(params);
  }, [query, category, setSearchParams]);

  const performSearch = async (searchQuery, searchCategory) => {
    setIsLoading(true);

    try {
      // Simulate slight delay for better UX
      await new Promise(resolve => setTimeout(resolve, 200));

      const searchResults = searchContent(searchQuery, searchCategory, 50);
      setResults(searchResults);

      // Calculate result statistics
      const stats = {
        total: searchResults.length,
        peptides: searchResults.filter(r => r.type === 'peptide').length,
        calculators: searchResults.filter(r => r.type === 'calculator').length,
        blog: searchResults.filter(r => r.type === 'blog').length,
        pages: searchResults.filter(r => r.type === 'page').length
      };
      setResultStats(stats);

      // Save to recent searches
      if (searchQuery.trim()) {
        saveRecentSearch(searchQuery);
        setRecentSearches(getRecentSearches());
      }
    } catch (error) {
      console.error('Search error:', error);
      setResults([]);
      setResultStats({});
    } finally {
      setIsLoading(false);
    }
  };

  const handleNewSearch = (e) => {
    e.preventDefault();
    if (query.trim()) {
      performSearch(query, category);
    }
  };

  const getItemIcon = (type) => {
    const iconClass = "h-5 w-5";
    switch (type) {
      case 'peptide':
        return <Pill className={`${iconClass} text-blue-600`} />;
      case 'calculator':
        return <Calculator className={`${iconClass} text-green-600`} />;
      case 'blog':
        return <FileText className={`${iconClass} text-purple-600`} />;
      case 'page':
        return <Users className={`${iconClass} text-orange-600`} />;
      default:
        return <Search className={`${iconClass} text-gray-400`} />;
    }
  };

  const getCategoryColor = (categoryName) => {
    const colors = {
      'Growth Hormone': 'bg-green-100 text-green-800',
      'GLP-1': 'bg-blue-100 text-blue-800',
      'Healing': 'bg-red-100 text-red-800',
      'Performance': 'bg-purple-100 text-purple-800',
      'Cognitive': 'bg-yellow-100 text-yellow-800',
      'Anti-Aging': 'bg-pink-100 text-pink-800',
      'Cosmetic': 'bg-indigo-100 text-indigo-800',
      'Immune': 'bg-orange-100 text-orange-800',
      'Health': 'bg-gray-100 text-gray-800',
      'Weight Loss': 'bg-red-100 text-red-800',
      'Dosing': 'bg-blue-100 text-blue-800',
      'Hormones': 'bg-purple-100 text-purple-800',
      'Fitness': 'bg-green-100 text-green-800',
      'Protocol': 'bg-yellow-100 text-yellow-800'
    };
    return colors[categoryName] || 'bg-gray-100 text-gray-800';
  };

  const getTypeLabel = (type) => {
    const labels = {
      peptide: 'Peptide',
      calculator: 'Calculator',
      blog: 'Article',
      page: 'Page'
    };
    return labels[type] || 'Result';
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Search Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-6">
            <h1 className="text-3xl font-bold text-gray-900">Search Results</h1>
            <Link
              to="/"
              className="text-blue-600 hover:text-blue-800 flex items-center text-sm"
            >
              Back to Home
              <ArrowRight className="h-4 w-4 ml-1" />
            </Link>
          </div>

          {/* Search Form */}
          <form onSubmit={handleNewSearch} className="mb-6">
            <div className="flex flex-col sm:flex-row gap-4">
              <div className="flex-1 relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="text"
                  value={query}
                  onChange={(e) => setQuery(e.target.value)}
                  placeholder="Search peptides, calculators, articles..."
                  className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 text-lg"
                />
              </div>
              <button
                type="submit"
                disabled={!query.trim() || isLoading}
                className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
              >
                {isLoading ? (
                  <div className="animate-spin h-4 w-4 border-2 border-white border-t-transparent rounded-full mr-2" />
                ) : (
                  <Search className="h-4 w-4 mr-2" />
                )}
                Search
              </button>
            </div>

            {/* Category Filter */}
            <div className="flex items-center space-x-2 mt-4">
              <Filter className="h-4 w-4 text-gray-400" />
              <span className="text-sm text-gray-600">Filter by:</span>
              <div className="flex flex-wrap gap-2">
                {Object.entries(SEARCH_CATEGORIES).map(([key, value]) => (
                  <button
                    key={key}
                    type="button"
                    onClick={() => setCategory(value)}
                    className={`px-3 py-1 text-sm rounded-full whitespace-nowrap transition-colors ${
                      category === value
                        ? 'bg-blue-500 text-white'
                        : 'bg-white text-gray-600 hover:bg-gray-100 border border-gray-300'
                    }`}
                  >
                    {key.charAt(0) + key.slice(1).toLowerCase()}
                  </button>
                ))}
              </div>
            </div>
          </form>

          {/* Search Stats */}
          {query && (
            <div className="flex flex-wrap items-center gap-4 text-sm text-gray-600 mb-6">
              <span>
                Showing {results.length} results for "<strong>{query}</strong>"
                {category !== SEARCH_CATEGORIES.ALL && (
                  <span> in {category} category</span>
                )}
              </span>
              {resultStats.total > 0 && (
                <div className="flex gap-4">
                  {resultStats.peptides > 0 && (
                    <span className="flex items-center">
                      <Pill className="h-4 w-4 mr-1 text-blue-600" />
                      {resultStats.peptides} peptides
                    </span>
                  )}
                  {resultStats.calculators > 0 && (
                    <span className="flex items-center">
                      <Calculator className="h-4 w-4 mr-1 text-green-600" />
                      {resultStats.calculators} calculators
                    </span>
                  )}
                  {resultStats.blog > 0 && (
                    <span className="flex items-center">
                      <FileText className="h-4 w-4 mr-1 text-purple-600" />
                      {resultStats.blog} articles
                    </span>
                  )}
                  {resultStats.pages > 0 && (
                    <span className="flex items-center">
                      <Users className="h-4 w-4 mr-1 text-orange-600" />
                      {resultStats.pages} pages
                    </span>
                  )}
                </div>
              )}
