frontend/src/pages/TRTCalculatorPage.jsx.content:
import React, { useState } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import MetaTags from '../components/seo/MetaTags';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../components/ui/select';
import { Badge } from '../components/ui/badge';
import { 
  Calculator, 
  Activity, 
  TrendingUp,
  Calendar,
  Syringe,
  AlertTriangle,
  CheckCircle,
  Crown,
  BarChart3
} from 'lucide-react';

const TRTCalculatorPage = () => {
  const [formData, setFormData] = useState({
    ester: '',
    target_mg_per_week: '',
    injection_frequency: '',
    concentration: '',
    baseline_t: ''
  });

  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // TRT Calculation Functions
  const calculateTRTProtocol = (ester, weeklyDose, injectionFreq, concentration, baselineT) => {
    // Dose per injection
    const dosePerInjection = weeklyDose / injectionFreq;

    // Injection volume (ml)
    const injectionVolume = dosePerInjection / concentration;

    // Half-life data for different esters (hours)
    const halfLives = {
      'cypionate': 168, // 7 days
      'enanthate': 120, // 5 days  
      'propionate': 48,  // 2 days
      'undecanoate': 504 // 21 days
    };

    const halfLife = halfLives[ester] || 168;

    // Calculate steady state time (approximately 5 half-lives)
    const steadyStateWeeks = Math.round((halfLife * 5) / 168 * 10) / 10;

    // Injection schedule recommendations
    let schedule = {};
    if (injectionFreq === 1) {
      schedule = { frequency: 'Once weekly', days: ['Monday'] };
    } else if (injectionFreq === 2) {
      schedule = { frequency: 'Twice weekly', days: ['Monday', 'Thursday'] };
    } else if (injectionFreq === 3) {
      schedule = { frequency: 'Every other day', days: ['Monday', 'Wednesday', 'Friday'] };
    } else {
      schedule = { frequency: `${injectionFreq} times weekly`, days: ['As prescribed'] };
    }

    // Expected testosterone levels (rough estimates)
    const peakMultiplier = ester === 'propionate' ? 1.8 : ester === 'enanthate' ? 1.6 : 1.5;
    const troughMultiplier = ester === 'propionate' ? 1.1 : ester === 'enanthate' ? 1.2 : 1.3;

    const estimatedPeak = baselineT ? baselineT * peakMultiplier : null;
    const estimatedTrough = baselineT ? baselineT * troughMultiplier : null;

    // Generate recommendations and warnings
    const recommendations = [
      `Inject ${dosePerInjection}mg (${Math.round(injectionVolume * 1000) / 1000}mL) ${schedule.frequency.toLowerCase()}`,
      `Use ${concentration}mg/mL concentration for optimal injection volume`,
      `Rotate injection sites to prevent tissue irritation`,
      `Monitor blood levels 4-6 weeks after starting or changing dose`,
      `Schedule injections at consistent times for stable levels`
    ];

    const warnings = [];
    if (injectionVolume > 1) {
      warnings.push(`Large injection volume (${injectionVolume.toFixed(2)}mL) - consider higher concentration`);
    }
    if (injectionFreq === 1 && ester !== 'undecanoate') {
      warnings.push('Once weekly injections may cause hormone level fluctuations');
    }
    if (weeklyDose > 200) {
      warnings.push('High dose - monitor for side effects and estrogen conversion');
    }

    return {
      weekly_dose: weeklyDose,
      dose_per_injection: Math.round(dosePerInjection * 100) / 100,
      injection_volume_ml: Math.round(injectionVolume * 1000) / 1000,
      injection_frequency: injectionFreq,
      concentration: concentration,
      ester: ester,
      half_life_hours: halfLife,
      steady_state_weeks: steadyStateWeeks,
      injection_schedule: schedule,
      estimated_levels: baselineT ? {
        peak: Math.round(estimatedPeak),
        trough: Math.round(estimatedTrough),
        baseline: baselineT
      } : null,
      recommendations: recommendations,
      warnings: warnings.length > 0 ? warnings : null,
      calculation_id: `TRT-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
    };
  };

  const handleCalculation = (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Validate required fields
      if (!formData.ester || !formData.target_mg_per_week || !formData.injection_frequency) {
        throw new Error('Please fill in all required fields');
      }

      const ester = formData.ester;
      const weeklyDose = parseFloat(formData.target_mg_per_week);
      const injectionFreq = parseInt(formData.injection_frequency);
      const concentration = formData.concentration ? parseFloat(formData.concentration) : getDefaultConcentration(ester);
      const baselineT = formData.baseline_t ? parseFloat(formData.baseline_t) : null;

      // Validate inputs
      if (weeklyDose <= 0 || weeklyDose > 1000) {
        throw new Error('Weekly dose must be between 1-1000mg');
      }

      if (injectionFreq < 1 || injectionFreq > 7) {
        throw new Error('Injection frequency must be between 1-7 times per week');
      }

      if (concentration <= 0 || concentration > 1000) {
        throw new Error('Concentration must be between 1-1000mg/ml');
      }

      const result = calculateTRTProtocol(ester, weeklyDose, injectionFreq, concentration, baselineT);
      setResults(result);

    } catch (err) {
      setError(err.message || 'Calculation failed');
    } finally {
      setLoading(false);
    }
  };

  const getDefaultConcentration = (ester) => {
    const defaults = {
      'cypionate': 200,
      'enanthate': 250,
      'propionate': 100,
      'undecanoate': 250
    };
    return defaults[ester] || 200;
  };

  return (
    <>
      <MetaTags 
        title="TRT Calculator | Testosterone Replacement Therapy Dosing Calculator"
        description="Calculate precise TRT (Testosterone Replacement Therapy) dosing protocols. Determine injection volumes, frequencies, and ester timing for optimal testosterone levels."
        keywords="TRT calculator, testosterone replacement therapy, TRT dosing, testosterone calculator, hormone therapy, testosterone injection calculator"
        canonical="https://www.professorpeptides.org/calculators/trt"
        type="article"
      />
      <Header />
      <div className="min-h-screen bg-gray-50 py-20">
      <div className="max-w-6xl mx-auto px-4">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="flex justify-center items-center mb-4">
            <div className="w-16 h-16 bg-gradient-to-r from-blue-100 to-cyan-100 rounded-full flex items-center justify-center">
              <Activity className="h-8 w-8 text-blue-600" />
            </div>
          </div>
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            ðŸ’ª TRT Protocol Pro Calculator
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Advanced testosterone replacement therapy protocol designer with pharmacokinetic analysis and injection scheduling
          </p>
          <Badge className="mt-4 bg-gradient-to-r from-blue-100 to-cyan-100 text-blue-700">
            <Crown className="h-4 w-4 mr-1" />
            PRO Calculator
          </Badge>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Calculator Form */}
          <Card className="shadow-xl">
            <CardHeader>
              <CardTitle className="flex items-center">
                <Calculator className="h-5 w-5 mr-2 text-blue-600" />
                TRT Protocol Calculator
              </CardTitle>
              <p className="text-gray-600">
                Calculate optimized testosterone replacement therapy protocols
              </p>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleCalculation} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Testosterone Ester
                  </label>
                  <Select onValueChange={(value) => setFormData({...formData, ester: value})}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select testosterone ester" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="cypionate">Cypionate (8-day half-life)</SelectItem>
                      <SelectItem value="enanthate">Enanthate (7-day half-life)</SelectItem>
                      <SelectItem value="propionate">Propionate (2-day half-life)</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Weekly Dose (mg)
                    </label>
                    <Input
                      type="number"
                      step="1"
                      value={formData.target_mg_per_week}
                      onChange={(e) => setFormData({...formData, target_mg_per_week: e.target.value})}
                      placeholder="150"
                      min="50"
                      max="500"
                      required
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Injection Frequency
                    </label>
                    <Select onValueChange={(value) => setFormData({...formData, injection_frequency: value})}>
                      <SelectTrigger>
                        <SelectValue placeholder="Frequency" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="1">Once per week</SelectItem>
                        <SelectItem value="2">Twice per week</SelectItem>
                        <SelectItem value="3">Three times per week</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Concentration (mg/mL) - Optional
                  </label>
                  <Input
                    type="number"
                    step="1"
                    value={formData.concentration}
                    onChange={(e) => setFormData({...formData, concentration: e.target.value})}
                    placeholder={formData.ester ? `Default: ${getDefaultConcentration(formData.ester)}` : "200"}
                    min="100"
                    max="500"
                  />
                  <div className="text-xs text-gray-500 mt-1">
                    Leave blank to use standard concentration
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Baseline Testosterone (ng/dL) - Optional
                  </label>
                  <Input
                    type="number"
                    step="1"
                    value={formData.baseline_t}
                    onChange={(e) => setFormData({...formData, baseline_t: e.target.value})}
                    placeholder="350"
                    min="100"
                    max="1000"
                  />
                  <div className="text-xs text-gray-500 mt-1">
                    For testosterone level projections
                  </div>
                </div>

                <Button type="submit" className="w-full" disabled={loading}>
                  {loading ? 'Creating Protocol...' : 'Generate TRT Protocol'}
                </Button>

                {error && (
                  <div className="bg-red-50 border border-red-200 rounded-lg p-3">
                    <p className="text-red-600 text-sm">âŒ {error}</p>
                  </div>
                )}
              </form>
            </CardContent>
          </Card>

          {/* Results */}
          <Card className="shadow-xl">
            <CardHeader>
              <CardTitle className="flex items-center">
                <BarChart3 className="h-5 w-5 mr-2 text-green-600" />
                Your TRT Protocol
              </CardTitle>
            </CardHeader>
            <CardContent>
              {results ? (
                <div className="space-y-6">
                  {/* Dosing Information */}
                  <div className="bg-blue-50 rounded-lg p-6">
                    <div className="flex items-center mb-3">
                      <Syringe className="h-5 w-5 text-blue-600 mr-2" />
                      <h3 className="text-lg font-semibold text-blue-900">Dosing Protocol</h3>
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <div className="text-2xl font-bold text-blue-700">
                          {results.dose_per_injection} mg
                        </div>
                        <div className="text-sm text-blue-600">Per injection</div>
                      </div>
                      <div>
                        <div className="text-2xl font-bold text-blue-700">
                          {results.injection_volume_ml} mL
                        </div>
                        <div className="text-sm text-blue-600">Volume per injection</div>
                      </div>
                    </div>
                    <div className="mt-3 pt-3 border-t border-blue-200">
                      <div className="text-sm text-blue-700">
                        <strong>Concentration:</strong> {results.concentration} mg/mL
                      </div>
                      <div className="text-sm text-blue-700">
                        <strong>Weekly Total:</strong> {results.weekly_dose} mg
                      </div>
                    </div>
                  </div>

                  {/* Pharmacokinetics */}
                  <div className="bg-green-50 rounded-lg p-6">
                    <div className="flex items-center mb-3">
                      <TrendingUp className="h-5 w-5 text-green-600 mr-2" />
                      <h3 className="text-lg font-semibold text-green-900">Pharmacokinetics</h3>
                    </div>
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <div className="text-2xl font-bold text-green-700">
                          {results.half_life_hours}h
                        </div>
                        <div className="text-sm text-green-600">Half-life ({results.ester})</div>
                      </div>
                      <div>
                        <div className="text-2xl font-bold text-green-700">
                          {results.steady_state_weeks}
                        </div>
                        <div className="text-sm text-green-600">Weeks to steady state</div>
                      </div>
                    </div>

                    {/* Testosterone Levels */}
                    {results.estimated_levels && (
                      <div className="mt-4 pt-4 border-t border-green-200">
                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <div className="text-lg font-bold text-green-700">
                              {results.estimated_levels.peak} ng/dL
                            </div>
                            <div className="text-sm text-green-600">Estimated Peak</div>
                          </div>
                          <div>
                            <div className="text-lg font-bold text-green-700">
                              {results.estimated_levels.trough} ng/dL
                            </div>
                            <div className="text-sm text-green-600">Estimated Trough</div>
                          </div>
                        </div>
                      </div>
                    )}
                  </div>

                  {/* Recommendations */}
                  <div className="bg-gray-50 rounded-lg p-4">
                    <div className="flex items-center mb-2">
                      <CheckCircle className="h-5 w-5 text-gray-600 mr-2" />
                      <h4 className="font-semibold text-gray-900">Clinical Recommendations</h4>
                    </div>
                    <ul className="text-gray-700 text-sm space-y-1">
                      {results.recommendations.map((rec, index) => (
                        <li key={index}>â€¢ {rec}</li>
                      ))}
                    </ul>
                  </div>

                  {/* Warnings */}
                  {results.warnings && results.warnings.length > 0 && (
                    <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                      <div className="flex items-center mb-2">
                        <AlertTriangle className="h-5 w-5 text-yellow-600 mr-2" />
                        <h4 className="font-semibold text-yellow-900">Important Notes</h4>
                      </div>
                      <ul className="text-yellow-800 text-sm space-y-1">
                        {results.warnings.map((warning, index) => (
                          <li key={index}>â€¢ {warning}</li>
                        ))}
                      </ul>
                    </div>
                  )}

                  {/* Illustration Placeholder */}
                  <div className="bg-gray-50 rounded-lg p-6 text-center">
                    <div className="w-16 h-16 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-3">
                      <Activity className="h-8 w-8 text-gray-400" />
                    </div>
                    <h4 className="font-semibold text-gray-900 mb-2">Protocol Visualization</h4>
                    <p className="text-gray-600 text-sm">
                      Interactive TRT timeline and hormone level charts coming soon
                    </p>
                  </div>

                  <div className="text-xs text-gray-500 text-center pt-4 border-t">
                    Calculation ID: {results.calculation_id}
                  </div>
                </div>
              ) : (
                <div className="text-center py-12 text-gray-500">
                  <Activity className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>Complete the form to generate your TRT protocol</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Injection Schedule */}
        {results && results.injection_schedule && (
          <div className="mt-12">
            <Card className="shadow-xl">
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Calendar className="h-5 w-5 mr-2 text-blue-600" />
                  Injection Schedule
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="bg-blue-50 rounded-lg p-6">
                  <div className="flex items-center mb-3">
                    <Calendar className="h-5 w-5 text-blue-600 mr-2" />
                    <h3 className="text-lg font-semibold text-blue-900">Schedule: {results.injection_schedule.frequency}</h3>
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <div className="text-2xl font-bold text-blue-700">
                        {results.injection_schedule.days.join(', ')}
                      </div>
                      <div className="text-sm text-blue-600">Injection days</div>
                    </div>
                    <div>
                      <div className="text-2xl font-bold text-blue-700">
                        {results.dose_per_injection}mg
                      </div>
                      <div className="text-sm text-blue-600">Per injection</div>
                    </div>
                  </div>
                </div>

                <div className="mt-4 text-center text-sm text-gray-500">
                  Maintain consistent timing for optimal hormone stability
                </div>
              </CardContent>
            </Card>
          </div>
        )}

        {/* Educational Information */}
        <div className="mt-12 bg-white rounded-lg shadow-lg p-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Understanding TRT Protocols</h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div>
              <h3 className="font-semibold text-gray-900 mb-3">Testosterone Esters</h3>
              <div className="space-y-3">
                <div>
                  <h4 className="font-medium text-gray-800">Cypionate</h4>
                  <p className="text-gray-600 text-sm">Long-acting, 8-day half-life. Most common in US. Stable levels with weekly injections.</p>
