import axios from 'axios';

// Get backend URL from environment variable
const API_BASE_URL = process.env.REACT_APP_BACKEND_URL || `https://${process.env.REACT_APP_REPLIT_DOMAIN || 'localhost'}:8000`;
const API_URL = `${API_BASE_URL}/api`;

// Retry configuration
const RETRY_CONFIG = {
  retries: 3,
  retryDelay: (retryCount) => Math.pow(2, retryCount) * 1000, // Exponential backoff: 1s, 2s, 4s
  retryCondition: (error) => {
    return error.code === 'ECONNABORTED' || 
           error.code === 'NETWORK_ERROR' ||
           (error.response && error.response.status >= 500);
  }
};

// Create axios instance with default config
const api = axios.create({
  baseURL: API_URL,
  timeout: 15000, // Increased timeout to 15 seconds
  headers: {
    'Content-Type': 'application/json',
  },
});

// Retry wrapper function
const withRetry = async (apiCall, retries = RETRY_CONFIG.retries) => {
  try {
    return await apiCall();
  } catch (error) {
    if (retries > 0 && RETRY_CONFIG.retryCondition(error)) {
      const delay = RETRY_CONFIG.retryDelay(RETRY_CONFIG.retries - retries);
      if (process.env.NODE_ENV === 'development') {
        console.log(`API call failed, retrying in ${delay}ms... (${retries} retries left)`);
      }

      await new Promise(resolve => setTimeout(resolve, delay));
      return withRetry(apiCall, retries - 1);
    }
    throw error;
  }
};

// API service functions
export const apiService = {
  // Health check with retry
  healthCheck: async () => {
    return withRetry(async () => {
      const response = await api.get('/health');
      return response.data;
    });
  },

  // Peptide related endpoints
  getPeptides: async () => {
    return withRetry(async () => {
      const response = await api.get('/peptides');
      return response.data;
    });
  },

  getPeptideCategories: async () => {
    return withRetry(async () => {
      const response = await api.get('/peptide-categories');
      return response.data;
    });
  },

  getPeptideCategory: async (categoryKey) => {
    return withRetry(async () => {
      const response = await api.get(`/peptide-categories/${categoryKey}`);
      return response.data;
    });
  },

  getPeptide: async (peptideSlug) => {
    return withRetry(async () => {
      const response = await api.get(`/peptides/${peptideSlug}`);
      return response.data;
    });
  },

  // Contact form
  submitContactForm: async (formData) => {
    return withRetry(async () => {
      const response = await api.post('/contact', formData);
      return response.data;
    });
  },

  // Newsletter signup
  submitNewsletterSignup: async (signupData) => {
    return withRetry(async () => {
      const response = await api.post('/newsletter/signup', signupData);
      return response.data;
    });
  },

  // Calculator
  calculatePeptide: async (calculationData) => {
    return withRetry(async () => {
      const response = await api.post('/calculator/calculate', calculationData);
      return response.data;
    });
  },

  // Team members
  getTeamMembers: async () => {
    return withRetry(async () => {
      const response = await api.get('/team-members');
      return response.data.team;
    });
  },

  // Blog posts
  getBlogPosts: async () => {
    return withRetry(async () => {
      const response = await api.get('/blog-posts');
      return response.data.posts;
    });
  }
};

export default apiService;

frontend/src/services/staticDataService..content:
/**
 * Static Data Service for Professor Peptides
 * 
 * This service provides access to all pre-fetched static data,
 * eliminating runtime API dependencies for a fully offline-capable site.
 * 
 * All data is embedded during build time from JSON files generated
 * by the prebuild-data-fetch.js script.
 */

// Import all static data files
import peptidesComplete from '../data/static/peptides-complete.json';
import peptideCategories from '../data/static/peptide-categories.json';
import teamMembers from '../data/static/team-members.json';
import blogPosts from '../data/static/blog-posts.json';
import calculatorConfigs from '../data/static/calculator-configs.json';
import peptideDosingData from '../data/static/peptide-dosing-data.json';
import assessmentQuiz from '../data/static/assessment-quiz.json';
import researchData from '../data/static/research-data.json';
import buildReport from '../data/static/build-report.json';

// Cache for individual peptide data - loaded dynamically
const peptideCache = new Map();

// Static data service class
class StaticDataService {
  constructor() {
    this.isStatic = true;
    this.buildTimestamp = buildReport.timestamp;
    this.dataVersion = '2.0.0-static';

    console.log(`ðŸ“‹ Static Data Service initialized (Built: ${this.buildTimestamp})`);
    console.log(`ðŸ”¢ Available peptides: ${this.getTotalPeptideCount()}`);
    console.log(`ðŸ“‚ Available categories: ${Object.keys(peptideCategories).length}`);
  }

  // Health check - always returns healthy for static mode
  async healthCheck() {
    return {
      status: 'healthy',
      mode: 'static',
      timestamp: new Date().toISOString(),
      buildTimestamp: this.buildTimestamp,
      dataVersion: this.dataVersion,
      peptideCount: this.getTotalPeptideCount(),
      categoriesCount: Object.keys(peptideCategories).length
    };
  }

  // Get all peptides data
  async getPeptides() {
    return peptidesComplete;
  }

  // Get peptide categories
  async getPeptideCategories() {
    return peptideCategories;
  }

  // Get specific peptide category
  async getPeptideCategory(categoryKey) {
    const category = peptideCategories[categoryKey];
    if (!category) {
      throw new Error(`Category not found: ${categoryKey}`);
    }
    return category;
  }

  // Get individual peptide by slug
  async getPeptide(peptideSlug) {
    // Check cache first
    if (peptideCache.has(peptideSlug)) {
      return peptideCache.get(peptideSlug);
    }

    try {
      // Dynamically import individual peptide file
      const peptideData = await import(`../data/static/peptide-${peptideSlug}.json`);
      const peptide = peptideData.default;

      // Cache the data
      peptideCache.set(peptideSlug, peptide);

      return peptide;
    } catch (error) {
      throw new Error(`Peptide not found: ${peptideSlug}`);
    }
  }

  // Get team members
  async getTeamMembers() {
    return teamMembers;
  }

  // Get blog posts
  async getBlogPosts() {
    return blogPosts;
  }

  // Calculator-related methods
  async getCalculatorConfigs() {
    return calculatorConfigs;
  }

  async getPeptideDosingData() {
    return peptideDosingData;
  }

  async calculatePeptide(calculationData) {
    // Perform client-side calculations
    const { peptideSlug, calculationType, formData } = calculationData;

    const configs = await this.getCalculatorConfigs();
    const dosingData = await this.getPeptideDosingData();

    const config = configs[calculationType];
    if (!config) {
      throw new Error(`Calculator type not supported: ${calculationType}`);
    }

    // Perform calculations based on type
    switch (calculationType) {
      case 'reconstitution':
        return this.calculateReconstitution(formData, peptideSlug);
      case 'dosing':
        return this.calculateDosing(formData, peptideSlug);
      case 'conversion':
        return this.calculateConversion(formData);
      case 'bmi':
        return this.calculateBMI(formData);
      case 'glp1':
        return this.calculateGLP1(formData);
      default:
        throw new Error(`Unsupported calculation type: ${calculationType}`);
    }
  }

  // Assessment quiz methods
  async getAssessmentQuiz() {
    return assessmentQuiz;
  }

  // Research data methods
  async getResearchData() {
    return researchData;
  }

  async getResearchPublications(peptideSlug) {
    const research = await this.getResearchData();
    return research.publications[peptideSlug] || [];
  }

  // Search functionality
  async searchPeptides(query) {
    const allPeptides = await this.getAllPeptidesFlat();
    const lowerQuery = query.toLowerCase();

    return allPeptides.filter(peptide => {
      return (
        peptide.name.toLowerCase().includes(lowerQuery) ||
        peptide.description.toLowerCase().includes(lowerQuery) ||
        peptide.benefits.some(benefit => benefit.toLowerCase().includes(lowerQuery)) ||
        (peptide.aliases && peptide.aliases.some(alias => alias.toLowerCase().includes(lowerQuery)))
      );
    });
  }

  // Utility methods
  getTotalPeptideCount() {
    return Object.values(peptidesComplete).reduce((total, category) => {
      return total + (category.peptides ? category.peptides.length : 0);
    }, 0);
  }

  async getAllPeptidesFlat() {
    const categories = await this.getPeptides();
    const allPeptides = [];

    Object.keys(categories).forEach(categoryKey => {
      const category = categories[categoryKey];
      if (category.peptides) {
        category.peptides.forEach(peptide => {
          allPeptides.push({
            ...peptide,
            category: categoryKey,
            categoryTitle: category.title
          });
        });
      }
    });

    return allPeptides;
  }

  async getPeptidesByCategory(categoryKey) {
    const category = await this.getPeptideCategory(categoryKey);
    return category.peptides || [];
  }

  async getFeaturedPeptides(limit = 6) {
    const allPeptides = await this.getAllPeptidesFlat();

    // Prioritize FDA approved peptides
    const fdaApproved = allPeptides.filter(p => p.fdaApproved);
    const others = allPeptides.filter(p => !p.fdaApproved);

    const featured = [...fdaApproved, ...others].slice(0, limit);
    return featured;
  }

  // Calculator helper methods
  calculateReconstitution(formData, peptideSlug) {
    const { vialSize, bacteriostaticWater, targetDose } = formData;
    const concentration = vialSize / bacteriostaticWater;

    const dosingData = peptideDosingData[peptideSlug];
    const unit = dosingData?.unit || 'mcg';

    if (unit === 'mg') {
      const injectionVolume = targetDose / concentration;
      const unitsPerML = concentration * 1000;

      return {
        concentration: concentration.toFixed(2),
        injectionVolume: injectionVolume.toFixed(3),
        unitsPerML: unitsPerML.toFixed(0),
        dosesPerVial: Math.floor(vialSize / targetDose),
        unit: 'mg/ml'
      };
    } else {
      const concentrationMcg = concentration * 1000;
      const injectionVolume = targetDose / concentrationMcg;

      return {
        concentration: concentration.toFixed(2),
        concentrationMcg: concentrationMcg.toFixed(0),
        injectionVolume: injectionVolume.toFixed(3),
        dosesPerVial: Math.floor((vialSize * 1000) / targetDose),
        unit: 'mg/ml'
      };
    }
  }

  calculateDosing(formData, peptideSlug) {
    const { bodyWeight, frequency, duration } = formData;
    const dosingData = peptideDosingData[peptideSlug];

    if (!dosingData) {
      throw new Error(`Dosing data not available for: ${peptideSlug}`);
    }

    const baseDose = dosingData.baseMultiplier;
    const dailyDose = baseDose * bodyWeight * frequency;
    const weeklyDose = dailyDose * 7;
    const totalDose = weeklyDose * duration;

    return {
      dailyDose: dailyDose.toFixed(2),
      weeklyDose: weeklyDose.toFixed(2),
      totalDose: totalDose.toFixed(2),
      vialsNeeded: Math.ceil(totalDose / 5),
      unit: dosingData.unit
    };
  }

  calculateConversion(formData) {
    const { amount, fromUnit, toUnit } = formData;
    let result = amount;

    // Convert to base unit (mg)
    switch (fromUnit) {
      case 'mcg':
        result = result / 1000;
        break;
      case 'IU':
        result = result * 0.33; // Default IU conversion
        break;
    }

    // Convert from base unit to target
    switch (toUnit) {
      case 'mcg':
        result = result * 1000;
        break;
      case 'IU':
        result = result / 0.33;
        break;
    }

    return {
      convertedAmount: result.toFixed(3),
      fromUnit,
      toUnit,
      originalAmount: amount
    };
  }

  calculateBMI(formData) {
    const { height, weight, age, gender } = formData;
    const heightM = height / 100; // Convert cm to meters
    const bmi = weight / (heightM * heightM);

    let category = '';
    let recommendation = '';

    if (bmi < 18.5) {
      category = 'Underweight';
      recommendation = 'Consider consulting a healthcare provider for weight gain strategies';
    } else if (bmi < 25) {
      category = 'Normal weight';
      recommendation = 'Maintain current weight with balanced diet and exercise';
    } else if (bmi < 30) {
      category = 'Overweight';
      recommendation = 'Consider weight management strategies and consultation with healthcare provider';
    } else {
      category = 'Obese';
      recommendation = 'Strongly recommend consultation with healthcare provider for weight management';
    }

    return {
      bmi: bmi.toFixed(1),
      category,
      recommendation,
      idealWeightRange: {
        min: (18.5 * heightM * heightM).toFixed(1),
        max: (24.9 * heightM * heightM).toFixed(1)
      }
    };
  }

  calculateGLP1(formData) {
    const { currentWeight, targetWeight, peptideType, duration } = formData;
    const weightLoss = currentWeight - targetWeight;
    const weightLossPercent = (weightLoss / currentWeight) * 100;

    // Efficacy rates based on clinical data
    const efficacyRates = {
      semaglutide: 0.15, // 15% average weight loss
      tirzepatide: 0.22, // 22% average weight loss
      liraglutide: 0.08  // 8% average weight loss
    };

    const expectedRate = efficacyRates[peptideType] || 0.15;
    const expectedWeightLoss = currentWeight * expectedRate;
    const projectedWeight = currentWeight - expectedWeightLoss;
    const timeToTarget = Math.max(duration, Math.ceil(weightLoss / (expectedWeightLoss / 52))); // weeks

    return {
      targetWeightLoss: weightLoss.toFixed(1),
      targetWeightLossPercent: weightLossPercent.toFixed(1),
      expectedWeightLoss: expectedWeightLoss.toFixed(1),
      projectedWeight: projectedWeight.toFixed(1),
      timeToTarget: timeToTarget,
      achievable: weightLossPercent <= expectedRate * 100,
      peptideType
    };
  }

  // Form submission methods (static mode - no actual submission)
  async submitContactForm(formData) {
    console.log('ðŸ“§ Contact form submission (static mode):', formData);
    // In static mode, we can't actually submit forms
    throw new Error('Form submission not available in static mode. Please enable backend API for form submissions.');
  }

  async submitNewsletterSignup(signupData) {
    console.log('ðŸ“¬ Newsletter signup (static mode):', signupData);
    // In static mode, we can't actually submit forms
    throw new Error('Newsletter signup not available in static mode. Please enable backend API for form submissions.');
  }

  // Get build information
  getBuildInfo() {
    return {
      mode: 'static',
      buildTimestamp: this.buildTimestamp,
      dataVersion: this.dataVersion,
      totalPeptides: this.getTotalPeptideCount(),
      totalCategories: Object.keys(peptideCategories).length,
      buildReport: buildReport
    };
  }
}

// Create and export singleton instance
const staticDataService = new StaticDataService();

// Export both the service instance and class for flexibility
export default staticDataService;
export { StaticDataService };

// For backwards compatibility with API service interface
export const apiService = staticDataService;

