frontend/src/styles/critical.css.content:
/* Critical CSS for above-the-fold performance */

/* Mobile-first optimizations */
* {
  text-rendering: optimizeLegibility;
  box-sizing: border-box;
}

/* System font stack for faster loading */
body {
  font-family: system-ui, -apple-system, 'Segoe UI', Roboto, Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

/* Avoid FOIT with system fonts */
@font-face {
  font-family: Inter;
  font-display: swap;
  src: local('Inter'), local('system-ui');
}

/* Critical viewport units optimization */
:root {
  --vh: 1vh;
}

/* Mobile viewport fix */
@supports (-webkit-touch-callout: none) {
  :root {
    --vh: calc(var(--vh, 1vh) * 100);
  }
}

/* Defer below-the-fold content */
.defer {
  content-visibility: auto;
  contain-intrinsic-size: 1px 2000px;
}

/* Optimize hero section for LCP */
.hero-section {
  contain: layout style paint;
  will-change: transform;
  min-height: calc(var(--vh, 1vh) * 100);
}

/* Mobile-optimized interactions */
.btn {
  touch-action: manipulation;
  -webkit-tap-highlight-color: transparent;
}

.btn:hover {
  transform: translateY(-1px);
  transition: transform 0.15s ease;
}

/* Critical layout stability */
img, video {
  max-width: 100%;
  height: auto;
  aspect-ratio: attr(width) / attr(height);
}

/* Optimize for mobile performance */
@media (max-width: 768px) {
  * {
    -webkit-transform: translateZ(0);
    transform: translateZ(0);
  }

  .hero-section {
    will-change: auto;
  }
}

/* Reduce motion for better performance */
@media (prefers-reduced-motion: reduce) {
  *, *::before, *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

/* Performance-critical animations only */
.animate-pulse {
  animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

@keyframes pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: .5; }
}

