/**
 * Canonical URL Management System for Professor Peptides
 * Handles canonical URL generation, redirects, and URL optimization
 * for all page types to prevent duplicate content issues and improve SEO
 */

const SITE_URL = 'https://www.professorpeptides.org';
const ALTERNATIVE_DOMAINS = [
  'https://www.professorpeptides.org',
  'https://professor-peptides.netlify.app',
  'https://professorpeptides.com',
  'https://www.professorpeptides.com'
];

/**
 * Generate canonical URL for any page type
 * @param {string} path - Current path
 * @param {object} params - URL parameters
 * @param {object} options - Additional options
 * @returns {string} Canonical URL
 */
export const generateCanonicalUrl = (path, params = {}, options = {}) => {
  // Clean the path
  const cleanPath = cleanUrlPath(path);

  // Build base URL
  let canonicalUrl = `${SITE_URL}${cleanPath}`;

  // Handle special cases and redirects
  canonicalUrl = handleSpecialCases(canonicalUrl, params, options);

  // Remove trailing slash except for root
  if (canonicalUrl !== `${SITE_URL}/` && canonicalUrl.endsWith('/')) {
    canonicalUrl = canonicalUrl.slice(0, -1);
  }

  return canonicalUrl;
};

/**
 * Clean and normalize URL path
 * @param {string} path - Raw path
 * @returns {string} Cleaned path
 */
const cleanUrlPath = (path) => {
  if (!path) return '/';

  // Remove duplicate slashes
  let cleanPath = path.replace(/\/+/g, '/');

  // Ensure leading slash
  if (!cleanPath.startsWith('/')) {
    cleanPath = '/' + cleanPath;
  }

  // Remove trailing slash except for root
  if (cleanPath !== '/' && cleanPath.endsWith('/')) {
    cleanPath = cleanPath.slice(0, -1);
  }

  // Handle URL encoding
  cleanPath = decodeURIComponent(cleanPath);

  return cleanPath;
};

/**
 * Handle special cases and legacy redirects
 * @param {string} url - Base URL
 * @param {object} params - Parameters
 * @param {object} options - Options
 * @returns {string} Processed URL
 */
const handleSpecialCases = (url, params, options) => {
  // Legacy URL redirects
  const legacyRedirects = {
    '/reconstitution-calculator': '/calculators/reconstitution',
    '/peptide-protocol-tool': '/calculators/protocol',
    '/bmi-calculator': '/calculators/bmi',
    '/glp1-calculator': '/calculators/glp1',
    '/trt-calculator': '/calculators/trt',
    '/fitness-calculator': '/calculators/fitness',
    '/melanotan-calculator': '/calculators/melanotan'
  };

  // Check for legacy redirects
  const currentPath = url.replace(SITE_URL, '');
  if (legacyRedirects[currentPath]) {
    return `${SITE_URL}${legacyRedirects[currentPath]}`;
  }

  // Handle peptide category URLs
  if (currentPath.includes('/peptides/') && !currentPath.includes('/peptide/')) {
    // Ensure category URLs are properly formatted
    const segments = currentPath.split('/');
    if (segments.length === 3 && segments[1] === 'peptides') {
      const categoryKey = segments[2];
      // Validate category exists
      const validCategories = [
        'metabolicHealth',
        'tissueRepairRecovery', 
        'performanceEnhancement',
        'cognitiveEnhancement',
        'painRelief',
        'skinHealth',
        'immuneSupport'
      ];

      if (validCategories.includes(categoryKey)) {
        return `${SITE_URL}/peptides/${categoryKey}`;
      }
    }
  }

  // Handle individual peptide URLs
  if (currentPath.startsWith('/peptide/')) {
    const slug = currentPath.replace('/peptide/', '');
    if (slug && slug.length > 0) {
      return `${SITE_URL}/peptide/${slug}`;
    }
  }

  // Handle calculator URLs
  if (currentPath.startsWith('/calculators/')) {
    const calcType = currentPath.replace('/calculators/', '');
    const validCalculators = [
      'bmi',
      'reconstitution', 
      'glp1',
      'trt',
      'fitness',
      'melanotan',
      'protocol'
    ];

    if (validCalculators.includes(calcType)) {
      return `${SITE_URL}/calculators/${calcType}`;
    }
  }

  // Handle blog URLs
  if (currentPath.startsWith('/blog/') && currentPath !== '/blog/') {
    const slug = currentPath.replace('/blog/', '');
    if (slug && slug.length > 0) {
      return `${SITE_URL}/blog/${slug}`;
    }
  }

  // Handle research URLs
  if (currentPath.startsWith('/research/') && currentPath !== '/research/') {
    const category = currentPath.replace('/research/', '');
    if (category && category.length > 0) {
      return `${SITE_URL}/research/${category}`;
    }
  }

  return url;
};

/**
 * Check if current URL should redirect to canonical
 * @param {string} currentUrl - Current page URL
 * @param {string} canonicalUrl - Canonical URL
 * @returns {boolean} Whether redirect is needed
 */
export const shouldRedirectToCanonical = (currentUrl, canonicalUrl) => {
  if (!currentUrl || !canonicalUrl) return false;

  // Normalize URLs for comparison
  const normalizedCurrent = normalizeUrl(currentUrl);
  const normalizedCanonical = normalizeUrl(canonicalUrl);

  return normalizedCurrent !== normalizedCanonical;
};

/**
 * Normalize URL for comparison
 * @param {string} url - URL to normalize
 * @returns {string} Normalized URL
 */
const normalizeUrl = (url) => {
  try {
    const urlObj = new URL(url);

    // Remove common parameters that don't affect content
    const paramsToRemove = [
      'utm_source', 'utm_medium', 'utm_campaign', 'utm_term', 'utm_content',
      'fbclid', 'gclid', 'ref', 'source', 'from'
    ];

    paramsToRemove.forEach(param => {
      urlObj.searchParams.delete(param);
    });

    // Sort remaining parameters for consistent comparison
    urlObj.searchParams.sort();

    // Remove trailing slash except for root
    let pathname = urlObj.pathname;
    if (pathname !== '/' && pathname.endsWith('/')) {
      pathname = pathname.slice(0, -1);
    }

    return `${urlObj.origin}${pathname}${urlObj.search}${urlObj.hash}`;
  } catch (error) {
    return url;
  }
};

/**
 * Generate alternate language URLs (hreflang)
 * @param {string} canonicalUrl - Base canonical URL
 * @param {array} languages - Supported languages
 * @returns {array} Array of hreflang objects
 */
export const generateHreflangUrls = (canonicalUrl, languages = ['en']) => {
  const hreflangs = [];

  languages.forEach(lang => {
    let url = canonicalUrl;

    // Add language parameter if not English
    if (lang !== 'en') {
      const urlObj = new URL(canonicalUrl);
      urlObj.searchParams.set('lang', lang);
      url = urlObj.toString();
    }

    hreflangs.push({
      lang: lang,
      url: url,
      title: getLanguageTitle(lang)
    });
  });

  // Add x-default for international targeting
  hreflangs.push({
    lang: 'x-default',
    url: canonicalUrl,
    title: 'Default'
  });

  return hreflangs;
};

/**
 * Get language title for display
 * @param {string} lang - Language code
 * @returns {string} Language title
 */
const getLanguageTitle = (lang) => {
  const titles = {
    'en': 'English',
    'es': 'EspaÃ±ol',
    'fr': 'FranÃ§ais',
    'de': 'Deutsch',
    'it': 'Italiano',
    'pt': 'PortuguÃªs',
    'zh': 'ä¸­æ–‡',
    'ja': 'æ—¥æœ¬èªž',
    'ru': 'Ð ÑƒÑÑÐºÐ¸Ð¹',
    'ar': 'Ø§Ù„Ø¹Ø±Ø¨ÙŠØ©'
  };

  return titles[lang] || lang.toUpperCase();
};

/**
 * Generate pagination canonical URLs
 * @param {string} basePath - Base path without pagination
 * @param {number} currentPage - Current page number
 * @param {number} totalPages - Total number of pages
 * @returns {object} Pagination canonical URLs
 */
export const generatePaginationCanonicals = (basePath, currentPage = 1, totalPages = 1) => {
  const canonicals = {
    current: generateCanonicalUrl(currentPage === 1 ? basePath : `${basePath}?page=${currentPage}`),
    first: generateCanonicalUrl(basePath),
    prev: currentPage > 1 ? generateCanonicalUrl(currentPage === 2 ? basePath : `${basePath}?page=${currentPage - 1}`) : null,
    next: currentPage < totalPages ? generateCanonicalUrl(`${basePath}?page=${currentPage + 1}`) : null,
    last: totalPages > 1 ? generateCanonicalUrl(`${basePath}?page=${totalPages}`) : null
  };

  return canonicals;
};

/**
 * Validate canonical URL
 * @param {string} url - URL to validate
 * @returns {object} Validation result
 */
export const validateCanonicalUrl = (url) => {
  const validation = {
    isValid: true,
    errors: [],
    warnings: []
  };

  try {
    const urlObj = new URL(url);

    // Check protocol
    if (!['https:', 'http:'].includes(urlObj.protocol)) {
      validation.errors.push('URL must use HTTP or HTTPS protocol');
      validation.isValid = false;
    }

    // Prefer HTTPS
    if (urlObj.protocol === 'http:') {
      validation.warnings.push('Consider using HTTPS for better SEO');
    }

    // Check domain
    if (!ALTERNATIVE_DOMAINS.some(domain => url.startsWith(domain)) && !url.startsWith(SITE_URL)) {
      validation.warnings.push('URL domain does not match primary or alternative domains');
    }

    // Check for common issues
    if (url.includes('//')) {
      validation.warnings.push('URL contains double slashes');
    }

    if (url !== url.toLowerCase() && !url.includes('?')) {
      validation.warnings.push('URL contains uppercase characters');
    }

    if (url.length > 255) {
      validation.warnings.push('URL is longer than recommended 255 characters');
    }

    // Check for tracking parameters
    const trackingParams = ['utm_source', 'utm_medium', 'utm_campaign', 'fbclid', 'gclid'];
    const hasTracking = trackingParams.some(param => urlObj.searchParams.has(param));
    if (hasTracking) {
      validation.warnings.push('URL contains tracking parameters that should be removed from canonical');
    }

  } catch (error) {
    validation.errors.push(`Invalid URL format: ${error.message}`);
    validation.isValid = false;
  }

  return validation;
};

/**
 * Get URL redirect recommendations
 * @param {string} currentUrl - Current URL
 * @returns {object} Redirect recommendations
 */
export const getRedirectRecommendations = (currentUrl) => {
  const recommendations = {
    shouldRedirect: false,
    targetUrl: null,
    reason: null,
    type: null // 301, 302, canonical
  };

  try {
    const urlObj = new URL(currentUrl);
    const path = urlObj.pathname;

    // Check for legacy URLs that should redirect
    const legacyRedirects = {
      '/reconstitution-calculator': '/calculators/reconstitution',
      '/peptide-protocol-tool': '/calculators/protocol',
      '/bmi-calculator': '/calculators/bmi',
      '/glp1-calculator': '/calculators/glp1'
    };

    if (legacyRedirects[path]) {
      recommendations.shouldRedirect = true;
      recommendations.targetUrl = `${SITE_URL}${legacyRedirects[path]}`;
      recommendations.reason = 'Legacy URL redirect';
      recommendations.type = '301';
    }

    // Check for trailing slashes
    if (path !== '/' && path.endsWith('/')) {
      recommendations.shouldRedirect = true;
      recommendations.targetUrl = `${urlObj.origin}${path.slice(0, -1)}${urlObj.search}`;
      recommendations.reason = 'Remove trailing slash';
      recommendations.type = '301';
    }

    // Check for non-canonical domain
    if (!currentUrl.startsWith(SITE_URL)) {
      recommendations.shouldRedirect = true;
      recommendations.targetUrl = currentUrl.replace(urlObj.origin, SITE_URL);
      recommendations.reason = 'Redirect to canonical domain';
      recommendations.type = '301';
    }

    // Check for HTTP to HTTPS
    if (urlObj.protocol === 'http:') {
      recommendations.shouldRedirect = true;
      recommendations.targetUrl = currentUrl.replace('http://', 'https://');
      recommendations.reason = 'Upgrade to HTTPS';
      recommendations.type = '301';
    }

  } catch (error) {
    // Invalid URL, no recommendations
  }

  return recommendations;
};

/**
 * Generate sitemap entry for URL
 * @param {string} url - URL to generate sitemap entry for
 * @param {object} options - Additional options
 * @returns {object} Sitemap entry
 */
export const generateSitemapEntry = (url, options = {}) => {
  return {
    loc: url,
    lastmod: options.lastModified || new Date().toISOString().split('T')[0],
    changefreq: options.changeFreq || 'monthly',
    priority: options.priority || '0.5',
    alternates: options.alternates || []
  };
};

export default {
  generateCanonicalUrl,
  shouldRedirectToCanonical,
  generateHreflangUrls,
  generatePaginationCanonicals,
  validateCanonicalUrl,
  getRedirectRecommendations,
  generateSitemapEntry
};

