// Core GLP-1 Calculator Functions
// Final BMI and Timeline Patch Implementation

// Base weekly rates as fractions per week
export const R = {
  semaglutide: 0.0022,
  tirzepatide: 0.0029,
  retatrutide: 0.0050,
  cagrisema: 0.0050,
  liraglutide: 0.0014,
  mazdutide: 0.0026,
  survodutide: 0.0032,
  caligrutide: 0.0018, // GLP-1/GLP-2 dual agonist - moderate efficacy
  dulaglutide: 0.0007
};

// Titration multipliers by peptide
export const TITRATION = {
  semaglutide: [{until:4,m:0.25},{until:8,m:0.50},{until:12,m:0.75},{until:16,m:0.90},{until:Infinity,m:1}],
  tirzepatide: [{until:4,m:0.20},{until:8,m:0.40},{until:12,m:0.60},{until:16,m:0.80},{until:20,m:0.90},{until:Infinity,m:1}],
  retatrutide: [{until:4,m:0.40},{until:8,m:0.70},{until:12,m:0.90},{until:Infinity,m:1}],
  cagrisema: [{until:4,m:0.50},{until:8,m:0.85},{until:Infinity,m:1}],
  liraglutide: [{until:1,m:0.25},{until:2,m:0.50},{until:3,m:0.75},{until:Infinity,m:1}],
  mazdutide: [{until:4,m:0.50},{until:8,m:0.75},{until:12,m:0.90},{until:Infinity,m:1}],
  survodutide: [{until:4,m:0.50},{until:8,m:0.75},{until:12,m:0.90},{until:Infinity,m:1}],
  caligrutide: [{until:4,m:0.30},{until:8,m:0.60},{until:12,m:0.85},{until:Infinity,m:1}], // Weekly dose escalation
  dulaglutide: [{until:4,m:0.50},{until:8,m:0.75},{until:12,m:0.90},{until:Infinity,m:1}]
};

// Conversion functions
export const toKg = (lb) => lb / 2.20462;
export const toLb = (kg) => kg * 2.20462;
export const inchesFromFI = (ft, inx) => ft * 12 + inx;
export const metersFromInches = (inches) => inches * 0.0254;
export const metersFromCm = (cm) => cm / 100;

// BMI calculations
export const bmiMetric = (kg, m) => kg / (m * m);
export const bmiImperial = (lb, inches) => (lb * 703) / (inches * inches);

// Height parsing function - handles whole feet and feet.inches
export function parseFeetInches(input) {
  const s = String(input).trim();
  if (!s) throw new Error("Missing height");
  if (!s.includes('.')) {
    const feet = Number(s);
    if (!Number.isFinite(feet) || isNaN(feet)) throw new Error("Invalid height");
    return { feet: feet, inches: 0 };
  }
  const [feetStr, inchRaw] = s.split('.');
  const inchStr = inchRaw.slice(0, 2); // one or two digits
  let feet = Number(feetStr);
  let inches = Number(inchStr);
  if (!Number.isFinite(feet) || !Number.isFinite(inches) || isNaN(feet) || isNaN(inches)) throw new Error("Invalid height");
  if (inches >= 12) { feet += Math.floor(inches / 12); inches = inches % 12; }
  return { feet, inches };
}

// Healthy range and goal calculations
export function healthyRangeKg(m) {
  const m2 = m * m;
  return { min: 18.5 * m2, optimal: 22.0 * m2, max: 24.9 * m2 };
}

export function goalKg(m, useOptimal = false) {
  const m2 = m * m;
  return (useOptimal ? 22.0 : 24.9) * m2;
}

// BMI category classification
export function getBMICategory(bmi) {
  if (bmi < 16.0) return 'Severely Underweight';
  if (bmi < 18.5) return 'Underweight';
  if (bmi < 25.0) return 'Normal Weight';
  if (bmi < 30.0) return 'Overweight';
  if (bmi < 35.0) return 'Moderately Obese Class I';
  if (bmi < 40.0) return 'Severely Obese Class II';
  return 'Very Severely Obese Class III';
}

// Effective rate with titration and options
export function effectiveRate(key, week1Based, opts = {}) {
  const rows = TITRATION[key] ?? [{until:Infinity,m:1}];
  let adjustedWeek = week1Based;

  if (opts.giHold && week1Based > 4) {
    adjustedWeek = Math.max(1, week1Based - 2);
  }

  if (opts.pause && week1Based > 1) {
    adjustedWeek = 1;
  }

  const mult = rows.find(r => adjustedWeek <= r.until)?.m || 1;
  return R[key] * mult;
}

// Weekly deficit loss
export function weeklyDeficitLoss(unit, deficitKcal) {
  if (deficitKcal <= 0) return 0;
  return unit === 'imperial' ? (deficitKcal * 7) / 3500 : (deficitKcal * 7) / 7700;
}

// Improved weeks to goal with ramp stepper
export function weeksToGoalWithRamp(
  startW, goalW, peptide, unit, deficitKcal,
  opts = { useRamp: true, giHold: false, pause: false }
) {
  const d = weeklyDeficitLoss(unit, deficitKcal);
  let W = startW;
  let week = 0;
  let hold = 0;
  const cap = 1000;

  while (W > goalW && week < cap) {
    week += 1;
    let r = R[peptide];
    if (opts.useRamp !== false) {
      r = effectiveRate(peptide, week - hold, opts);
      if (opts.giHold) hold += (week === 1 || week === 5 || week === 9) ? 2 : 0;
      if (opts.pause) r = effectiveRate(peptide, 1, opts);
    }
    W = r > 0 ? W * (1 - r) - d : W - d;
  }
  return W <= goalW ? week : Infinity;
}

// Window blocks for timeline presentation
function windowBlocks(peptide) {
  const rows = TITRATION[peptide] ?? [{until:Infinity,m:1}];
  let prev = 0;
  return rows.map(r => {
    const start = prev + 1;
    const end = isFinite(r.until) ? r.until : Number.POSITIVE_INFINITY;
    prev = isFinite(r.until) ? r.until : prev;
    return { start, end, m: r.m };
  });
}

// Split window loss function for accurate math
export function splitWindowLoss(W0, weeks, rEff, dAbs) {
  let W = W0, pep = 0, def = 0;
  for(let i = 0; i < weeks; i++) {
    const pl = W * rEff;
    pep += pl;
    W -= pl;
    W -= dAbs;
    def += dAbs;
  }
  return { endWeight: W, peptideLoss: pep, deficitLoss: def, totalLoss: pep + def };
}

// Windowed timeline projection with accurate split loss
export function projectByWindows(
  startW, goalW, peptide, unit, deficitKcal, maxWeeks = 520
) {
  const d = weeklyDeficitLoss(unit, deficitKcal);
  const base = R[peptide];
  const blocks = windowBlocks(peptide);
  const out = [];
  let W = startW, used = 0;

  for (const b of blocks) {
    if (W <= goalW || used >= maxWeeks) break;
    const rEff = base * b.m;
    let n = Math.min((isFinite(b.end) ? b.end : maxWeeks) - Math.max(b.start, 1) + 1, maxWeeks - used);

    // Find actual weeks needed to reach goal in this window
    let k = 0, tempW = W;
    while (k < n && tempW > goalW) { 
      k++; 
      tempW = rEff > 0 ? tempW * (1 - rEff) - d : tempW - d; 
    }
    if (tempW <= goalW) n = k;

    // Use split window loss for accurate calculations
    const result = splitWindowLoss(W, n, rEff, d);

    out.push({
      label: `Weeks ${used + 1} to ${used + n}`,
      startWeight: W,
      endWeight: result.endWeight,
      rateFactor: b.m,
      peptideLoss: result.peptideLoss,
      deficitLoss: result.deficitLoss,
      totalLoss: result.totalLoss
    });

    W = result.endWeight;
    used += n;
  }
  return { windows: out, weeks: used, endWeight: W, hitGoal: W <= goalW };
}

// Analytics logging function - environment aware
export function logCalculation(data) {
  if (process.env.NODE_ENV === 'development') {
    console.log('GLP-1 Calculate Clicked:', {
      unit: data.unit,
      peptide: data.peptide,
      bmi: parseFloat(data.bmi),
      weight_to_lose_min: data.weightToLose?.min || 0,
      weight_to_lose_opt: data.weightToLose?.max || 0,
      r_base: R[data.peptide],
      deficit_per_day: parseInt(data.deficit),
      weeks_to_24_9: data.weeksEstimate?.toHealthy || 0,
      weeks_to_22_0: data.weeksEstimate?.toOptimal || 0,
      ramp_on: data.useTitrationRamp || false,
      timestamp: new Date().toISOString()
    });
  }
}

