// Advanced image optimization utilities
import { useState, useEffect, useCallback } from 'react';

// Image format detection and conversion
export const detectWebPSupport = () => {
  if (typeof window === 'undefined') return false;

  return new Promise((resolve) => {
    const webP = new Image();
    webP.onload = webP.onerror = () => {
      resolve(webP.height === 2);
    };
    webP.src = 'data:image/webp;base64,UklGRjoAAABXRUJQVlA4IC4AAACyAgCdASoCAAIALmk0mk0iIiIiIgBoSygABc6WWgAA/veff/0PP8bA//LwYAAA';
  });
};

// Responsive image source generator
export const generateResponsiveSources = (baseUrl, formats = ['webp', 'jpg'], sizes = [320, 640, 768, 1024, 1200, 1920]) => {
  const sources = [];

  formats.forEach(format => {
    const srcSet = sizes.map(size => {
      const url = baseUrl.includes('?') 
        ? `${baseUrl}&w=${size}&f=${format}&q=80`
        : `${baseUrl}?w=${size}&f=${format}&q=80`;
      return `${url} ${size}w`;
    }).join(', ');

    sources.push({
      type: `image/${format}`,
      srcSet,
      sizes: '(max-width: 320px) 320px, (max-width: 640px) 640px, (max-width: 768px) 768px, (max-width: 1024px) 1024px, (max-width: 1200px) 1200px, 1920px'
    });
  });

  return sources;
};

// Progressive image loading hook
export const useProgressiveImg = (lowQualitySrc, highQualitySrc) => {
  const [src, setSrc] = useState(lowQualitySrc);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setSrc(lowQualitySrc);
    setLoading(true);

    const img = new Image();
    img.onload = () => {
      setSrc(highQualitySrc);
      setLoading(false);
    };
    img.onerror = () => {
      setLoading(false);
    };
    img.src = highQualitySrc;
  }, [lowQualitySrc, highQualitySrc]);

  return { src, loading };
};

// Lazy loading with Intersection Observer
export const useLazyImage = (src, options = {}) => {
  const [imageSrc, setImageSrc] = useState(null);
  const [imageRef, setImageRef] = useState();

  useEffect(() => {
    let observer;

    if (imageRef && imageSrc !== src) {
      if (IntersectionObserver) {
        observer = new IntersectionObserver(
          entries => {
            entries.forEach(entry => {
              if (entry.isIntersecting) {
                setImageSrc(src);
                observer.unobserve(imageRef);
              }
            });
          },
          {
            threshold: 0.1,
            rootMargin: '50px',
            ...options
          }
        );
        observer.observe(imageRef);
      } else {
        // Fallback for browsers without IntersectionObserver
        setImageSrc(src);
      }
    }

    return () => {
      if (observer && observer.unobserve) {
        observer.unobserve(imageRef);
      }
    };
  }, [src, imageSrc, imageRef, options]);

  return [setImageRef, imageSrc];
};

// Optimized image component
export const OptimizedImage = ({ 
  src, 
  alt, 
  className = '', 
  lazy = true, 
  progressive = true,
  responsive = true,
  quality = 80,
  ...props 
}) => {
  const [webPSupported, setWebPSupported] = useState(false);
  const [setRef, lazyImageSrc] = useLazyImage(src);

  useEffect(() => {
    detectWebPSupport().then(setWebPSupported);
  }, []);

  const optimizedSrc = lazy ? lazyImageSrc : src;

  if (!optimizedSrc) {
    return (
      <div 
        ref={setRef}
        className={`bg-gray-200 animate-pulse ${className}`}
        style={{ aspectRatio: '16/9' }}
        aria-label={`Loading ${alt}`}
      />
    );
  }

  if (responsive && src.includes('http')) {
    const sources = generateResponsiveSources(src, webPSupported ? ['webp', 'jpg'] : ['jpg']);

    return (
      <picture className={className}>
        {sources.map((source, index) => (
          <source key={index} {...source} />
        ))}
        <img
          src={optimizedSrc}
          alt={alt}
          loading={lazy ? 'lazy' : 'eager'}
          decoding="async"
          {...props}
        />
      </picture>
    );
  }

  return (
    <img
      ref={lazy ? setRef : undefined}
      src={optimizedSrc}
      alt={alt}
      className={className}
      loading={lazy ? 'lazy' : 'eager'}
      decoding="async"
      {...props}
    />
  );
};

// Image preloader for critical images
export const preloadImages = (imageSrcs) => {
  return Promise.all(
    imageSrcs.map(src => 
      new Promise((resolve, reject) => {
        const img = new Image();
        img.onload = () => resolve(src);
        img.onerror = () => reject(new Error(`Failed to preload ${src}`));
        img.src = src;
      })
    )
  );
};

// Critical image preloader hook
export const useCriticalImagePreload = (images) => {
  const [loaded, setLoaded] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    preloadImages(images)
      .then(() => setLoaded(true))
      .catch(setError);
  }, [images]);

  return { loaded, error };
};

// Blur-up image loading technique
export const BlurUpImage = ({ 
  src, 
  blurDataURL, 
  alt, 
  className = '',
  ...props 
}) => {
  const [isLoaded, setIsLoaded] = useState(false);
  const [imageSrc, setImageSrc] = useState(blurDataURL);

  useEffect(() => {
    const img = new Image();
    img.onload = () => {
      setImageSrc(src);
      setIsLoaded(true);
    };
    img.src = src;
  }, [src]);

  return (
    <div className="relative overflow-hidden">
      <img
        src={imageSrc}
        alt={alt}
        className={`transition-all duration-300 ${
          isLoaded ? 'blur-0' : 'blur-sm'
        } ${className}`}
        {...props}
      />
      {!isLoaded && (
        <div className="absolute inset-0 bg-gradient-to-r from-gray-200 to-gray-300 animate-pulse" />
      )}
    </div>
  );
};

// Image size optimization
export const getOptimalImageSize = (containerWidth, devicePixelRatio = 1) => {
  const targetWidth = containerWidth * devicePixelRatio;
  const sizes = [320, 640, 768, 1024, 1200, 1920];

  // Find the smallest size that's larger than the target
  return sizes.find(size => size >= targetWidth) || sizes[sizes.length - 1];
};

// Batch image preloader
export class ImagePreloader {
  constructor() {
    this.cache = new Set();
    this.loading = new Map();
  }

  preload(src) {
    if (this.cache.has(src)) {
      return Promise.resolve();
    }

    if (this.loading.has(src)) {
      return this.loading.get(src);
    }

    const promise = new Promise((resolve, reject) => {
      const img = new Image();
      img.onload = () => {
        this.cache.add(src);
        this.loading.delete(src);
        resolve();
      };
      img.onerror = () => {
        this.loading.delete(src);
        reject(new Error(`Failed to preload ${src}`));
      };
      img.src = src;
    });

    this.loading.set(src, promise);
    return promise;
  }

  preloadBatch(srcs) {
    return Promise.all(srcs.map(src => this.preload(src)));
  }

  isLoaded(src) {
    return this.cache.has(src);
  }

  clear() {
    this.cache.clear();
    this.loading.clear();
  }
}

// Global image preloader instance
export const imagePreloader = new ImagePreloader();

