// Advanced lazy import utilities with preloading and error boundaries
import { lazy } from 'react';

// Preload cache to store component promises
const preloadCache = new Map();

// Advanced lazy loading with preloading capability
export const lazyWithPreload = (importFn) => {
  const Component = lazy(importFn);

  // Add preload method
  Component.preload = () => {
    const componentName = importFn.name || 'anonymous';
    if (!preloadCache.has(componentName)) {
      preloadCache.set(componentName, importFn());
    }
    return preloadCache.get(componentName);
  };

  return Component;
};

// Retry mechanism for failed imports
export const lazyWithRetry = (importFn, retries = 3) => {
  return lazy(() => 
    new Promise((resolve, reject) => {
      const attemptImport = (attempt) => {
        importFn()
          .then(resolve)
          .catch((error) => {
            if (attempt < retries) {
              console.warn(`Import failed, retrying... (${attempt + 1}/${retries})`, error);
              setTimeout(() => attemptImport(attempt + 1), 1000 * attempt);
            } else {
              reject(error);
            }
          });
      };
      attemptImport(0);
    })
  );
};

// Smart lazy loading with route-based preloading
export const smartLazy = (importFn) => {
  const Component = lazy(importFn);

  // Add preload method
  Component.preload = () => {
    return importFn();
  };

  return Component;
};

// Critical component preloader for above-fold content
export const preloadCritical = (components) => {
  return Promise.all(
    components.map(component => {
      if (component.preload) {
        return component.preload();
      }
      return Promise.resolve();
    })
  );
};

// Intersection Observer based preloading
export const createIntersectionPreloader = () => {
  const preloadMap = new Map();

  const observer = new IntersectionObserver(
    (entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const component = preloadMap.get(entry.target);
          if (component && component.preload) {
            component.preload();
            observer.unobserve(entry.target);
          }
        }
      });
    },
    { rootMargin: '100px' } // Start loading 100px before element is visible
  );

  return {
    register: (element, component) => {
      preloadMap.set(element, component);
      observer.observe(element);
    },
    unregister: (element) => {
      preloadMap.delete(element);
      observer.unobserve(element);
    }
  };
};

// Route-based prefetching
export const prefetchRoute = (routeComponent) => {
  if (routeComponent && routeComponent.preload) {
    // Use requestIdleCallback for non-critical prefetching
    if ('requestIdleCallback' in window) {
      requestIdleCallback(() => routeComponent.preload());
    } else {
      setTimeout(() => routeComponent.preload(), 0);
    }
  }
};

// Advanced chunk loading with error handling
export const loadChunk = async (chunkName) => {
  try {
    const module = await import(
      /* webpackChunkName: "[request]" */
      `../components/${chunkName}`
    );
    return module.default || module;
  } catch (error) {
    console.error(`Failed to load chunk: ${chunkName}`, error);
    // Return a fallback component
    return () => (
      <div className="error-fallback">
        <p>Failed to load component. Please refresh the page.</p>
      </div>
    );
  }
};

// Performance monitoring for lazy loaded components
export const withLoadingMetrics = (Component) => {
  // Return the component directly for lazy loading compatibility
  return Component;
};

