// Mobile-specific performance optimizations

// Viewport height fix for mobile browsers
export const initViewportFix = () => {
  const setVH = () => {
    const vh = window.innerHeight * 0.01;
    document.documentElement.style.setProperty('--vh', `${vh}px`);
  };

  setVH();
  window.addEventListener('resize', setVH);
  window.addEventListener('orientationchange', setVH);
};

// Passive scroll listeners for better performance
export const initPassiveListeners = () => {
  let ticking = false;

  const updateScrollPosition = () => {
    const scrollY = window.scrollY;
    document.documentElement.style.setProperty('--scroll-y', scrollY);
    ticking = false;
  };

  const onScroll = () => {
    if (!ticking) {
      requestAnimationFrame(updateScrollPosition);
      ticking = true;
    }
  };

  window.addEventListener('scroll', onScroll, { passive: true });
};

// Preload critical resources
export const preloadCriticalResources = () => {
  // Preload key CSS
  const criticalCSS = document.createElement('link');
  criticalCSS.rel = 'preload';
  criticalCSS.as = 'style';
  criticalCSS.href = '/static/css/main.css';
  document.head.appendChild(criticalCSS);

  // DNS prefetch for external domains
  const domains = [
    'https://vercel.analytics.com',
    'https://npiregistry.cms.hhs.gov'
  ];

  domains.forEach(domain => {
    const link = document.createElement('link');
    link.rel = 'dns-prefetch';
    link.href = domain;
    document.head.appendChild(link);
  });
};

// Reduce animations on low-power devices
export const adaptAnimationsForDevice = () => {
  const isLowPowerDevice = () => {
    return navigator.hardwareConcurrency <= 2 || 
           navigator.deviceMemory <= 2 ||
           window.matchMedia('(prefers-reduced-motion: reduce)').matches;
  };

  if (isLowPowerDevice()) {
    document.documentElement.classList.add('reduce-motion');
  }
};

// Optimize touch interactions
export const optimizeTouchInteractions = () => {
  // Add touch-action: manipulation to all interactive elements
  const style = document.createElement('style');
  style.textContent = `
    button, a, [role="button"], [tabindex] {
      touch-action: manipulation;
    }

    /* Reduce visual effects on mobile */
    @media (max-width: 768px) {
      * {
        will-change: auto !important;
      }

      .animate-pulse {
        animation-duration: 3s;
      }
    }
  `;
  document.head.appendChild(style);
};

// Initialize all mobile optimizations
export const initMobileOptimizations = () => {
  if (typeof window !== 'undefined') {
    initViewportFix();
    initPassiveListeners();
    preloadCriticalResources();
    adaptAnimationsForDevice();
    optimizeTouchInteractions();
  }
};

