/**
 * Open Graph Image Generation System for Professor Peptides
 * Automated system for generating professional OG images for social sharing
 * Supports all page types: peptides, calculators, blog posts, categories
 */

const SITE_URL = 'https://www.professorpeptides.org';
const CDN_BASE = 'https://images.professorpeptides.org'; // Future CDN implementation

// Template configurations for different page types
const OG_TEMPLATES = {
  peptide: {
    width: 1200,
    height: 630,
    template: 'peptide-profile',
    background: '#1e40af', // Blue medical theme
    textColor: '#ffffff',
    accentColor: '#3b82f6'
  },
  calculator: {
    width: 1200,
    height: 630,
    template: 'calculator-tool',
    background: '#059669', // Green professional theme
    textColor: '#ffffff',
    accentColor: '#10b981'
  },
  blog: {
    width: 1200,
    height: 630,
    template: 'blog-post',
    background: '#7c3aed', // Purple content theme
    textColor: '#ffffff',
    accentColor: '#8b5cf6'
  },
  category: {
    width: 1200,
    height: 630,
    template: 'category-page',
    background: '#dc2626', // Red category theme
    textColor: '#ffffff',
    accentColor: '#ef4444'
  },
  default: {
    width: 1200,
    height: 630,
    template: 'general',
    background: '#1f2937', // Dark professional theme
    textColor: '#ffffff',
    accentColor: '#60a5fa'
  }
};

/**
 * Generate Open Graph image URL for any page type
 * @param {string} type - Page type (peptide, calculator, blog, category, default)
 * @param {object} data - Page-specific data
 * @param {object} options - Additional options
 * @returns {string} OG image URL
 */
export const generateOGImageUrl = (type, data = {}, options = {}) => {
  const template = OG_TEMPLATES[type] || OG_TEMPLATES.default;

  switch (type) {
    case 'peptide':
      return generatePeptideOGUrl(data, options);
    case 'calculator':
      return generateCalculatorOGUrl(data, options);
    case 'blog':
      return generateBlogOGUrl(data, options);
    case 'category':
      return generateCategoryOGUrl(data, options);
    default:
      return generateDefaultOGUrl(data, options);
  }
};

/**
 * Generate peptide-specific OG image URL
 * @param {object} peptide - Peptide data
 * @param {object} options - Additional options
 * @returns {string} Peptide OG image URL
 */
const generatePeptideOGUrl = (peptide, options = {}) => {
  const baseUrl = 'https://og-image-generator.vercel.app/api/og';

  const params = new URLSearchParams({
    title: peptide.name || 'Research Peptide',
    subtitle: peptide.categoryTitle || 'Peptide Research',
    description: peptide.description?.substring(0, 80) + '...' || 'Comprehensive peptide research profile',
    logo: 'https://www.professorpeptides.org/images/logo-white.png',
    brand: 'Professor Peptides',
    template: 'peptide',
    theme: 'medical',
    width: '1200',
    height: '630',
    fdaStatus: peptide.fdaApproved ? 'FDA Approved' : 'Research Only',
    wadaStatus: peptide.wadaBanned ? 'WADA Banned' : 'WADA Permitted',
    category: peptide.category || '',
    ...options
  });

  return `${baseUrl}?${params.toString()}`;
};

/**
 * Generate calculator-specific OG image URL
 * @param {object} calculator - Calculator data
 * @param {object} options - Additional options
 * @returns {string} Calculator OG image URL
 */
const generateCalculatorOGUrl = (calculator, options = {}) => {
  const baseUrl = 'https://og-image-generator.vercel.app/api/og';

  const params = new URLSearchParams({
    title: calculator.name || 'Medical Calculator',
    subtitle: 'Professional Calculator Tool',
    description: calculator.description?.substring(0, 80) + '...' || 'Free professional medical calculator',
    logo: 'https://www.professorpeptides.org/images/logo-white.png',
    brand: 'Professor Peptides',
    template: 'calculator',
    theme: 'professional',
    width: '1200',
    height: '630',
    accuracy: 'Medical-Grade',
    cost: 'Free',
    users: calculator.users || '10,000+',
    ...options
  });

  return `${baseUrl}?${params.toString()}`;
};

/**
 * Generate blog post-specific OG image URL
 * @param {object} post - Blog post data
 * @param {object} options - Additional options
 * @returns {string} Blog post OG image URL
 */
const generateBlogOGUrl = (post, options = {}) => {
  const baseUrl = 'https://og-image-generator.vercel.app/api/og';

  const params = new URLSearchParams({
    title: post.title || 'Research Article',
    subtitle: post.category || 'Peptide Research',
    description: post.excerpt?.substring(0, 80) + '...' || 'Expert insights on peptide research',
    logo: 'https://www.professorpeptides.org/images/logo-white.png',
    brand: 'Professor Peptides Blog',
    template: 'blog',
    theme: 'content',
    width: '1200',
    height: '630',
    author: post.author || 'Professor Peptides Team',
    readTime: post.reading_time || '5 min read',
    date: post.date || new Date().toISOString().split('T')[0],
    ...options
  });

  return `${baseUrl}?${params.toString()}`;
};

/**
 * Generate category-specific OG image URL
 * @param {object} category - Category data
 * @param {object} options - Additional options
 * @returns {string} Category OG image URL
 */
const generateCategoryOGUrl = (category, options = {}) => {
  const baseUrl = 'https://og-image-generator.vercel.app/api/og';

  const params = new URLSearchParams({
    title: category.title || 'Peptide Category',
    subtitle: 'Research Database',
    description: category.description?.substring(0, 80) + '...' || 'Comprehensive peptide research database',
    logo: 'https://www.professorpeptides.org/images/logo-white.png',
    brand: 'Professor Peptides',
    template: 'category',
    theme: 'database',
    width: '1200',
    height: '630',
    peptideCount: category.peptides?.length || '10+',
    status: category.status || 'Research Available',
    ...options
  });

  return `${baseUrl}?${params.toString()}`;
};

/**
 * Generate default OG image URL
 * @param {object} data - Page data
 * @param {object} options - Additional options
 * @returns {string} Default OG image URL
 */
const generateDefaultOGUrl = (data, options = {}) => {
  const baseUrl = 'https://og-image-generator.vercel.app/api/og';

  const params = new URLSearchParams({
    title: data.title || 'Professor Peptides',
    subtitle: data.subtitle || 'Research Platform',
    description: data.description?.substring(0, 80) + '...' || 'World\'s most comprehensive peptide research platform',
    logo: 'https://www.professorpeptides.org/images/logo-white.png',
    brand: 'Professor Peptides',
    template: 'general',
    theme: 'default',
    width: '1200',
    height: '630',
    ...options
  });

  return `${baseUrl}?${params.toString()}`;
};

/**
 * Fallback static images for when dynamic generation is unavailable
 */
export const getFallbackOGImage = (type, data = {}) => {
  const baseImageUrl = `${SITE_URL}/images/og-fallbacks`;

  switch (type) {
    case 'peptide':
      if (data.fdaApproved) {
        return `${baseImageUrl}/peptide-fda-approved.jpg`;
      }
      return `${baseImageUrl}/peptide-research.jpg`;

    case 'calculator':
      return `${baseImageUrl}/calculator-professional.jpg`;

    case 'blog':
      return `${baseImageUrl}/blog-research.jpg`;

    case 'category':
      return `${baseImageUrl}/category-${data.categoryKey || 'general'}.jpg`;

    default:
      return `${baseImageUrl}/professor-peptides-main.jpg`;
  }
};

/**
 * Generate image alt text for accessibility
 * @param {string} type - Page type
 * @param {object} data - Page data
 * @returns {string} Alt text
 */
export const generateImageAltText = (type, data = {}) => {
  switch (type) {
    case 'peptide':
      return `${data.name || 'Research Peptide'} - ${data.fdaApproved ? 'FDA Approved' : 'Research Only'} peptide information from Professor Peptides`;

    case 'calculator':
      return `${data.name || 'Medical Calculator'} - Free professional calculator tool from Professor Peptides`;

    case 'blog':
      return `${data.title || 'Research Article'} - Expert insights from Professor Peptides blog`;

    case 'category':
      return `${data.title || 'Peptide Category'} - Research database from Professor Peptides`;

    default:
      return 'Professor Peptides - World\'s most comprehensive peptide research platform';
  }
};

/**
 * Preload critical OG images for better performance
 * @param {array} pages - Array of critical pages
 */
export const preloadCriticalOGImages = (pages = []) => {
  const criticalImages = [
    getFallbackOGImage('default'),
    getFallbackOGImage('peptide', { fdaApproved: true }),
    getFallbackOGImage('peptide', { fdaApproved: false }),
    getFallbackOGImage('calculator'),
    getFallbackOGImage('blog')
  ];

  // Add page-specific images
  pages.forEach(page => {
    if (page.type && page.data) {
      criticalImages.push(generateOGImageUrl(page.type, page.data));
    }
  });

  // Preload images
  criticalImages.forEach(imageUrl => {
    if (typeof window !== 'undefined') {
      const link = document.createElement('link');
      link.rel = 'preload';
      link.href = imageUrl;
      link.as = 'image';
      document.head.appendChild(link);
    }
  });
};

/**
 * Generate image dimensions for responsive display
 * @param {string} size - Size preset (sm, md, lg, xl)
 * @returns {object} Width and height
 */
export const getImageDimensions = (size = 'lg') => {
  const dimensions = {
    sm: { width: 400, height: 210 },
    md: { width: 600, height: 315 },
    lg: { width: 1200, height: 630 },
    xl: { width: 1600, height: 840 }
  };

  return dimensions[size] || dimensions.lg;
};

/**
 * Optimize image URL with query parameters
 * @param {string} imageUrl - Base image URL
 * @param {object} options - Optimization options
 * @returns {string} Optimized image URL
 */
export const optimizeImageUrl = (imageUrl, options = {}) => {
  const url = new URL(imageUrl);

  // Add optimization parameters
  if (options.quality) url.searchParams.set('q', options.quality);
  if (options.format) url.searchParams.set('f', options.format);
  if (options.width) url.searchParams.set('w', options.width);
  if (options.height) url.searchParams.set('h', options.height);
  if (options.dpr) url.searchParams.set('dpr', options.dpr);

  return url.toString();
};

export default {
  generateOGImageUrl,
  getFallbackOGImage,
  generateImageAltText,
  preloadCriticalOGImages,
  getImageDimensions,
  optimizeImageUrl
};

