// Advanced performance monitoring and analytics

// Web Vitals tracking
let perfObserver;
let resourceObserver;
let layoutShiftEntries = [];

// Performance metrics collector
class PerformanceMonitor {
  constructor() {
    this.metrics = new Map();
    this.observers = new Map();
    this.initialized = false;

    if (typeof window !== 'undefined') {
      this.init();
    }
  }

  init() {
    if (this.initialized) return;

    // Initialize core performance monitoring
    this.trackWebVitals();
    this.trackResourceTiming();
    this.trackNavigationTiming();
    this.trackUserTiming();
    this.trackMemoryUsage();

    this.initialized = true;
    console.log('Performance monitoring initialized');
  }

  // Track Core Web Vitals
  trackWebVitals() {
    if ('PerformanceObserver' in window) {
      // Largest Contentful Paint (LCP)
      this.createObserver('largest-contentful-paint', (entries) => {
        const lcp = entries[entries.length - 1];
        this.recordMetric('LCP', lcp.startTime);
        this.sendToAnalytics('lcp', lcp.startTime);
      });

      // First Input Delay (FID)
      this.createObserver('first-input', (entries) => {
        const fid = entries[0];
        const delay = fid.processingStart - fid.startTime;
        this.recordMetric('FID', delay);
        this.sendToAnalytics('fid', delay);
      });

      // Cumulative Layout Shift (CLS)
      this.createObserver('layout-shift', (entries) => {
        for (const entry of entries) {
          if (!entry.hadRecentInput) {
            layoutShiftEntries.push(entry);
          }
        }

        const cls = this.calculateCLS();
        this.recordMetric('CLS', cls);
        this.sendToAnalytics('cls', cls);
      });
    }

    // Track First Contentful Paint using Paint Timing API
    if ('PerformanceObserver' in window) {
      this.createObserver('paint', (entries) => {
        for (const entry of entries) {
          if (entry.name === 'first-contentful-paint') {
            this.recordMetric('FCP', entry.startTime);
            this.sendToAnalytics('fcp', entry.startTime);
          } else if (entry.name === 'first-paint') {
            this.recordMetric('FP', entry.startTime);
          }
        }
      });
    }
  }

  // Track resource loading performance
  trackResourceTiming() {
    if ('PerformanceObserver' in window) {
      this.createObserver('resource', (entries) => {
        entries.forEach(entry => {
          const resourceTiming = {
            name: entry.name,
            type: entry.initiatorType,
            duration: entry.duration,
            size: entry.transferSize || 0,
            cached: entry.transferSize === 0 && entry.decodedBodySize > 0
          };

          this.recordResourceMetric(resourceTiming);

          // Track slow resources
          if (entry.duration > 1000) {
            this.sendToAnalytics('slow_resource', {
              url: entry.name,
              duration: entry.duration,
              type: entry.initiatorType
            });
          }
        });
      });
    }
  }

  // Track navigation timing
  trackNavigationTiming() {
    if ('performance' in window && 'getEntriesByType' in performance) {
      const navigation = performance.getEntriesByType('navigation')[0];

      if (navigation) {
        const metrics = {
          TTFB: navigation.responseStart - navigation.requestStart,
          domContentLoaded: navigation.domContentLoadedEventEnd - navigation.domContentLoadedEventStart,
          loadComplete: navigation.loadEventEnd - navigation.loadEventStart,
          totalLoadTime: navigation.loadEventEnd - navigation.navigationStart
        };

        Object.entries(metrics).forEach(([key, value]) => {
          this.recordMetric(key, value);
        });

        this.sendToAnalytics('navigation_timing', metrics);
      }
    }
  }

  // Track custom user timing marks
  trackUserTiming() {
    if ('PerformanceObserver' in window) {
      this.createObserver('measure', (entries) => {
        entries.forEach(entry => {
          this.recordMetric(`custom_${entry.name}`, entry.duration);
          this.sendToAnalytics('user_timing', {
            name: entry.name,
            duration: entry.duration
          });
        });
      });
    }
  }

  // Track memory usage
  trackMemoryUsage() {
    if ('memory' in performance) {
      const memory = performance.memory;
      const memoryUsage = {
        used: memory.usedJSHeapSize,
        total: memory.totalJSHeapSize,
        limit: memory.jsHeapSizeLimit
      };

      this.recordMetric('memory_usage', memoryUsage);

      // Check for memory leaks
      if (memoryUsage.used / memoryUsage.total > 0.9) {
        this.sendToAnalytics('memory_warning', memoryUsage);
      }

      // Schedule periodic memory checks
      setTimeout(() => this.trackMemoryUsage(), 30000);
    }
  }

  // Create performance observer
  createObserver(type, callback) {
    try {
      const observer = new PerformanceObserver(callback);
      observer.observe({ type, buffered: true });
      this.observers.set(type, observer);
    } catch (error) {
      console.warn(`Failed to create observer for ${type}:`, error);
    }
  }

  // Calculate Cumulative Layout Shift
  calculateCLS() {
    let maxSessionValue = 0;
    let currentSessionValue = 0;
    let lastEntryTime = 0;

    for (const entry of layoutShiftEntries) {
      if (entry.startTime - lastEntryTime > 1000) {
        maxSessionValue = Math.max(maxSessionValue, currentSessionValue);
        currentSessionValue = entry.value;
      } else {
        currentSessionValue += entry.value;
      }
      lastEntryTime = entry.startTime;
    }

    return Math.max(maxSessionValue, currentSessionValue);
  }

  // Record metric
  recordMetric(name, value) {
    this.metrics.set(name, {
      value,
      timestamp: Date.now()
    });
  }

  // Record resource metric
  recordResourceMetric(resource) {
    const resourceMetrics = this.metrics.get('resources') || [];
    resourceMetrics.push(resource);
    this.metrics.set('resources', resourceMetrics);
  }

  // Send metrics to analytics
  sendToAnalytics(eventName, data) {
    // Send to Google Analytics 4 if available
    if (typeof gtag === 'function') {
      gtag('event', eventName, {
        custom_parameter_1: typeof data === 'object' ? JSON.stringify(data) : data,
        event_category: 'performance',
        value: typeof data === 'number' ? Math.round(data) : 0
      });
    }

    // Send to Vercel Analytics if available
    if (window.va) {
      window.va('track', eventName, data);
    }

    // Log for development
    if (process.env.NODE_ENV === 'development') {
      console.log(`Performance: ${eventName}`, data);
    }
  }

  // Get all metrics
  getMetrics() {
    return Object.fromEntries(this.metrics.entries());
  }

  // Get performance summary
  getPerformanceSummary() {
    const summary = {};

    // Core Web Vitals
    ['FCP', 'LCP', 'FID', 'CLS', 'TTFB'].forEach(metric => {
      const data = this.metrics.get(metric);
      if (data) {
        summary[metric] = data.value;
      }
    });

    // Resource summary
    const resources = this.metrics.get('resources') || [];
    summary.resourceCount = resources.length;
    summary.totalResourceSize = resources.reduce((sum, r) => sum + r.size, 0);
    summary.cachedResources = resources.filter(r => r.cached).length;

    // Memory usage
    const memory = this.metrics.get('memory_usage');
    if (memory) {
      summary.memoryUsed = memory.value.used;
      summary.memoryEfficiency = (1 - memory.value.used / memory.value.total) * 100;
    }

    return summary;
  }

  // Grade performance based on Core Web Vitals
  getPerformanceGrade() {
    const metrics = this.getPerformanceSummary();
    let score = 100;

    // Grade FCP (First Contentful Paint)
    if (metrics.FCP > 3000) score -= 20;
    else if (metrics.FCP > 1800) score -= 10;

    // Grade LCP (Largest Contentful Paint)
    if (metrics.LCP > 4000) score -= 25;
    else if (metrics.LCP > 2500) score -= 15;

    // Grade FID (First Input Delay)
    if (metrics.FID > 300) score -= 20;
    else if (metrics.FID > 100) score -= 10;

    // Grade CLS (Cumulative Layout Shift)
    if (metrics.CLS > 0.25) score -= 15;
    else if (metrics.CLS > 0.1) score -= 8;

    // Grade TTFB (Time to First Byte)
    if (metrics.TTFB > 800) score -= 10;
    else if (metrics.TTFB > 500) score -= 5;

    return {
      score: Math.max(0, score),
      grade: score >= 90 ? 'A' : score >= 80 ? 'B' : score >= 70 ? 'C' : score >= 60 ? 'D' : 'F'
    };
  }

  // Clean up observers
  disconnect() {
    this.observers.forEach(observer => observer.disconnect());
    this.observers.clear();
    this.metrics.clear();
    this.initialized = false;
  }
}

// Utility functions for custom timing
export const performanceMark = (name) => {
  if ('performance' in window && 'mark' in performance) {
    performance.mark(name);
  }
};

export const performanceMeasure = (name, startMark, endMark) => {
  if ('performance' in window && 'measure' in performance) {
    try {
      performance.measure(name, startMark, endMark);
    } catch (error) {
      console.warn('Failed to create performance measure:', error);
    }
  }
};

// React component performance tracking
export const withPerformanceTracking = (Component, componentName) => {
  return (props) => {
    const startTime = performance.now();

    React.useEffect(() => {
      const endTime = performance.now();
      const renderTime = endTime - startTime;

      // Track component render time
      performanceMonitor.sendToAnalytics('component_render', {
        component: componentName,
        renderTime: renderTime
      });

      // Warn about slow components
      if (renderTime > 16) { // Longer than one frame at 60fps
        console.warn(`Slow component render: ${componentName} took ${renderTime.toFixed(2)}ms`);
      }
    });

    return React.createElement(Component, props);
  };
};

// Page load performance tracker
export const trackPageLoad = (pageName) => {
  performanceMark(`${pageName}_start`);

  return () => {
    performanceMark(`${pageName}_end`);
    performanceMeasure(`${pageName}_load`, `${pageName}_start`, `${pageName}_end`);
  };
};

// Global performance monitor instance
export const performanceMonitor = new PerformanceMonitor();

// Export performance data for external use
export const getPerformanceReport = () => {
  return {
    metrics: performanceMonitor.getMetrics(),
    summary: performanceMonitor.getPerformanceSummary(),
    grade: performanceMonitor.getPerformanceGrade(),
    timestamp: Date.now()
  };
};

// Automatically track route changes
export const trackRouteChange = (from, to) => {
  performanceMonitor.sendToAnalytics('route_change', {
    from: from || 'unknown',
    to: to || 'unknown',
    timestamp: Date.now()
  });
};

