  { name: 'Peptide Protocol Tool', category: 'Protocol', type: 'calculator', description: 'Comprehensive peptide protocol guide', url: '/calculators/protocol', keywords: ['protocol', 'cycle', 'stack', 'combination'] }
];

// Search function that indexes all content
export const searchContent = (query, category = SEARCH_CATEGORIES.ALL, limit = 20) => {
  if (!query || query.trim().length < 2) {
    return [];
  }

  const searchTerms = query.toLowerCase().trim().split(' ').filter(term => term.length > 1);
  const results = [];

  // Helper function to calculate relevance score
  const calculateScore = (item, searchTerms) => {
    let score = 0;
    const searchableText = [
      item.name || item.title || '',
      item.description || item.excerpt || '',
      ...(item.keywords || []),
      item.category || '',
      item.author || ''
    ].join(' ').toLowerCase();

    searchTerms.forEach(term => {
      // Exact name/title match gets highest score
      if ((item.name || item.title || '').toLowerCase().includes(term)) {
        score += 100;
      }
      // Description match
      if ((item.description || item.excerpt || '').toLowerCase().includes(term)) {
        score += 50;
      }
      // Keywords match
      if ((item.keywords || []).some(keyword => keyword.toLowerCase().includes(term))) {
        score += 75;
      }
      // Category match
      if ((item.category || '').toLowerCase().includes(term)) {
        score += 25;
      }
      // General content match
      if (searchableText.includes(term)) {
        score += 10;
      }
    });

    return score;
  };

  // Search peptides
  if (category === SEARCH_CATEGORIES.ALL || category === SEARCH_CATEGORIES.PEPTIDES) {
    PEPTIDE_DATA.forEach(peptide => {
      const score = calculateScore(peptide, searchTerms);
      if (score > 0) {
        results.push({
          ...peptide,
          type: 'peptide',
          url: `/peptides/${peptide.name.toLowerCase().replace(/[\s-]/g, '-')}`,
          score
        });
      }
    });
  }

  // Search calculators
  if (category === SEARCH_CATEGORIES.ALL || category === SEARCH_CATEGORIES.CALCULATORS) {
    CALCULATOR_DATA.forEach(calculator => {
      const score = calculateScore(calculator, searchTerms);
      if (score > 0) {
        results.push({
          ...calculator,
          score
        });
      }
    });
  }

  // Search blog articles
  if (category === SEARCH_CATEGORIES.ALL || category === SEARCH_CATEGORIES.BLOG) {
    const allBlogArticles = Object.values(mockBlogArticles).flat();

    allBlogArticles.forEach(article => {
      const score = calculateScore(article, searchTerms);
      if (score > 0) {
        results.push({
          ...article,
          type: 'blog',
          url: `/blog/${article.category}/${article.slug}`,
          score
        });
      }
    });
  }

  // Search resources (basic page content)
  if (category === SEARCH_CATEGORIES.ALL || category === SEARCH_CATEGORIES.RESOURCES) {
    const resourcePages = [
      { name: 'About', description: 'Learn about Peptide Professor and our mission', type: 'page', url: '/about', category: 'Company', keywords: ['about', 'company', 'mission', 'team'] },
      { name: 'Research', description: 'Latest peptide research and scientific studies', type: 'page', url: '/research', category: 'Science', keywords: ['research', 'studies', 'science', 'publications'] },
      { name: 'Resources', description: 'Educational resources and guides', type: 'page', url: '/resources', category: 'Education', keywords: ['resources', 'guides', 'education', 'learning'] },
      { name: 'Contact', description: 'Contact our team of experts', type: 'page', url: '/company#contact', category: 'Support', keywords: ['contact', 'support', 'help', 'email'] }
    ];

    resourcePages.forEach(page => {
      const score = calculateScore(page, searchTerms);
      if (score > 0) {
        results.push({
          ...page,
          score
        });
      }
    });
  }

  // Sort by relevance score and limit results
  return results
    .sort((a, b) => b.score - a.score)
    .slice(0, limit)
    .map(({ score, ...item }) => item); // Remove score from final results
};

// Generate search suggestions based on popular terms
export const getSearchSuggestions = (query) => {
  if (!query || query.length < 2) {
    return [];
  }

  const popularTerms = [
    'semaglutide', 'tirzepatide', 'bpc-157', 'tb-500', 'ipamorelin', 'cjc-1295',
    'weight loss', 'healing', 'growth hormone', 'anti-aging', 'muscle growth',
    'calculator', 'dosing', 'protocol', 'research', 'side effects'
  ];

  return popularTerms
    .filter(term => term.toLowerCase().includes(query.toLowerCase()))
    .slice(0, 5);
};

// Get recent searches from localStorage
export const getRecentSearches = () => {
  try {
    const recent = localStorage.getItem('recent-searches');
    return recent ? JSON.parse(recent) : [];
  } catch {
    return [];
  }
};

// Save search to recent searches
export const saveRecentSearch = (query) => {
  if (!query || query.trim().length < 2) return;

  try {
    const recent = getRecentSearches();
    const updated = [query.trim(), ...recent.filter(q => q !== query.trim())].slice(0, 10);
    localStorage.setItem('recent-searches', JSON.stringify(updated));
  } catch {
    // Ignore localStorage errors
  }
};

// Clear recent searches
export const clearRecentSearches = () => {
  try {
    localStorage.removeItem('recent-searches');
  } catch {
    // Ignore localStorage errors
  }
};

export default {
  searchContent,
  getSearchSuggestions,
  getRecentSearches,
  saveRecentSearch,
  clearRecentSearches,
  SEARCH_CATEGORIES
};

