  return analysis;
};

/**
 * Calculate overall SEO score
 * @param {object} categories - All category results
 * @returns {object} Overall score and grade
 */
const calculateOverallScore = (categories) => {
  const weights = {
    technical: 0.25,
    content: 0.25,
    performance: 0.20,
    mobile: 0.15,
    structured: 0.10,
    social: 0.05
  };

  let weightedScore = 0;
  Object.keys(weights).forEach(category => {
    if (categories[category] && categories[category].score) {
      weightedScore += categories[category].score * weights[category];
    }
  });

  const score = Math.round(weightedScore);
  let grade, status;

  if (score >= 90) {
    grade = 'A';
    status = 'Excellent';
  } else if (score >= 80) {
    grade = 'B';
    status = 'Good';
  } else if (score >= 70) {
    grade = 'C';
    status = 'Fair';
  } else if (score >= 60) {
    grade = 'D';
    status = 'Needs Improvement';
  } else {
    grade = 'F';
    status = 'Poor';
  }

  return { score, grade, status };
};

/**
 * Generate recommendations based on audit results
 * @param {object} categories - Audit categories
 * @returns {array} Recommendations list
 */
const generateRecommendations = (categories) => {
  const recommendations = [];

  // High priority recommendations
  recommendations.push({
    priority: 'High',
    category: 'Technical',
    title: 'Implement Advanced Caching Strategy',
    description: 'Add service worker and implement advanced caching for static assets to improve Core Web Vitals.',
