    description: 'Create more content targeting specific peptide + condition combinations (e.g., "BPC-157 for tendon injury").',
    impact: 'Increased organic traffic, better SERP rankings',
    effort: 'High'
  });

  // Medium priority recommendations
  recommendations.push({
    priority: 'Medium',
    category: 'Structured Data',
    title: 'Add HowTo Schema for Calculator Guides',
    description: 'Implement HowTo structured data for calculator usage guides to enhance SERP features.',
    impact: 'Rich snippets, improved click-through rates',
    effort: 'Low'
  });

  recommendations.push({
    priority: 'Medium',
    category: 'Social',
    title: 'Optimize Social Media Integration',
    description: 'Test and refine Open Graph images, add LinkedIn-specific tags, implement social sharing buttons.',
    impact: 'Better social media visibility and engagement',
    effort: 'Medium'
  });

  // Low priority recommendations
  recommendations.push({
    priority: 'Low',
    category: 'Content',
    title: 'Implement User-Generated Content',
    description: 'Add review system for calculators and peptide information pages to increase engagement.',
    impact: 'User engagement, social proof',
    effort: 'High'
  });

  return recommendations;
};

/**
 * Generate action items
 * @param {object} categories - Audit categories
 * @returns {array} Action items list
 */
const generateActionItems = (categories) => {
  return [
    {
      task: 'Submit comprehensive sitemap to Google Search Console',
      category: 'Technical',
      timeline: 'This week',
      responsible: 'SEO Team'
    },
    {
      task: 'Test Open Graph images using Facebook Debugger',
      category: 'Social',
      timeline: 'This week',
      responsible: 'Marketing Team'
    },
    {
      task: 'Monitor Core Web Vitals in Google PageSpeed Insights',
      category: 'Performance',
      timeline: 'Ongoing',
      responsible: 'Development Team'
    },
    {
      task: 'Create peptide-specific FAQ pages with Schema markup',
      category: 'Content',
      timeline: 'Next month',
      responsible: 'Content Team'
    },
    {
      task: 'Implement HowTo schema for calculator instructions',
      category: 'Structured Data',
      timeline: 'Next sprint',
      responsible: 'Development Team'
    }
  ];
};

/**
 * Generate audit summary
 * @param {object} auditResults - Complete audit results
 * @returns {object} Summary object
 */
const generateSummary = (auditResults) => {
  const { overall, categories } = auditResults;

  const strengths = [];
  const weaknesses = [];
  const opportunities = [];

  // Identify strengths (categories scoring 85+)
  Object.keys(categories).forEach(category => {
    if (categories[category].score >= 85) {
      strengths.push(`${category.charAt(0).toUpperCase() + category.slice(1)} SEO (${categories[category].score}/100)`);
    } else if (categories[category].score < 75) {
      weaknesses.push(`${category.charAt(0).toUpperCase() + category.slice(1)} optimization needed (${categories[category].score}/100)`);
    }
  });

  // Key opportunities
  opportunities.push('Expand to additional peptide categories');
  opportunities.push('Implement local SEO for clinic partnerships');
  opportunities.push('Create video content for complex topics');
  opportunities.push('Develop peptide comparison tools');

  return {
    strengths,
    weaknesses,
    opportunities,
    keyMetrics: {
      totalPages: categories.content?.statistics?.totalPages || 100,
      peptidesCovered: categories.content?.statistics?.peptidePages || 47,
      blogPosts: categories.content?.statistics?.blogPosts || 3,
      technicalScore: categories.technical?.score || 0,
      contentScore: categories.content?.score || 0
    }
  };
};

export default {
  runSEOAudit,
  runTechnicalAudit,
  runContentAudit,
  runPerformanceAudit,
  runMobileAudit,
  runStructuredDataAudit,
  runSocialMediaAudit,
  calculateOverallScore,
  generateRecommendations,
  generateActionItems
};

