// Advanced Service Worker registration and management

const isProduction = process.env.NODE_ENV === 'production';
const swUrl = '/sw-advanced.js';

// Check if service workers are supported
const isSwSupported = () => {
  return 'serviceWorker' in navigator;
};

// Register service worker with advanced features
export const registerSW = async () => {
  if (!isSwSupported()) {
    console.log('Service workers not supported');
    return null;
  }

  try {
    const registration = await navigator.serviceWorker.register(swUrl, {
      scope: '/',
      updateViaCache: 'none' // Always fetch fresh service worker
    });

    console.log('SW: Registered successfully', registration);

    // Handle service worker updates
    registration.addEventListener('updatefound', () => {
      const newWorker = registration.installing;

      if (newWorker) {
        console.log('SW: New service worker installing');

        newWorker.addEventListener('statechange', () => {
          if (newWorker.state === 'installed') {
            if (navigator.serviceWorker.controller) {
              // New service worker available
              console.log('SW: New content available');
              showUpdateNotification();
            } else {
              // First time installation
              console.log('SW: Content cached for offline use');
              showInstallNotification();
            }
          }
        });
      }
    });

    // Listen for messages from service worker
    navigator.serviceWorker.addEventListener('message', event => {
      if (event.data && event.data.type === 'SW_UPDATE_AVAILABLE') {
        showUpdateNotification();
      }
    });

    // Check for updates every 30 minutes
    setInterval(() => {
      registration.update();
    }, 30 * 60 * 1000);

    return registration;
  } catch (error) {
    console.error('SW: Registration failed', error);
    return null;
  }
};

// Unregister service worker
export const unregisterSW = async () => {
  if (!isSwSupported()) {
    return false;
  }

  try {
    const registration = await navigator.serviceWorker.getRegistration();
    if (registration) {
      const unregistered = await registration.unregister();
      console.log('SW: Unregistered successfully', unregistered);
      return unregistered;
    }
  } catch (error) {
    console.error('SW: Unregistration failed', error);
  }

  return false;
};

// Update service worker
export const updateSW = async () => {
  if (!isSwSupported()) {
    return;
  }

  const registration = await navigator.serviceWorker.getRegistration();
  if (registration && registration.waiting) {
    // Tell the waiting service worker to skip waiting
    registration.waiting.postMessage({ type: 'SKIP_WAITING' });

    // Reload the page to use the new service worker
    window.location.reload();
  }
};

// Show update notification to user
const showUpdateNotification = () => {
  // Create a subtle notification
  if (!document.getElementById('sw-update-notification')) {
    const notification = document.createElement('div');
    notification.id = 'sw-update-notification';
    notification.className = 'fixed bottom-4 right-4 bg-blue-600 text-white px-6 py-3 rounded-lg shadow-lg z-50 transition-all duration-300 transform translate-y-full';
    notification.innerHTML = `
      <div class="flex items-center space-x-3">
        <div class="flex-1">
          <p class="text-sm font-medium">New version available!</p>
          <p class="text-xs opacity-90">Click to update</p>
        </div>
        <button id="sw-update-btn" class="bg-blue-500 hover:bg-blue-400 px-3 py-1 rounded text-xs">
          Update
        </button>
        <button id="sw-dismiss-btn" class="text-blue-200 hover:text-white">
          Ã—
        </button>
      </div>
    `;

    document.body.appendChild(notification);

    // Show notification
    setTimeout(() => {
      notification.classList.remove('translate-y-full');
    }, 100);

    // Add event listeners
    document.getElementById('sw-update-btn').addEventListener('click', () => {
      updateSW();
    });

    document.getElementById('sw-dismiss-btn').addEventListener('click', () => {
      hideUpdateNotification();
    });

    // Auto-hide after 10 seconds
    setTimeout(() => {
      hideUpdateNotification();
    }, 10000);
  }
};

// Show install notification
const showInstallNotification = () => {
  console.log('SW: App is ready for offline use');

  // Show a brief toast notification
  const toast = document.createElement('div');
  toast.className = 'fixed bottom-4 left-4 bg-green-600 text-white px-4 py-2 rounded-lg shadow-lg z-50 opacity-0 transition-opacity duration-300';
  toast.textContent = 'App ready for offline use!';

  document.body.appendChild(toast);

  // Show toast
  setTimeout(() => {
    toast.classList.remove('opacity-0');
  }, 100);

  // Hide toast after 3 seconds
  setTimeout(() => {
    toast.classList.add('opacity-0');
    setTimeout(() => {
      if (toast.parentNode) {
        toast.parentNode.removeChild(toast);
      }
    }, 300);
  }, 3000);
};

// Hide update notification
const hideUpdateNotification = () => {
  const notification = document.getElementById('sw-update-notification');
  if (notification) {
    notification.classList.add('translate-y-full');
    setTimeout(() => {
      if (notification.parentNode) {
        notification.parentNode.removeChild(notification);
      }
    }, 300);
  }
};

// Cache management utilities
export const cacheMgmt = {
  // Clear all caches
  clearAll: async () => {
    if ('caches' in window) {
      const cacheNames = await caches.keys();
      await Promise.all(
        cacheNames.map(cacheName => caches.delete(cacheName))
      );
      console.log('SW: All caches cleared');
    }
  },

  // Get cache statistics
  getStats: async () => {
    if ('caches' in window) {
      const cacheNames = await caches.keys();
      const stats = {};

      for (const cacheName of cacheNames) {
        const cache = await caches.open(cacheName);
        const keys = await cache.keys();
        stats[cacheName] = keys.length;
      }

      return stats;
    }
    return {};
  },

  // Preload critical resources
  preloadCritical: async (urls) => {
    const registration = await navigator.serviceWorker.getRegistration();
    if (registration && registration.active) {
      registration.active.postMessage({
        type: 'CACHE_UPDATE',
        urls: urls
      });
    }
  }
};

// Background sync utilities
export const backgroundSync = {
  // Register for background sync
  register: async (tag) => {
    const registration = await navigator.serviceWorker.getRegistration();
    if (registration && 'sync' in registration) {
      try {
        await registration.sync.register(tag);
        console.log(`SW: Background sync registered for ${tag}`);
      } catch (error) {
        console.error('SW: Background sync registration failed', error);
      }
    }
  },

  // Register for periodic background sync (if supported)
  registerPeriodic: async (tag, minInterval = 24 * 60 * 60 * 1000) => {
    const registration = await navigator.serviceWorker.getRegistration();
    if (registration && 'periodicSync' in registration) {
      try {
        await registration.periodicSync.register(tag, {
          minInterval: minInterval
        });
        console.log(`SW: Periodic background sync registered for ${tag}`);
      } catch (error) {
        console.error('SW: Periodic background sync registration failed', error);
      }
    }
  }
};

// Network status monitoring
export const networkMgmt = {
  isOnline: () => navigator.onLine,

  onStatusChange: (callback) => {
    window.addEventListener('online', () => callback(true));
    window.addEventListener('offline', () => callback(false));
  },

  // Force sync when coming back online
  syncOnReconnect: () => {
    window.addEventListener('online', () => {
      backgroundSync.register('reconnect-sync');
    });
  }
};

// Initialize service worker and related features
export const initializeServiceWorker = async () => {
  if (isProduction && isSwSupported()) {
    const registration = await registerSW();

    if (registration) {
      // Set up background sync for offline data
      await backgroundSync.register('peptide-data-sync');

      // Set up periodic sync for data refresh (if supported)
      await backgroundSync.registerPeriodic('peptide-data-refresh');

      // Sync when reconnecting to network
      networkMgmt.syncOnReconnect();

      return registration;
    }
  }

  return null;
};

