import { getAllPeptides, peptideCategories, getBlogData } from '../data/peptideData';

/**
 * Comprehensive XML Sitemap Generator for Professor Peptides
 * Generates complete sitemap with all static pages, dynamic peptide pages,
 * calculator pages, blog posts, and category pages with proper priorities
 * and change frequencies for optimal search engine crawling
 */

const SITE_URL = 'https://www.professorpeptides.org';
const CURRENT_DATE = new Date().toISOString().split('T')[0];

// Define all static routes with their SEO properties
const staticRoutes = [
  // Main pages - High priority
  {
    path: '/',
    priority: 1.0,
    changefreq: 'weekly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/peptides',
    priority: 0.9,
    changefreq: 'weekly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/calculators',
    priority: 0.9,
    changefreq: 'weekly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/research',
    priority: 0.8,
    changefreq: 'weekly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/blog',
    priority: 0.8,
    changefreq: 'daily',
    lastmod: CURRENT_DATE
  },

  // Secondary pages
  {
    path: '/resources',
    priority: 0.7,
    changefreq: 'weekly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/about',
    priority: 0.7,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/faq',
    priority: 0.8,
    changefreq: 'weekly',
    lastmod: CURRENT_DATE
  },

  // Calculator pages - High value for SEO
  {
    path: '/calculators/bmi',
    priority: 0.8,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/calculators/reconstitution',
    priority: 0.8,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/calculators/glp1',
    priority: 0.8,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/calculators/trt',
    priority: 0.7,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/calculators/fitness',
    priority: 0.7,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/calculators/melanotan',
    priority: 0.7,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/calculators/protocol',
    priority: 0.7,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },

  // Peptide tools and features
  {
    path: '/peptides/browse',
    priority: 0.8,
    changefreq: 'weekly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/peptides/interactions',
    priority: 0.7,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/peptides/compatibility',
    priority: 0.7,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },

  // Company pages
  {
    path: '/company',
    priority: 0.6,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },

  // Legacy URLs for backward compatibility
  {
    path: '/reconstitution-calculator',
    priority: 0.6,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  },
  {
    path: '/peptide-protocol-tool',
    priority: 0.6,
    changefreq: 'monthly',
    lastmod: CURRENT_DATE
  }
];

// Generate peptide category routes
const generateCategoryRoutes = () => {
  const categories = [];

  Object.keys(peptideCategories).forEach(categoryKey => {
    const category = peptideCategories[categoryKey];
    categories.push({
      path: `/peptides/${categoryKey}`,
      priority: 0.8,
      changefreq: 'weekly',
      lastmod: CURRENT_DATE,
      title: category.title
    });
  });

  return categories;
};

// Generate individual peptide routes
const generatePeptideRoutes = () => {
  const peptides = getAllPeptides();

  return peptides.map(peptide => ({
    path: `/peptide/${peptide.slug}`,
    priority: peptide.fdaApproved ? 0.9 : 0.8, // Higher priority for FDA approved peptides
    changefreq: 'monthly',
    lastmod: CURRENT_DATE,
    title: peptide.name,
    category: peptide.categoryTitle
  }));
};

// Generate blog post routes
const generateBlogRoutes = () => {
  const blogPosts = getBlogData();

  return blogPosts.map(post => ({
    path: `/blog/${post.slug}`,
    priority: 0.7,
    changefreq: 'weekly',
    lastmod: post.date || CURRENT_DATE,
    title: post.title,
    category: post.category
  }));
};

// Generate research category routes
const generateResearchRoutes = () => {
  const researchCategories = [
    'metabolic-health',
    'tissue-repair',
    'cognitive-enhancement',
    'performance-enhancement',
    'anti-aging',
    'immune-support',
    'pain-management'
  ];

  return researchCategories.map(category => ({
    path: `/research/${category}`,
    priority: 0.7,
    changefreq: 'weekly',
    lastmod: CURRENT_DATE,
    title: category.replace('-', ' ').replace(/\b\w/g, l => l.toUpperCase())
  }));
};

// Generate complete sitemap XML
export const generateSitemap = () => {
  const allRoutes = [
    ...staticRoutes,
    ...generateCategoryRoutes(),
    ...generatePeptideRoutes(),
    ...generateBlogRoutes(),
    ...generateResearchRoutes()
  ];

  const sitemapHeader = `<?xml version="1.0" encoding="UTF-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9"
        xmlns:news="http://www.google.com/schemas/sitemap-news/0.9"
        xmlns:xhtml="http://www.w3.org/1999/xhtml"
        xmlns:mobile="http://www.google.com/schemas/sitemap-mobile/1.0"
        xmlns:image="http://www.google.com/schemas/sitemap-image/1.1"
        xmlns:video="http://www.google.com/schemas/sitemap-video/1.1">`;

  const sitemapBody = allRoutes.map(route => {
    const url = `${SITE_URL}${route.path}`;

    return `  <url>
    <loc>${url}</loc>
    <lastmod>${route.lastmod}</lastmod>
    <changefreq>${route.changefreq}</changefreq>
    <priority>${route.priority}</priority>${route.title ? `
    <!-- ${route.title}${route.category ? ` - ${route.category}` : ''} -->` : ''}
  </url>`;
  }).join('\n');

  const sitemapFooter = `
</urlset>`;

  return sitemapHeader + '\n' + sitemapBody + sitemapFooter;
};

// Generate sitemap index for large sites
export const generateSitemapIndex = () => {
  const sitemaps = [
    {
      location: `${SITE_URL}/sitemap-main.xml`,
      lastmod: CURRENT_DATE
    },
    {
      location: `${SITE_URL}/sitemap-peptides.xml`,
      lastmod: CURRENT_DATE
    },
    {
      location: `${SITE_URL}/sitemap-blog.xml`,
      lastmod: CURRENT_DATE
    },
    {
      location: `${SITE_URL}/sitemap-calculators.xml`,
      lastmod: CURRENT_DATE
    }
  ];

  const sitemapIndexHeader = `<?xml version="1.0" encoding="UTF-8"?>
<sitemapindex xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">`;

  const sitemapIndexBody = sitemaps.map(sitemap => {
    return `  <sitemap>
    <loc>${sitemap.location}</loc>
    <lastmod>${sitemap.lastmod}</lastmod>
  </sitemap>`;
  }).join('\n');

  const sitemapIndexFooter = `
</sitemapindex>`;

  return sitemapIndexHeader + '\n' + sitemapIndexBody + sitemapIndexFooter;
};

// Generate specialized peptide sitemap
export const generatePeptideSitemap = () => {
  const peptideRoutes = [
    {
      path: '/peptides',
      priority: 0.9,
      changefreq: 'weekly',
      lastmod: CURRENT_DATE
    },
    {
      path: '/peptides/browse',
      priority: 0.8,
      changefreq: 'weekly',
      lastmod: CURRENT_DATE
    },
    ...generateCategoryRoutes(),
    ...generatePeptideRoutes()
  ];

  const sitemapHeader = `<?xml version="1.0" encoding="UTF-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
  <!-- Professor Peptides - Peptide Database Sitemap -->
  <!-- Contains all peptide pages, categories, and related tools -->`;

  const sitemapBody = peptideRoutes.map(route => {
    const url = `${SITE_URL}${route.path}`;

    return `  <url>
    <loc>${url}</loc>
    <lastmod>${route.lastmod}</lastmod>
    <changefreq>${route.changefreq}</changefreq>
    <priority>${route.priority}</priority>${route.title ? `
    <!-- ${route.title}${route.category ? ` (${route.category})` : ''} -->` : ''}
  </url>`;
  }).join('\n');

  const sitemapFooter = `
</urlset>`;

  return sitemapHeader + '\n' + sitemapBody + sitemapFooter;
};

// Generate calculator-specific sitemap
export const generateCalculatorSitemap = () => {
  const calculatorRoutes = staticRoutes.filter(route => 
    route.path.startsWith('/calculators')
  );

  const sitemapHeader = `<?xml version="1.0" encoding="UTF-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
  <!-- Professor Peptides - Calculator Tools Sitemap -->
  <!-- Contains all medical and peptide calculators -->`;

  const sitemapBody = calculatorRoutes.map(route => {
    const url = `${SITE_URL}${route.path}`;

    return `  <url>
    <loc>${url}</loc>
    <lastmod>${route.lastmod}</lastmod>
    <changefreq>${route.changefreq}</changefreq>
    <priority>${route.priority}</priority>
  </url>`;
  }).join('\n');

  const sitemapFooter = `
</urlset>`;

  return sitemapHeader + '\n' + sitemapBody + sitemapFooter;
};

// Generate news sitemap for blog content
export const generateNewsSitemap = () => {
  const blogPosts = getBlogData();
  const recentPosts = blogPosts.filter(post => {
    const postDate = new Date(post.date);
    const twoDaysAgo = new Date();
    twoDaysAgo.setDate(twoDaysAgo.getDate() - 2);
    return postDate >= twoDaysAgo;
  });

  if (recentPosts.length === 0) {
    return null; // No recent news
  }

  const sitemapHeader = `<?xml version="1.0" encoding="UTF-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9"
        xmlns:news="http://www.google.com/schemas/sitemap-news/0.9">
  <!-- Professor Peptides - News Sitemap -->
  <!-- Contains recent blog posts and research updates -->`;

  const sitemapBody = recentPosts.map(post => {
    const url = `${SITE_URL}/blog/${post.slug}`;

    return `  <url>
    <loc>${url}</loc>
    <news:news>
      <news:publication>
        <news:name>Professor Peptides</news:name>
        <news:language>en</news:language>
      </news:publication>
      <news:publication_date>${post.date}</news:publication_date>
      <news:title>${post.title}</news:title>
      <news:keywords>${post.category}, peptide research, medical research</news:keywords>
    </news:news>
    <lastmod>${post.date}</lastmod>
    <changefreq>weekly</changefreq>
    <priority>0.8</priority>
  </url>`;
  }).join('\n');

  const sitemapFooter = `
</urlset>`;

  return sitemapHeader + '\n' + sitemapBody + sitemapFooter;
};

// Generate robots.txt content
export const generateRobotsTxt = () => {
  return `User-agent: *
Allow: /

# Sitemaps
Sitemap: ${SITE_URL}/sitemap.xml
Sitemap: ${SITE_URL}/sitemap-peptides.xml
Sitemap: ${SITE_URL}/sitemap-blog.xml
Sitemap: ${SITE_URL}/sitemap-calculators.xml

# Crawl-delay for respectful crawling
Crawl-delay: 1

# Block unwanted paths
Disallow: /admin/
Disallow: /api/
Disallow: /private/
Disallow: /*.json$
Disallow: /*?*utm_*
Disallow: /*?*fbclid*
Disallow: /*?*gclid*

# Allow important assets
Allow: /css/
Allow: /js/
Allow: /images/
Allow: /fonts/

# Block sensitive files
Disallow: /.env
Disallow: /package.json
Disallow: /webpack.config.js
Disallow: /.git/

# Special directives for search engines
User-agent: Googlebot
Allow: /

User-agent: Bingbot
Allow: /

User-agent: facebookexternalhit
Allow: /

User-agent: Twitterbot
Allow: /

User-agent: LinkedInBot
Allow: /

# Block problematic bots
User-agent: SemrushBot
Disallow: /

User-agent: AhrefsBot
Disallow: /

User-agent: MJ12bot
Disallow: /

# Host declaration
Host: ${SITE_URL}`;
};

// Generate URL list for manual submission
export const generateUrlList = () => {
  const allRoutes = [
    ...staticRoutes,
    ...generateCategoryRoutes(),
    ...generatePeptideRoutes(),
    ...generateBlogRoutes(),
    ...generateResearchRoutes()
  ];

  return allRoutes
    .sort((a, b) => b.priority - a.priority)
    .map(route => `${SITE_URL}${route.path}`)
    .join('\n');
};

// Statistics and analysis
export const getSitemapStats = () => {
  const allRoutes = [
    ...staticRoutes,
    ...generateCategoryRoutes(),
    ...generatePeptideRoutes(),
    ...generateBlogRoutes(),
    ...generateResearchRoutes()
  ];

  const stats = {
    total: allRoutes.length,
    byPriority: {
      high: allRoutes.filter(r => r.priority >= 0.8).length,
      medium: allRoutes.filter(r => r.priority >= 0.6 && r.priority < 0.8).length,
      low: allRoutes.filter(r => r.priority < 0.6).length
    },
    byType: {
      static: staticRoutes.length,
      peptides: generatePeptideRoutes().length,
      categories: generateCategoryRoutes().length,
      blog: generateBlogRoutes().length,
      research: generateResearchRoutes().length
    },
    byChangeFreq: {
      daily: allRoutes.filter(r => r.changefreq === 'daily').length,
      weekly: allRoutes.filter(r => r.changefreq === 'weekly').length,
      monthly: allRoutes.filter(r => r.changefreq === 'monthly').length
    }
  };

  return stats;
};

export default {
  generateSitemap,
  generateSitemapIndex,
  generatePeptideSitemap,
  generateCalculatorSitemap,
  generateNewsSitemap,
  generateRobotsTxt,
  generateUrlList,
  getSitemapStats
};

