import { NextRequest, NextResponse } from 'next/server';
import { createHmac } from 'crypto';
import { limit } from '@/lib/ratelimit';

const ALLOWED_ORIGIN = process.env.ALLOWED_ORIGIN || '';
const HMAC_SECRET = process.env.HMAC_SECRET || '';
const TURNSTILE_SECRET = process.env.TURNSTILE_SECRET || '';

const SIG_HEADER = 'x-edge-sig';
const SIG_TTL_MS = 5 * 60 * 1000; // 5 minutes

function verifyEdgeSig(req: NextRequest, path: string) {
  const header = req.headers.get(SIG_HEADER);
  const sid = req.cookies.get('pp_sid')?.value;
  if (!header || !sid) return false;
  const ua = req.headers.get('user-agent') || '';
  const ip = req.ip ?? req.headers.get('x-forwarded-for')?.split(',')[0]?.trim() ?? '0.0.0.0';
  const [tsStr, sig] = header.split('.') as [string, string];
  const ts = Number(tsStr);
  if (!Number.isFinite(ts) || Date.now() - ts > SIG_TTL_MS) return false;
  const payload = [sid, ip, ua, tsStr, path].join('|');
  const expected = createHmac('sha256', HMAC_SECRET).update(payload).digest('hex');
  return sig === expected;
}

async function verifyTurnstile(token?: string) {
  if (!TURNSTILE_SECRET) return true; // skip if not configured
  if (!token) return false;
  const r = await fetch('https://challenges.cloudflare.com/turnstile/v0/siteverify', {
    method: 'POST',
    headers: { 'content-type': 'application/x-www-form-urlencoded' },
    body: new URLSearchParams({ secret: TURNSTILE_SECRET, response: token })
  });
  const data = await r.json();
  return Boolean(data?.success);
}

export async function OPTIONS() {
  const res = new NextResponse(null, { status: 204 });
  if (ALLOWED_ORIGIN) {
    res.headers.set('Access-Control-Allow-Origin', ALLOWED_ORIGIN);
    res.headers.set('Vary', 'Origin');
    res.headers.set('Access-Control-Allow-Credentials', 'true');
    res.headers.set('Access-Control-Allow-Headers', 'content-type, authorization');
    res.headers.set('Access-Control-Allow-Methods', 'POST, GET, OPTIONS');
  }
  return res;
}

export async function POST(req: NextRequest) {
  if (!verifyEdgeSig(req, '/api/calc')) {
    return NextResponse.json({ error: 'bad_sig' }, { status: 403 });
  }

  const ip = req.ip ?? req.headers.get('x-forwarded-for')?.split(',')[0]?.trim() ?? '0.0.0.0';
  const rl = await limit(`calc:${ip}`);
  if (!rl.success) return NextResponse.json({ error: 'rate_limited' }, { status: 429 });

  const { tool, input, cfToken } = await req.json();
  const botOK = await verifyTurnstile(cfToken);
  if (!botOK) return NextResponse.json({ error: 'bot_blocked' }, { status: 403 });

  // Example calculators. Replace with your server-only implementations.
  function bmiCalc({ heightCm, weightKg }: { heightCm: number; weightKg: number }) {
    const h = heightCm / 100;
    return { bmi: Number((weightKg / (h * h)).toFixed(1)) };
  }
  function reconCalc({ mg, ml }: { mg: number; ml: number }) {
    return { mgPerMl: Number((mg / ml).toFixed(4)) };
  }

  let result: unknown;
  switch (tool) {
    case 'bmi':
      result = bmiCalc(input);
      break;
    case 'reconstitution':
      result = reconCalc(input);
      break;
    default:
      return NextResponse.json({ error: 'unknown_tool' }, { status: 400 });
  }

  const res = NextResponse.json({ ok: true, tool, result });
  if (ALLOWED_ORIGIN) {
    res.headers.set('Access-Control-Allow-Origin', ALLOWED_ORIGIN);
    res.headers.set('Vary', 'Origin');
    res.headers.set('Access-Control-Allow-Credentials', 'true');
  }
  return res;
}

